INSERT INTO `c_integration` VALUES ('86', '3', '<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\"><span style=\"font-size:14px;\"></span><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:SimSun;font-size:14px;\">继去年《从宁波民谚探析</span></span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">“</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">红帮</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">”</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">形成的文化生态》一文获得优秀论文奖后，红帮文化研究所冯盈之老师撰写的论文《红帮裁缝对辛亥革命的历史贡献研究》再次获得宁波市社会科学界第二届学术年会优秀论文奖。</span><span style=\"font-family:ˎ̥;color:#333333;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;\"><span style=\"font-family:SimSun;font-size:14px;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:SimSun;font-size:14px;\">宁波市社会科学界第二届学术年会开幕式</span></span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">11</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">月</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">8</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">日下午在宁波万豪酒店举行。本次学术年会的主题是：</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">“</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">宁波新发展：机遇、挑战、路径</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">”</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">，年会共收到论文</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">332</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">篇，评选出优秀论文</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">45</span><span style=\"font-family:SimSun;color:#333333;font-size:14px;\">篇。宁波市副市长成岳冲等为优秀论文获奖者颁发了荣誉证书。</span><span style=\"font-family:ˎ̥;color:#333333;\"> </span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"right\">\r\n	<img alt=\"\" src=\"/hbwh/attached/image/20120329/20120329160348_932.bmp\" width=\"494\" height=\"399\" /> \r\n</p>\r\n<span style=\"font-family:ˎ̥;color:#333333;font-size:10pt;\"></span>', '', '1', '/hbwh/attached/image/20120329/20120329160348_932.bmp', '99', '2012-04-18 14:11:25', '红帮文化研究成果再次在宁波市社科界获奖', '10', '文化品牌', '浙江纺织服装学院', '8', null, '2012-03-29 16:00:24', '0', '', '0');
