INSERT INTO `ws_information` VALUES ('1', null, '&nbsp;\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">机构设置<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;font-size:12pt;\">一、行政机构</span></b><b><span style=\"font-size:12pt;\"></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">书记兼副院长：丁丽君</span><span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">副</span><span><span>&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">院</span><span><span>&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">长：</span> <span style=\"font-family:宋体;\">余国良</span><span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">办公室：</span><span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">王锡金</span><span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">教务办：</span><span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">张玉</span><span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">实训中心：胡裕波、陈宝珍</span><span></span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;font-size:12pt;\">二、教学机构</span></b><b><span style=\"font-size:12pt;\"></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■</span><span style=\"font-family:宋体;font-size:12pt;\">语文教研室</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主任</span><span style=\"font-size:12pt;\">: </span><span style=\"font-family:宋体;font-size:12pt;\">周震豪</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成员：张艺、孙奕、林莹、张宏儿、吴颖、许维维</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■</span><span style=\"font-family:宋体;font-size:12pt;\">思政教研室</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主任：王苏鹤</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成员</span><span style=\"font-size:12pt;\">: </span><span style=\"font-family:宋体;font-size:12pt;\">崔玉环、李洪波、周素珍、王冬梅、黄梅君、胡杏华、周旭萍、方玉凤、胡海军、刁晨燕</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■</span><span style=\"font-family:宋体;font-size:12pt;\">英语教研室</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主任：施慧英</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成员：付丽华、龚婉琴、卢锦芍、张雪芬、张莹、黄俊彦、朱燕辉、严谨、徐海明、张红赛、陆晓楠、周鼎坤、罗佳炜、刘欣、蒋平、郑卡莉、林碧英、洪芸、李剑霞、刘恒、庄可、罗技科、吴梅、顾铁华、谢琳、林函洁</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■</span><span style=\"font-family:宋体;font-size:12pt;\">体育教研室</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主任：张宾</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成员：刘建中、徐群连、袁平君、严睿、杜林颖、周荣春、林宗德、欧鹏飞、鲍可可、徐丽玲、李金花、常洪斌、盛菊霞</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■</span><span style=\"font-family:宋体;font-size:12pt;\">数理教研室</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主任：左秀山</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成员：高霞萍</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;font-size:12pt;\">三、决策与咨询机构 <span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■ </span><span style=\"font-family:宋体;font-size:12pt;\">院务委员会 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主<span> &nbsp; </span>任：丁丽君<span> <br />\r\n</span>副主任： 余国良 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成<span> &nbsp; </span>员：张玉、胡裕波 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">秘<span> &nbsp; </span>书：王锡金 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■ </span><span style=\"font-family:宋体;font-size:12pt;\">学术委员会 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主<span> &nbsp; </span>任：丁丽君 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">副主任： 余国良 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成<span> &nbsp; </span>员：施惠英、张艺、王苏鹤、严睿、左秀山、崔玉环、王苹<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">秘<span> &nbsp; </span>书：王锡金 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■ </span><span style=\"font-family:宋体;font-size:12pt;\">教学委员会 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主<span> &nbsp; </span>任：丁丽君<span> <br />\r\n</span>副主任： 余国良 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成<span> &nbsp; </span>员： 张宾、付丽华、刘建中、孙奕、高霞萍、周素珍<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">秘<span> &nbsp; </span>书：张玉 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■ </span><span style=\"font-family:宋体;font-size:12pt;\">教学督导委员会 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主<span> &nbsp; </span>任：李洪波 <span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">副主任： 郑卡莉<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成<span> &nbsp; </span>员：郑晓、龚婉琴、徐群连、卢锦芍、林莹、王冬梅<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">秘书：张玉<span></span></span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>', null, '2012-05-05', 'admin', '', null, '33', '12', '机构设置', '2', '2012-05-06 13:36:30', null, null, '2', null, null, '机构设置', null, null, null);
INSERT INTO `ws_information` VALUES ('2', null, '&nbsp;\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<b><span style=\"font-family:宋体;font-size:14pt;\">人文学院</span></b><b><span style=\"font-size:14pt;\"></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">【概述】</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">人文学院下设思政教研室、语文教研室、数学教研室、公共英语教研室、体育教研室五个教研室，负责全院的公共课教学任务。共有教师<span>70</span>名，其中双师型教师<span>5</span>人，高级职称<span>28</span>人，具有硕士学位的<span>15</span>名，在职攻读硕士学位教师<span>15</span>人。学院</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">秉承</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">传道授业、厚积薄发” 的传统，坚持以学科建设为龙头，以教学质量为生命，以学术研究为支撑，以培养人才为目标的指导思想，努力实践</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">修德长技、求真尚美</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">之校训，致力于铸人文素养之魂，煅明辨慎思之质，培养崇德厚道、明体达用之高素质强技能应用型人才。</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">【班子建设】</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">年人文学院在学院总体规划安排下，进行领导班子调整，以“建设、探索、落实、沟通、合作、创新”为总体思路，以“<span>职业核心竞争力”人才培养为核心</span>，以常规工作为抓手，规范各项管理规章制度，二级管理机制进一步完善。<span style=\"color:red;\"></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">【教学科研】</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">年人文学院教师共开设课程<span>16</span>门次，开设选修课<span>18</span>门次；</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">在学院网站上发布信息</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">31</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">条；送讲座到分院</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">8</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">个；</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">在各级各类刊物上发表论文近<span>60</span>篇，</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">教改课题立项</span><span style=\"line-height:150%;font-size:12pt;\">10</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">项，结题</span><span style=\"line-height:150%;font-size:12pt;\">3</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">项；厅市级课题</span><span style=\"line-height:150%;font-size:12pt;\">6</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">项，市局级课题</span><span style=\"line-height:150%;font-size:12pt;\">14</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">项，</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">院级示范项目<span>1</span>个，院招标课题<span>1</span>个，重点教材建设<span>1</span>项，院级课题<span>17</span>项。</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">语文教研室</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">相继出版了《汉字与文化》、《中国现当代女性文学》、《现代传媒与文学》等教材，思政教研室</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">“红帮精神的传承和创新”入选大学生思想政治教育的宁波模式一书正式出版。</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">各教研室 “四个一、一个五”（各教研室每学期争取有一个教学成果、一个讲座、一个教改项目、一个教学竞赛类获奖、五篇宣传报道）的年度考核目标和任务基本得到落实，活动初见成效</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">。教学改革初见成效：</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">英语教研室“课内外联动教学改革”成绩显著，考试方式改革取得很大成功，</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">施慧英主持的《职场英语实</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">训—涉外商务》获</span><span style=\"line-height:150%;font-family:\'ˎ̥\',\'serif\';font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">年度浙江省高校重点教材建设项目</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">；</span><span style=\"line-height:150%;font-family:仿宋_GB2312;color:black;font-size:12pt;\">思政教研室在</span><span style=\"line-height:150%;font-family:新宋体;color:black;font-size:12pt;\">10</span><span style=\"line-height:150%;font-family:仿宋_GB2312;color:black;font-size:12pt;\">级新生中实施“分段式、本土化”教学改革，取得较好效果；体育教研室</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">年<span>12</span>月<span>26</span>日承办宁波市乒乓球比赛，</span><span style=\"line-height:150%;font-family:仿宋_GB2312;color:black;font-size:12pt;\">组织的“阳光系列体育活动”、校第六届运动会取得圆满</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">成绩。</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">【师资队伍建设】</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">年人文学院有<span>1</span>名教师晋升高级职称，<span>2</span>名年轻教师晋升讲师。共有<span>15</span>名教师进修硕士学位，<span>3</span>人参加职业核心能力素质平台建设培训。<span>1</span>人参加国际吴语学术研讨会议、浙江省语言学年会。<span style=\"color:red;\"></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">【实验实训】</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">年人文学院共有语音室<span>9</span>个，承担全院学生的公共英语听力教育，同时语音室免费对学生开放，每周开放<span>10</span>学时。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">【获奖情况】</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">年</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">教学、技能竞赛成绩突出：国家级获奖<span>4</span>项，</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">省级以上获奖</span><span style=\"line-height:150%;font-size:12pt;\">8</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">项，市级以上获奖</span><span style=\"line-height:150%;font-size:12pt;\">8</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">项。</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">2</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">篇论文入选中国体育科学分会体育产业大会和浙江省第三十一届高校体育论文报告会作专题报告并荣获三等奖；</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">市级获奖</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">论文</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">一等奖</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">4</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">篇，二等奖<span>5</span>篇</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">，院级获奖论文</span><span style=\"line-height:150%;font-size:12pt;\">3</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">篇</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">；</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">获学院教学成果二等奖</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">1</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">项；</span><span style=\"line-height:150%;font-size:12pt;\">1</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">项院级精品课程结题并获优秀</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">。</span><span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">【党建思政工作】</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">以</span><span style=\"line-height:150%;font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">建组织、强机制、树形象、作贡献</span><span style=\"line-height:150%;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">为载体，党建工作稳步推进</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">，<span>2010</span>年人文学院共有教师党员<span>24</span>名，申请入党人员<span>1</span>名；学院</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">利用红帮文化研究所、红帮课程<span>2</span>个平台面向<span>10</span>级新生，开展“红帮”主题教育活动。学习型党组织建设上新台阶，党员创先争优活动开展如火如荼</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">；</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">以党员“八个一”活动为载体，通过党日活动、民主生活会、“创先争优大家谈”、我为学院献一计等活动，充分发挥学院党员干部的引领作用，扎实推进</span><span style=\"line-height:150%;font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">三为三强化</span><span style=\"line-height:150%;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">，强化战斗堡垒作用，强化党员科研意识。充分完善人文学院网站的党建内容。</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">及时</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">完成了上级部门布置的临时性、突击性的任务，</span><em><span style=\"line-height:150%;font-family:仿宋_GB2312;color:windowtext;font-size:12pt;\">人文学院党建工作实现了</span></em><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">规范化、制度化和科学化。工会</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">完善“教工小家”建设，完成了分工会委员的换届选举工作，加强培训分工会委员，增强分工会委员的服务意识。</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">协助学院工会做好教职工的思想政治工作和</span><span style=\"line-height:150%;font-family:\'ˎ̥\',\'serif\';font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">三育人</span><span style=\"line-height:150%;font-family:\'ˎ̥\',\'serif\';font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">工作：盛菊霞老师被评为院“三育人”奖，王苏鹤老师在学院工会组织的“我对红帮文化的理解”征文中获一等奖，韩世强老师获三等奖。</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">年“慈善一日捐”活动中人文学院教师共捐款<span>16700</span>多元。<span></span></span>\r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">人文学院<span></span></span>\r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"line-height:150%;font-family:仿宋_GB2312;font-size:12pt;\">2011.3</span>\r\n</p>', null, '2012-05-05', 'admin', '', null, '51', '9', '要事一览', '3', '2012-05-06 13:36:30', null, null, '2', null, null, '人文学院2010年鉴', null, null, null);
INSERT INTO `ws_information` VALUES ('3', null, '<p align=\"center\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"text-indent:55.25pt;\" align=\"center\">\r\n	<b><span style=\"font-size:14pt;\">2010-2011</span></b><b><span style=\"font-family:宋体;font-size:14pt;\">学年人文学院教职工参加学术会议情况</span></b><b><span style=\"font-size:14pt;\"></span></b>\r\n</p>\r\n<p style=\"text-indent:55.25pt;\" align=\"center\">\r\n	<b><span style=\"font-size:14pt;\"></span></b>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"border-bottom:medium none;border-left:medium none;width:437.4pt;border-collapse:collapse;border-top:medium none;border-right:medium none;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\" width=\"583\" align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"114\">\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;font-size:12pt;\">姓名</span></b><b><span style=\"font-size:12pt;\"></span></b>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"134\">\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;font-size:12pt;\">事由</span></b><b><span style=\"font-size:12pt;\"></span></b>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"94\">\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;font-size:12pt;\">时间</span></b><b><span style=\"font-size:12pt;\"></span></b>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-