INSERT INTO `ws_information` VALUES ('28', null, '　　英国总领事馆文化教育领事、华东地区主任包迈岫、英国大使馆文化教育处一秘苏珊Susan Milner女士，教育部中国高等教育学会秘书长范文曜，宁波市政府副秘书长王仁元，学院党委书记毛大龙、院长王梅珍，及宁波市教育局、市科技局、市服装协会、市经信局等相关负责人参加了当天中英纺织服装设计中心开园仪式。\r\n<p align=\"center\">\r\n	<img border=\"0\" src=\"http://www.zjff.net/uploads/news/pic/2012_04/25/141737.jpg\" />\r\n</p>\r\n<p align=\"center\">\r\n	王梅珍院长致辞\r\n</p>\r\n<p align=\"center\">\r\n	<img border=\"0\" src=\"http://www.zjff.net/uploads/news/pic/2012_04/25/141732.jpg\" />\r\n</p>\r\n<p align=\"center\">\r\n	宁波市教育局副局长胡赤弟发言\r\n</p>\r\n<p align=\"center\">\r\n	<img border=\"0\" src=\"http://www.zjff.net/uploads/news/pic/2012_04/25/141743.jpg\" />\r\n</p>\r\n<p align=\"center\">\r\n	英国总领事馆文化教育领事、华东地区主任包迈岫发言\r\n</p>', null, '', 'admin', '管理员', null, '25', '13', '师德创优', '3', '2012-05-06 13:36:15', null, null, '2', null, null, '中英产学研联动，助力宁波纺织服装业新发展', '2', '/resourcefiles/thumbnail/d48250ce07660a1230d5b49b5aa869b4.jpg', null);
