INSERT INTO `ws_information` VALUES ('42', '', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;font-weight:normal;\">思政教研室简介</span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;font-weight:normal;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:18pt;\">\r\n		<span style=\"font-family:\'新宋体\';font-size:12pt;\">思政教研室承担着</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">我院全院的思想政治理论课程教学，是学院德育工作的主渠道、主阵地。本</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">教研</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">室拥有一支学历高、结构合理、战斗力强、师资雄厚的教师队伍。现有专职教师１2名，其中副教授7名，讲师5名，另有多名经验丰富的兼职教师。</span><span style=\"font-family:\'新宋体\';font-size:12pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'新宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;按照教育部文件要求，</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">教研</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">室在全院开设的公共必修课程有《思想道德修养和法律基础》、</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">《毛泽东思想和中国特色社会主义理论体系概论》</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">、《形势与政策》。所开课使用的教材为高等教育出版社出版的马克思理论研究和建设工程重点教材。</span><span style=\"font-family:\'新宋体\';font-size:12pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'新宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;本着“学生为本”、“教书育人”的理念，在教学管理上，采用集体备课方式交流教学经验，实现教学资源的共享，取长补短，博采众长，提高教学水平，统一教学内容，统一教学进度，统一教学考试标准；在课堂教学中，</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">教师坚守现代高职教育理念，注重职业素质教育，突出职业能力培养，坚持“教、学、做合一”，理论和实践相结合，第一课堂和第二课堂相结合，因材施教，教学手段方法及考核评价方式灵活多样，提高学生思想政治理论素养，促进学生德智体美全面发展。</span><span style=\"font-family:\'新宋体\';font-size:12pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'新宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;在科研建设上，教师积极申报各级各类课题，参加有关学术交流活动，初步形成了教学科研梯队。本教研室现已或即将出版3本专著，八项省部级课题，90多篇论文，有着丰硕的科研成果。</span><span style=\"font-family:\'新宋体\';font-size:12pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'新宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;教研室的《思想道德修养与法律基础》、</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">《毛泽东思想和中国特色社会主义理论体系概论》</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">课均为在建的学院精品课程。</span><span style=\"font-family:\'新宋体\';font-size:12pt;\"></span> \r\n	</p>\r\n</div>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p align=\"center\">\r\n	<img alt=\"\" src=\"/attached/image/20120528/20120528175050_687.jpg\" width=\"700\" height=\"394\" /> \r\n</p>', '', '2012-05-28', 'admin', '管理员', '', '16', '54', '思政教研室', '3', '2012-05-28 17:39:05', '', '', '2', '', null, '思政教研室简介', '2', '/resourcefiles/thumbnail/54e6229cf797a03aad7c00f98e77312a.jpg', '1');
INSERT INTO `ws_information` VALUES ('43', '', '<p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:宋体;font-size:14pt;\">公共英语教研室简介</span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">公共英语教研室英语课程教学团队学历、职称及年龄结构合理。目前共有专职教师<span>28</span><span>人，实验管理人员</span><span>2</span><span>人，其中副教授</span><span>8</span><span>人，占</span><span>26.67%;&nbsp;</span><span>讲师</span><span>17</span><span>人，&nbsp;中级实验师</span><span>2</span><span>人，占</span><span>63.33%;&nbsp;</span></span><span style=\"font-family:\'新宋体\';font-size:12pt;\">具有硕士学位或在读硕士学位的有19人，</span><span style=\"font-family:\'宋体\';font-size:12pt;\">占</span><span style=\"font-family:\'新宋体\';font-size:12pt;\">63%；</span><span style=\"font-family:\'宋体\';font-size:12pt;\">具有双师资格的<span>3</span><span>人。就年龄结构而言，以中青年教师为主。公共英语教研室下设三个教学团队，负责全院</span><span>A</span><span>、</span><span>B</span><span>、</span><span>C</span><span>三个层次的英语教学。</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:30pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">整个教学团队师德高尚，</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">责任心强，</span><span style=\"font-family:\'宋体\';font-size:12pt;\">业务素质好。近几年教学团队中有多人次在学院各种教学技能竞赛中获得佳绩；教学团队</span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\">指导的学生也多人次获浙江省实用英语口语竞赛及写作竞赛一、二、三等奖。</span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:23.52pt;margin-left:5.88pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">英语教学团队也致力于高职教学研究，积极进行高职英语教学改革，改革英语教学内容，加强英语教学内容的职业针对性，编写出版了浙江省重点教材：</span><span style=\"font-family:\'宋体\';font-size:12pt;\">《职场英语实训-涉外商务》教程。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;margin-left:5.88pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"><img alt=\"\" src=\"/attached/image/20120529/20120529134838_497.jpg\" width=\"700\" height=\"525\" /><br />\r\n</span>\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>', '', '2012-05-29', 'admin', '管理员', '', '5', '55', '英语教研室', '3', '2012-05-29 13:49:12', '', '', '2', '', null, '公共英语教研室简介', '2', '/resourcefiles/thumbnail/c56b7a8a2360d5c986308dcec0f8f390.jpg', '1');
INSERT INTO `ws_information` VALUES ('44', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:16pt;font-family:宋体;\">体育教研室简介</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:30.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">体育教研室本着把体育教育作为人才培养体系的组成部分，把体育教学作为学校教学的重要方面，把身心素质作为素质教育的基础内容，承担着学校体育教学和其他体育工作。认真贯彻“</span><span style=\"color:#333333;font-size:12.0000pt;font-family:\'宋体\';\">以人为本、</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">健康第一”思想，</span><span style=\"color:#333333;font-size:12.0000pt;font-family:\'宋体\';\">全面实施“能力本位”课程改革，</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">全面推进素质教育，培养“终身体育”的意识和科学健身的能力，增强学生身心健康，提高学生体育文化素养，实现学校教育的整体目标，培养全面发展的创新人才而发挥体育教学的特殊功能。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"> 现有的体育教师<span>15</span><span>人，以中青年教师为主，其中副教授</span><span>4</span><span>名，讲师</span><span>11</span><span>名，具有硕士学位</span><span>2</span><span>人，在职攻读硕士研究生</span><span>3</span><span>人。</span></span><span style=\"font-size:12.0000pt;font-family:\'Symbol\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"> 体育教研室长期注重体育教学的改革创新，依据教育部《全国普通高等学校体育课程教学指导纲要》，实施课内课外一体化体育课程教学模式，逐步向全方位校园体育文化的多元模式发展。根据学生兴趣和学习需求，充分发挥场地设施和师资的潜力，开设体育专项课。现已开设篮球、足球、排球、乒乓球、羽毛球、健美、木球、瑜伽、网球、体育舞蹈、排舞及体育保健课等十几项课程项目，并成立相应的课外体育俱乐部，深受学生的欢迎。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"> 除体育课程以外，体育教研室还承担着全校每年进行一次《学生体质健康》测试、运动会、各类体育竞赛的训练和组织工作。</span><span style=\"color:#333333;font-size:12.0000pt;font-family:\'宋体\';\">近年来，学生参加国家、省的各项大学生体育竞赛中，取得了优异成绩。如：健美操队荣获全国比赛金牌1枚、银牌2枚、铜牌2枚；省冠军2次，篮球队荣获省比赛冠军1次，田径队荣获省比赛团体第三名、市比赛团体第二名等。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">体育教师锐意进取，勇于探索，积极开展科研活动，进几年公开发表学术论文<span>40</span><span>余篇，其中核心刊物</span><span>10</span><span>余篇，全国及省部级体育科学论文科报会</span><span>8</span><span>篇。主持或参与各级科研项目</span><span>30</span><span>余项，</span></span><span style=\"color:#333333;font-size:12.0000pt;font-family:\'宋体\';\">主编或参编各类教材4本</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n</div>', null, '2012-05-29', 'admin', '管理员', null, '4', '56', '体育教研室', '3', '2012-05-29 13:52:06', null, null, '2', null, null, '体育教研室简介', '1', null, '1');
INSERT INTO `ws_information` VALUES ('45', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:14.0000pt;font-family:\'宋体\';\">数学教研室简介</span><span style=\"font-size:14.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21.7500pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">我校</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">高职数学系列课程教学团队</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">现有</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">成员共有</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">7</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">人</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">（其中专职教师2名，兼职教师5名）</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">，其中副</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">高级职称</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">5名，</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">中级职称2</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">名</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">既有具有较深造诣且长期坚持在教学第一线授课的老教师，也</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">有</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">参加职业教育战线的年轻的</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">教师，团队可以很好地承担起学校开设的所有数学课程和学校有关数学的各项教学任务。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21.7500pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">近三年来，我们主要做了如下工作：</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21.7500pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">1<span>、课程</span></span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">主要建设内容：</span><span style=\"font-size:12.0000pt;font-family:\'Symbol\';\">&nbsp;</span><span style=\"font-size:12.0000pt;font-family:\'Symbol\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">根据高职学生的实际水平，我们在能力培养、思维素质提高、数学技术传授等方面寻找合适的切入点，这里我们没有受高等数学过去知识体系的束缚，我们突出地应用了通俗、有趣的方法向学员进行数学思维素质的熏陶以及数学技术的传授，体现了数学素质教育与数学技术教育的大方向；</span><span style=\"font-size:12.0000pt;font-family:\'Symbol\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:18.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">2<span>、</span></span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">在教材方面努力做到体现总体思路的内容。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">近三年来，主编了高职系列教材《高职高等数学应用基础教程》、高职高专规划教材</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">《高职数学应用基础》</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">和</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">《高职数学》</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">，作为副主编编写了国家</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">“</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">十一五</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">”</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">规划教材、</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">国家级精品课程配套教材、浙江省重点教材《经济数学》。教材内容充分体现了高职高等数学的特点，体现了课程总体思路。所用教材不单单是高等数学的科学文化基础教材，更是一本可以直接为专业服务的技术性很强的高等数学应用教材。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">3<span>、注重学生的科学素质培养</span></span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">根据高职高专人才培养目标，高职数学课程建设要重视数学文化素质的培养。</span><span style=\"font-size:12.0000pt;font-family:\'Symbol\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">我们能够从数学文化素质方面，来讲授数学知识、方法和应用，注重学生数学文化素质的培养。通过学习高等数学，能够使学生们比较深入地体会到高等数学的价值，体验到了数学文化素质的重要性，增强了学生的运算能力和数学的应用能力，使学生获得了数学文化素质的提高；</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24.0000pt;\">\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">根据高职高专人才培养目标，高职数学课程建设注重学生数学分析能力</span><span style=\"font-size:10.5000pt;font-family:\'宋体\';\">的培养。在突出高等数学应用性的同时，也注重传授高等数学所具有的运算、思维、逻辑、和理性推断的能力。使学生科学数学文化素质有了比较大的提高。</span><span style=\"font-size:10.5000pt;font-family:\'Symbol\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'Symbol\';\"></span>\r\n	</p>\r\n</div>', null, '2012-05-29', 'admin', '管理员', null, '3', '57', '数理教研室', '3', '2012-05-29 13:52:06', null, null, '2', null, null, '数学教研室简介', '1', null, '1');
