INSERT INTO `ws_information` VALUES ('48', '', '<p style=\"text-align:center;\">\r\n	<a href=\"/attached/file/20120530/20120530153441_322.xls\" target=\"_blank\">英语教研室课程成果.xls</a> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529145523_599.jpg\" width=\"700\" height=\"381\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529145542_661.jpg\" width=\"700\" height=\"60\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529150059_958.jpg\" width=\"700\" height=\"680\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529145624_395.jpg\" width=\"700\" height=\"91\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529145640_265.jpg\" width=\"700\" height=\"163\" /> \r\n</p>', '', '2012-05-29', 'admin', '管理员', '', '14', '52', '教学成果', '3', '2012-05-29 14:56:50', '', '', '2', '', null, '英语教研室课程成果', '2', '/resourcefiles/thumbnail/5ad0ad0909c77c2907292d5e6a74db90.jpg', '1');
INSERT INTO `ws_information` VALUES ('49', '', '<p style=\"text-align:center;\">\r\n	<a href=\"/attached/file/20120529/20120529154910_795.xls\" target=\"_blank\">体育教研室课程成果.xls</a> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529151545_272.jpg\" width=\"700\" height=\"374\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529151609_196.jpg\" width=\"700\" height=\"73\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529151628_130.jpg\" width=\"700\" height=\"396\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529151652_37.jpg\" width=\"700\" height=\"299\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529151714_125.jpg\" width=\"700\" height=\"355\" /> \r\n</p>', '', '2012-05-29', 'admin', '管理员', '', '9', '52', '教学成果', '3', '2012-05-29 15:17:48', '', '', '2', '', null, '体育教研室课程成果', '2', '/resourcefiles/thumbnail/c670fc66fb367c202bc8a40dd3feed11.jpg', '1');
INSERT INTO `ws_information` VALUES ('50', null, '&nbsp;\r\n<p>\r\n	<b><span style=\"font-family:宋体;font-size:12pt;\"></span></b>\r\n</p>\r\n<p style=\"text-indent:23.6pt;\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">王苹</span></b><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">，女，<span> 1963 </span>年出生，浙江绍兴人。毕业于北京师范大学中文系。原为宁波大学人文与传媒学院教授， 硕士生导师，<span>2010</span>年<span> 4 </span>月调入浙江纺织学院人文分院，任院长。为国家级普通话水平测试员，华东修辞学会、浙江省语言学会会员，</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">浙江省语言学修辞学会副会长。</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\"> 主要从事现代汉语教学与研究， 开设过“现代汉语”、“形式逻辑”、“修辞研究”、“汉语通论”、“大学生口才与交际”、“教师教学语言”、“普通话学习与考级”等专业课与选修课，教学效果优秀，深受学生欢迎。学术研究方向为修辞学、文化语言学。在《语言研究》、《修辞学习》、《汉字文化》、《辞书研究》、《现代语文》、《贵州社会科学》、《中文》（台湾）等国内一、二级中文核心刊物及学报上发表论文<span> 70 </span>余篇；出版专著《汉语修辞与文化》一部，合著《廉政文化丛书》之一，</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">担任《汉语通论》主编、</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">《现代汉语》、《普通话学习与测试教程》、《实用应用文》</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">教材副主编三部</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">、参编《新编大学生口语交际教程》《文学通论》教材两部；主持承担了浙江省教育厅、浙江省社科联、浙江省哲社规划、浙江省新世纪高等教育教学改革研究项目 、宁波市及宁波大学的科研课题<span> 20</span>余项；专著获浙江省社科联省级学术著作出版基金项目资助，并获得宁波市第十一届哲学社会科学优秀成果奖二等奖；教研成果“‘教<span> \' </span>‘测<span> \' </span>结合，建立高校普通话教学体系”获得宁波市第四届高校优秀教学成果二等奖 、 宁波大学优秀教学成果二等奖；宁波市教育规划课题成果获宁波市教育规划二等奖；所写论文获得过浙江省语言文字论文三等奖；宁波市首届普通话科研论文二等奖。除此以外，还 获得过宁波大学教学优秀奖， 宁波大学 文学院首届 教学评比一等奖，宁波大学优秀毕业论文指导教师奖，宁波大学优秀青年骨教师奖，宁波大学“事业家庭兼顾型”优秀女教职工奖，宁波大学优秀工会干部，宁波大学优秀班主任，浙江省省级优秀测试员，宁波大学优秀测试员，宁波大学推普先进个人等多项荣誉称号。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '29', '主讲教师', '4', '2012-05-29 15:55:25', null, null, '2', '/c336bcf23b35fb33c1588d1700118fc9.jpg', null, '王苹', '1', null, '1');
INSERT INTO `ws_information` VALUES ('51', null, '&nbsp;\r\n<p style=\"text-align:center;text-indent:14pt;\" align=\"center\">\r\n	<span style=\"font-family:宋体;font-size:14pt;\">崔玉环个人简历</span><span style=\"font-size:14pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">崔玉环，女，</span><span style=\"line-height:150%;font-size:12pt;\">1962</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年生人，蒙古族。浙江纺织服装职业技术学院人文学院思想政治理论课教师，国家二级心理咨询师。</span><span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">崔玉环</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">老师热爱教育事业，热爱学生，为人师表。自<span>2003</span>年<span>3</span>月调入我院以来，一直从事大学生思想政治理论课教学和大学生心理健康教育工作。深受广大师生的欢迎。同时长期受聘于宁波市教育局教育工会教师服务中心心理咨询热线心理咨询师。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">崔</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">老师热爱德育工作，具有丰富的德育工作经验，被聘为学院心理健康教育中心兼职心理咨询师，善于做学生思想政治工作。在<span>8</span>年的大学生思想政治理论课教学和大学生心理咨询服务中，崔老师被学生评选为“我心目中的好老师”。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">崔</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">老师在我院承担《思想道德修养与法律基础》、《毛泽东思想和中国特色社会主义理论体系概论》两门课程的教学工作，并担任《思想道德修养与法律基础》精品课程的负责人。教学中注重教学改革，在<span>2009</span>年宁波市思政理论课年会中，获得优秀论文和课件一等奖。在学院“思政理论教师精彩一课”评比中，获得教案和课件评比一等奖。课堂教学中，崔老师从学生关注的热点问题及社会热点问题入手，结合教材内容，以鲜活的案例进行讲解。崔老师的思政理论课是学生每天最期待的课。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">发表“<span>大一新生心理适应能力现状与分析”“高职院校思政理论课教师教学效能感研究”等多篇论文，完成“</span>高职学生网络成瘾的现状、影响因素及干预措施研究”“<span>高职学生心理互助教育模式研究”等多项课题，并获得宁波市高校思想政治教育研究会优秀论二等奖，多次获得浙江纺织服装职业技术学院优秀论文、优秀课题奖。<span></span></span></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '29', '主讲教师', '4', '2012-05-29 15:55:25', null, null, '2', '/fd7ea2dc4ca8133637d1614e26f6f658.jpg', null, '崔玉环', '1', null, '1');
INSERT INTO `ws_information` VALUES ('52', null, '&nbsp;\r\n<p style=\"text-indent:23.6pt;\">\r\n	<b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:23.6pt;\">\r\n	<b>周素珍，</b>女，<span> 1966 </span>年出生，浙江余姚人。毕业于浙江师范大学政教系，副教授，中共党员。长期从事思想政治理论课教学，开设过“马克思主义哲学”、“政治经济学”、“法律基础”、“思想道德修养与法律基础”、“毛泽东思想和中国特色社会主义理论体系概论”等课程。教学过程秉承“以生为本”的理念，注重学生思辩能力和实际参与意识的提高，教学效果好。学术研究方向为政治学、教育学。在《中国林业教育》等刊物公开发表论文<span>16</span>余篇；参编由成都科技大学出版社的《从鸦片战争到改革开放》一书；主持并参与院级思政课题多项；参与“毛泽东思想和中国特色社会主义理论体系概论”院级精品课程的建设；多次在市高职院校思政研究会上获得论文一等奖；并先后在校讲课比赛、院演讲比赛、院思政理论课精彩一课比赛、院思政理论课课件比赛、院教师技能比赛中获得一等奖。<span>2011</span>年获宁波市优秀思政工作者称号。\r\n</p>\r\n<p style=\"text-indent:23.6pt;\">\r\n	<br />\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '1', '29', '主讲教师', '4', '2012-05-29 15:55:25', null, null, '2', '/810d286a748f7afd72657863c76fdc7a.jpg', null, '周素珍', '1', null, '1');
INSERT INTO `ws_information` VALUES ('53', null, '&nbsp;\r\n<p>\r\n	<b><span style=\"font-size:12pt;\">&nbsp;</span></b>\r\n</p>\r\n<p style=\"text-indent:23.6pt;\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">张莹，</span></b><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">女，</span><span style=\"line-height:150%;font-size:12pt;\"> 1964</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年出生，陕西省西安市人。毕业于兰州大学外语系，英语副教授</span><span style=\"line-height:150%;font-size:12pt;\">,</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">主要从事英语教学与研究，</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">开设过“大学英语”、“英语精读”、“英语泛读”、“英语语音”、“财经英语”、等公共英语课与专业课，教学效果优秀，深受学生欢迎。学术研究方向为英语教学、</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">英美文化研究</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">。在《中国民航飞行学院学报》、《鸡西大学学报》、《浙江纺织服装职业技术学院学报》等学报上发表论文</span><span style=\"line-height:150%;font-size:12pt;\"> 20</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">篇；与团队共创《大学英语》精品课程。参编《财经英语》教材获学院教学成果三等奖奖项，此外，还曾获得教师教案优秀奖等奖项。</span><span style=\"line-height:150%;font-size:12pt;\"> </span>\r\n</p>\r\n<p style=\"text-indent:23.6pt;\">\r\n	<span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:23.6pt;\">\r\n	<span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '29', '主讲教师', '4', '2012-05-29 15:55:25', null, null, '2', '/26a3228a35b8c7707bf408994946e8f4.jpg', null, '张莹', '1', null, '1');
INSERT INTO `ws_information` VALUES ('54', null, '&nbsp;\r\n<p>\r\n	<b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:17.7pt;\">\r\n	<b>郑卡莉，</b>女，<span> 1971 </span>年出生，浙江象山人。毕业于杭州大学英语学院。现为人文学院副教授。主要从事英语教学与教师评价研究。多年来一直从事公共外语教学。在《教师教育研究》、《教育评论》、《中国职业技术教育》等一些国内核心刊物及学报上发表论文多篇；参编《服装英语<span> 300 </span>句》教材一部；主持承担了中央教科所《职业院校教师评价体系研究》课题及学院《网络环境的开放式英语教学》课题，并参与各级课题多项。曾获学院先进工作者荣誉称号。<span> </span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '29', '主讲教师', '4', '2012-05-29 15:55:18', null, null, '2', '/31d49c4211ee319e760527930b6574eb.jpg', null, '郑卡莉', '1', null, '1');
INSERT INTO `ws_information` VALUES ('55', null, '&nbsp;\r\n<p>\r\n	<b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:23.6pt;\">\r\n	<b>罗技科，</b>男，<span> 1978</span>年出生，浙江宁波人。毕业于宁波大学。<span>2001</span>年进入宁波服装职业技术学院任教。主要从事大学英语的教学工作。<span> 2007</span>年辅导两位同学在浙江省第四届高职高专实用英语口语大赛中都获得三等奖。<span>2010</span>年指导学生王斌权和金静参加浙江省第七届高职高专实用英语口语大赛，分别获得一等奖和三等奖。学术研究方向为英语教学法、跨文化研究。独立发表了论文《高职英语听力分层次教学初探》、《论高职学生英语听力理解障碍及对策》、《浅析高职英语教学中的德育渗透》、《影响高职学生英语习得的非智力因素探索》、《浅析英汉文化差异对翻译的影响》等。同时参加各类科研课题的开发和撰写，主持院级课题《影响高职学生英语习得的非智力因素探索》和《任务型教学在高职高专英语口语教学中的应用》，参与多个院级课题研究和一个省级课题《服装英语<span>300</span>句》，参编《全国高等学校英语应用能力考试解题技巧与模拟试题（<span> A</span>级）》和《全国高等学校英语应用能力考试解题技巧与模拟试题（<span>B</span>级）》。历年获得过艺术系优秀班主任、学院先进工作者、优秀共产党员。\r\n</p>\r\n<p style=\"text-indent:23.6pt;\">\r\n	<b><span></span></b>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '29', '主讲教师', '4', '2012-05-29 15:55:18', null, null, '2', '/1de16d42a51f335eb410c9f5e01f37da.jpg', null, '罗技科', '1', null, '1');
INSERT INTO `ws_information` VALUES ('56', null, '&nbsp;\r\n<p style=\"text-indent:27.1pt;\">\r\n	<b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:30.95pt;\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">左秀山</span></b><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">，男，</span><span style=\"line-height:150%;font-size:12pt;\">&nbsp;1960&nbsp;</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年出生，哈尔滨人，毕业于黑龙江大学数学系，学士学位。曾到黑龙江商学院、上海复旦大学、北京师范大学进修学习应用数学、理论数学、计算机硬件、计算机程序设计和应用。现任浙江纺织服装职业技术学院人文学院副教授，数学教研室主任。</span><span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:30.85pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">主要从事高职数学的教学、教改和数学应用研究工作。本人高校教龄</span><span style=\"line-height:150%;font-size:12pt;\">30</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年，在教学、教研等方面积累了比较丰富的高校教育经验，承担过大量各级各类高等数学的教学任务。开设的课程有：</span><span style=\"line-height:150%;font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">高等数学</span><span style=\"line-height:150%;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">、</span><span style=\"line-height:150%;font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">经济数学</span><span style=\"line-height:150%;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">、</span><span style=\"line-height:150%;font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">概率统计</span><span style=\"line-height:150%;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">、</span><span style=\"line-height:150%;font-size:12pt;\"> “</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">线性代数</span><span style=\"line-height:150%;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">、</span><span style=\"line-height:150%;font-size:12pt;\"> “</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">数学建模</span><span style=\"line-height:150%;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">、“专升本数学”、</span><span style=\"line-height:150%;font-size:12pt;\">“</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">统计学原理</span><span style=\"line-height:150%;font-size:12pt;\">”</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">等数学课、统计课及其选修课。本人教学一贯认真负责，深受广大学生的欢迎。</span><span style=\"line-height:150%;font-size:12pt;\">&nbsp;</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">主要研究方向：高等职业数学教育、应用数学及其数学软件应用。在国内正式刊物及学报上发表论文数十</span><span style=\"line-height:150%;font-size:12pt;\">&nbsp;</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">篇；宁波市高校高等数学教学论文三等奖；全国大学生数学建模比赛教师指导省三等奖；主编出版了《</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">高等数学应用基础教程</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">》、</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">《高职数学应用基础》和《高职数学》三部高职教材</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">，</span><span style=\"line-height:150%;font-size:12pt;\">&nbsp;</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">本科《</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">线性代数</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">》一部教材副主编，</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">副主编出版了国家“十一五”规划教材、</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">国家级精品课程配套教材、浙江省重点教材《经济数学》一部，</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">副主编</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">高职教材</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">《</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">经济数学</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">》一部；积极承担和参与省级课题和校级</span><span style=\"line-height:150%;font-size:12pt;\">&nbsp;</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">课题多项；长期开展高职数学的教学研究、教学改革和数学软件应用的教学实践活动。</span><span style=\"line-height:150%;font-size:12pt;\"><span>&nbsp;&nbsp;&nbsp;&nbsp; </span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '29', '主讲教师', '4', '2012-05-29 15:55:18', null, null, '2', '/dc77fc6e43daed319474b2a9ec31f3a6.jpg', null, '左秀山', '1', null, '1');
INSERT INTO `ws_information` VALUES ('57', null, '&nbsp;\r\n<p style=\"text-indent:21.1pt;\">\r\n	<b><span style=\"font-family:宋体;\"></span></b>\r\n</p>\r\n<p style=\"text-indent:24.1pt;\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">王苏鹤，</span></b><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">男，<span>1963</span>年出生，浙江余姚人。毕业于浙江师范大学政教系。浙江纺织服装学院人文分院副教授，思政教研室主任。主要从事马克思主义和思想政治理论教育教学与研究，</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">开设过“思想道德修养与法律基础”、“毛泽东思想和中国特色社会主义理论体系概论”、“当代世界经济与政治”等公共必修课与选修课，教学效果优秀，深受学生欢迎。</span><span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">学术研究方向为政治学、思想政治教育学。在《社会科学》、《宁波工程学院学报》等国内刊物及学报上发表论文多篇；合著有《高职院校思想政治教育工作创新研究》、《思想政治教育的宁波模式》等二部；主持承担了学院科研、教研课题多项；获得</span><span style=\"line-height:150%;font-size:12pt;\">2007</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年年度优秀教研室称号。</span><span style=\"line-height:150%;font-size:12pt;\">2011</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年学院纪念建党</span><span style=\"line-height:150%;font-size:12pt;\">90</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">周年参加学院微型党课比赛二等奖，获得</span><span style=\"line-height:150%;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">—</span><span style=\"line-height:150%;font-size:12pt;\">2011</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年度学院优秀共产党员，</span><span style=\"line-height:150%;font-size:12pt;\">2010</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">—</span><span style=\"line-height:150%;font-size:12pt;\">2011</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年度宁波市</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">高校</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">优秀思政工作者，宁波市教育系统“五带头”共产党员称号。</span><span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '29', '主讲教师', '4', '2012-05-29 15:55:18', null, null, '2', '/779734446afe9cec3cea1fa49fdfb0b3.JPG', null, '王苏鹤', '1', null, '1');
INSERT INTO `ws_information` VALUES ('58', null, '&nbsp;\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">施慧英，女，浙江余姚人</span><span style=\"line-height:150%;font-size:12pt;\">, 1986</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年毕业于浙江师范大学英语系，现任公共英语教研室主任。主要从事英语教学与研究，开设过“综合英语”、“外贸英语函电”、“商务英语”、“外贸英语”等课程，教学效果好。研究方向为中西方文化及语言对比和高职英语教学。发表论文近</span><span style=\"line-height:150%;font-size:12pt;\">20</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">篇。主编教材及教辅二部，其中一部是浙江省重点教材。主持承担了多项中国职业技术教育学会课题、全国英语教学指导委员会课题、宁波市教育规划课题及学校课题。曾获得过校优秀班主任、校优秀授课奖、校优秀教案奖、校先进工作者、宁波市三优秀三文明优秀教职工等荣誉称号。</span><span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '1', '29', '主讲教师', '4', '2012-05-29 15:55:18', null, null, '2', '/dc5a5c2949e87d4e8da879ebd0f8f490.jpg', null, '施慧英', '1', null, '1');
INSERT INTO `ws_information` VALUES ('59', null, '&nbsp;\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">盛菊霞，女，</span><span style=\"line-height:150%;font-size:12pt;\"> 1975 </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年出生，浙江新昌人。毕业于浙江师范大学体育系，讲师。长期从事体育教学和训练工作。教学过程追求“身心兼育”的理念，在注重学生健身技能掌握的同时，尤其注重学生心理素质方面的健康发展。主持的《高职院校阳光体育“三育人”模式》获院级教学成果奖二等奖。学术研究方向为体育教学和公共体育场馆。在《浙江体育科学》、《宁波大学学报》（社科版）等刊物公开发表论文近</span><span style=\"line-height:150%;font-size:12pt;\">10</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">篇；主持市局级及院级课题</span><span style=\"line-height:150%;font-size:12pt;\">5</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">项，参与国家级及省部级课题</span><span style=\"line-height:150%;font-size:12pt;\">2</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">项。获</span><span style=\"line-height:150%;font-size:12pt;\">2011</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">《高校体育年鉴》（理论篇）论文一等奖；参与宁波市首届高校体育教师技能比赛获二等奖。</span><span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '1', '29', '主讲教师', '4', '2012-05-29 15:55:18', null, null, '2', '/aa6b3e10b2b32ff768939436d520d7d8.jpg', null, '盛菊霞', '1', null, '1');
INSERT INTO `ws_information` VALUES ('60', null, '&nbsp;\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">李洪波，女，</span><span style=\"line-height:150%;font-size:12pt;\"> 1962 </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年出生，黑龙江省佳木斯人。</span><span style=\"line-height:150%;font-size:12pt;\">1984</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年毕业于黑龙江大学哲学系。原为佳木斯市委党校教师，</span><span style=\"line-height:150%;font-size:12pt;\">2003</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年</span><span style=\"line-height:150%;font-size:12pt;\"> 2</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">月调入浙江纺织学院人文分院任思政课教师。为院党建和思政学会会员、院督导室成员兼人文分院督导组组长。主要从事思政理论的教学与研究，</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">开设和承担过“马克思主义哲学原理”、“青年学”、</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">“行政法与行政诉讼法”、“婚姻法学”、“法理学”、“法律教程”、“思想道德修养和法律基础”、</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">“形势与政策”、“毛泽东思想与中国特色社会主义理论体系概论”</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">、“世界反腐败模式比较”等专业课与专题教学。学术研究方向为马克思主义基本理论。先后</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">在《</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">中国科学文化出版社》、</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">《</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">黑龙江人民出版社》、《学术交流》、《佳木斯大学社会科学学报》</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">、《高等教育出版社》等刊物及学报上发表论文、教材、课题等</span><span style=\"line-height:150%;font-size:12pt;\"> 30</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">多篇。曾获市委党校《哲学原理》课优秀教学奖和优秀党员称号。两篇论文获省级学会一等奖；</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">在宁波市职业技术院校政治经济教育研究会年会上论文获一等奖<span>2</span>次、二等奖<span>1</span>次、<span><span>&nbsp; </span></span>课件一等奖<span>1</span>次、二等奖<span>3</span>次；</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">在</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">宁波市高校优秀党课评比中获一等奖；在</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">学院思政“精彩一课”、多媒体课件、微型党课比赛中均获二等奖</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">，</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">优秀教案</span><span style=\"line-height:150%;font-size:12pt;\"> </span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">三等奖；在学院教学业绩和年度考核中，曾获</span><span style=\"line-height:150%;font-size:12pt;\">A</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">和优秀的评价。</span><span style=\"line-height:150%;font-family:楷体_GB2312;font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '1', '29', '主讲教师', '4', '2012-05-29 15:55:18', null, null, '2', '/34dda29582525a7d83fbcbb5355abe2b.jpg', null, '李洪波', '1', null, '1');
INSERT INTO `ws_information` VALUES ('61', null, '&nbsp;\r\n<p style=\"text-indent:21pt;\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">欧鹏飞，男，</span><span style=\"line-height:150%;font-size:12pt;\">1979</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年出生于山城重庆。毕业于长春师范学院体育系。长期从事健美操、瑜伽的教学和训练工作，指导学生多次在全国和省市健美操比赛中获得冠军。</span><span style=\"line-height:150%;font-size:12pt;\">2011</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年获宁波市最佳团队奖，学院育人奖。现担任宁波市体操协会竞赛部主任。</span><span style=\"line-height:150%;font-size:12pt;\"> </span>\r\n</p>\r\n<p style=\"text-indent:24pt;\">\r\n	<span style=\"line-height:150%;font-size:12pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '1', '29', '主讲教师', '4', '2012-05-29 15:55:18', null, null, '2', '/41be2ecc686c638ed14439117671fb63.jpg', null, '欧鹏飞', '1', null, '1');
INSERT INTO `ws_information` VALUES ('62', '', ' \r\n<p style=\"text-indent:168.65pt;\">\r\n	<b><span style=\"font-family:宋体;font-size:12pt;\">余国良简介</span></b><b><span style=\"font-size:12pt;\"></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\"><span>    </span></span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">余国良，男，浙江慈溪人。英语硕士、教授，宁波市外文翻译学会常务理事、浙江外文学会理事、中国英汉语比较研究会会员、上海市“口译岗位资格证书”口试考官。主要研究方向为英汉语比较、语际语语言学、语料库语言学。先后在《外国语》《外语学刊》《四川外语学院学报》等外语类核心期刊及其他高校学报上发表学术论文三十余篇，出版《语际语语用学研究：帮助我国学生提高英语语用能力》（中国文史出版社）、《语料库语言学的研究与应用》（四川大学出版社）等专著两本，参编《计算机英语》（高等教育出版社）教材一本、参译《科幻之路》（福建少年儿童出版社）一本；主持完成了国家教育部人文社科重点研究基地、浙江省教育厅、宁波市教育科学规划及学校重点课程建设等项目多项；讲授过英语精读、英语语法、英语语音、英语口译、翻译理论与实践等专业课程，并被确定为学校中青年骨干教师、中青年学科带头人；获得过学校优秀班主任、先进工作者、教学成果奖、科研成果奖、“我最崇敬的教师”（由全校学生评选）及宁波市优秀共产党员等称号或荣誉。<span></span></span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>', '', '2012-05-29', 'admin', '管理员', '', '0', '29', '主讲教师', '4', '2012-05-29 15:55:18', '', '', '2', '/43efbc528340ec2dc55fd338fadeeed9.jpg', null, '余国良', '1', null, '1');
INSERT INTO `ws_information` VALUES ('63', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">动感地带杯</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">”</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">宁波市第五届大学生田径运动会系列报道之一</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\"><br />\r\n</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">我院体育健儿获佳绩</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">王锡金</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">04</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">29</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">28</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日，宁波市第五届大学生田径运动会在浙江医药高等专科学校隆重开幕，截至下午</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">时，我校已取得</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">金</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">银的好成绩：陈玲燕同学以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11.5</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米的成绩获得女子铅球冠军并打破了赛会纪录，周碧云、徐晓东两同学分获女子</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">100</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米、男子</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">100</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米金牌、银牌。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　虽然天气酷热，但我校老师与运动员会克服一切困难，加倍努力，争创更好成绩。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529153233_211.png\" width=\"268\" height=\"200\" />\r\n					</p>\r\n					<p>\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529153306_777.png\" width=\"268\" height=\"200\" />\r\n					</p>\r\n					<p>\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529153339_773.png\" width=\"346\" height=\"460\" />\r\n					</p>\r\n<img alt=\"\" src=\"/attached/image/20120529/20120529153411_484.png\" width=\"346\" height=\"460\" /> \r\n					<p>\r\n						&nbsp;\r\n					</p>\r\n					<p style=\"text-align:left;\" align=\"center\">\r\n						&nbsp;\r\n					</p>\r\n<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：常洪彬</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '5', '23', '竞赛获奖', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '“动感地带杯”宁波市第五届大学生田径运动会系列报道之一', '2', '/resourcefiles/thumbnail/5e33d966a7ce181a321c0077e86819d8.png', '1');
INSERT INTO `ws_information` VALUES ('64', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">我院篮球队获佳绩</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">王锡金</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">06</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">13</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">6</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日，宁波市大学生男子篮球赛在宁波大学体育馆圆满落幕，我院男女两支篮球队均获得第四名的好成绩，我院男篮主教练常洪彬老师被评为最佳教练员。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　通过此次比赛，我院两只篮球队，水平得到了大幅度提升，我们希望通过接下来的刻苦训练，我院篮球队能在</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月的浙江省</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">CUBA</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">比赛中，取得更好的成绩。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529153535_849.png\" width=\"500\" height=\"337\" /><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：常洪彬</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '23', '竞赛获奖', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '我院篮球队获佳绩', '2', '/resourcefiles/thumbnail/64c72c57b445c00b6234f41392b541ca.png', '1');
INSERT INTO `ws_information` VALUES ('65', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">我院体育教师获省级说课比赛三等奖</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">06</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">20</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　在</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2011</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年浙江省高职高专院校体育教师说课比赛中，人文学院体育教研室杜林颖老师荣获三等奖，常洪斌老师荣获优胜奖。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　据悉，此次比赛在浙江育英职业技术学院隆重举行，共有来自全省的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">40</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">多所高职院校</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">60</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">多位体育教师参加赛事。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<img alt=\"\" src=\"/attached/image/20120529/20120529153906_628.png\" width=\"500\" height=\"331\" /> \r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>&nbsp;\r\n					</p>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529153922_567.png\" width=\"500\" height=\"331\" /><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '23', '竞赛获奖', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '我院体育教师获省级说课比赛三等奖', '2', '/resourcefiles/thumbnail/a573e67a3fe3dbe4e2cd35d3ce85c167.png', '1');
INSERT INTO `ws_information` VALUES ('66', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">院健美操队再获殊荣</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">09</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">5</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">—6</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日在舟山体育馆举行的浙江省首届体育社团运动会啦啦操比赛中，我院健美操队再次以无可懈击的技艺和表演征服了现场的评委和观众，夺得了本次比赛的一等奖。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　浙江省首届体育社团运动会啦啦操比赛由省体育局和省体育总会共同主办，市体育局和市体育总会承办。来自全省</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">8</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">支代表队的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">128</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">名运动员报名参赛。基于我院在宁波市第十六届运动会开幕式上的优秀表现，宁波市体育局决定选派我院啦啦操队代表宁波市出征比赛。比赛中，我院健儿怀着为学院、地方争光的强烈愿望，继续发扬集体主义精神，团结协作，奋勇拼搏，用优秀的完成和富有激情的感染力最终取得了比赛一等奖和最佳完成奖的好成绩。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　本次比赛由体育教研室欧鹏飞老师带队，参赛学生有：国际学院的董琴琴同学，机电与轨道交通学院的沈吴璇同学，商学院的林微微同学，艺术与设计学院的章燕君、李彬彬、蔡董永、曾新新、杨培灵、陈小莉、戴梦丽、林安妮、李伟东同学。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529154116_618.png\" width=\"500\" height=\"334\" />\r\n					</p>\r\n<img alt=\"\" src=\"/attached/image/20120529/20120529154127_472.png\" width=\"500\" height=\"334\" /> \r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						&nbsp;\r\n					</p>\r\n<img alt=\"\" src=\"/attached/image/20120529/20120529154138_63.png\" width=\"500\" height=\"334\" /> \r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						&nbsp;\r\n					</p>\r\n<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：欧鹏飞</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '23', '竞赛获奖', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '院健美操队再获殊荣', '2', '/resourcefiles/thumbnail/1ea4ceb3c285618277c5f51efe78c13d.png', '1');
INSERT INTO `ws_information` VALUES ('67', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">我院田径健儿在省第十三届大运会中喜获佳绩</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">林宗德</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">30</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">25</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">至</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">29</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日，在由浙江省教育厅、浙江省体育局和台州市人民政府主办，台州学院承办的浙江省第十三届大学生运动会中，我院田径健儿在团长杨威副院长、领队张宾、教练员葛金华、林宗德、刘建中、周荣春老师的带领下，顽强拼搏，不畏强手，荣获</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">金</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">银的佳绩，并且打破了</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">项省大学生田径项目最高纪录，破</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">项学院田径最高纪录，在浙江省</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">43</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">所高职院校中位列奖牌榜第</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">7</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">位，团体总分第</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">9</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">位，比赛成绩较上届大运会有了明显提高。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　本届大运会共设包括田径、游泳、篮球等</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">个项目，有来自全省</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">76</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">所高校的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1289</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">名大学生运动健儿进行了激烈角逐，是历届当中规模最大、项目设置最多、参赛高校数量和参赛运动员人数最多的一次体育盛会。我院的田径健儿们团结一致、奋勇争先、不负众望，在多个此赛项目中展现风采，其中</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">09</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">网络陈玲燕同学获女子铁饼冠军，铅球亚军，并以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">20.05</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米的成绩打破省大学生乙组女子铅球最高纪录。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">09</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">市营</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">徐晓东同学获男子</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">200</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米冠军，</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">100</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米亚军，并以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11.22</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">秒的成绩打破省大学生乙组男子</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">100</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米最高纪录。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">市营</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">陆佳辉同学获男子铅球亚军，并以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11.94</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米的成绩打破校男子铅球最高纪录。校男子</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4×400</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米接力队（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">轨道交通蔡文超、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">计算机胡思聪、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">10</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">模具</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">朱凯晗、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">09</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">市营</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">徐晓东）以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">35</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">秒</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">85</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的成绩打破校</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4×400</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米最高纪录。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">10</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">模具</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">朱凯晗同学以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">0</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">秒</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">97</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的成绩打破校</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">400</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">米栏最高纪录。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　同时，在本届运动会上，我院运动员不仅赛出了水平，而且赛出了风格。我院田径队被本届大赛组委会评为体育道德风尚队，陈玲燕、徐晓东同学被评为省大运会优秀运动员，林宗德老师被评为省大运会田径优秀教练员，盛菊霞老师被评为省大运会田径优秀裁判员。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　省大学生运动会是我省最高规格的高校体育赛事，每</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年举办一届，由各高校轮流承办。第十四届大运会将于</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年后在温州大学举行。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529154331_347.png\" width=\"503\" height=\"375\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">参赛田径运动员</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529154344_818.png\" width=\"503\" height=\"375\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">参赛教练员、运动员合影</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529154427_234.png\" width=\"268\" height=\"200\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">奖牌</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529154508_803.png\" width=\"424\" height=\"460\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">奖牌</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529154552_119.png\" width=\"500\" height=\"375\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">优秀教练员证书</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529154623_259.png\" width=\"500\" height=\"375\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">获奖证书</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：林宗德</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '23', '竞赛获奖', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '我院田径健儿在省第十三届大运会中喜获佳绩', '2', '/resourcefiles/thumbnail/6528d3f35491955f771f09fbb7b00cf3.png', '1');
INSERT INTO `ws_information` VALUES ('68', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">毛大龙书记到人文学院调研</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">10</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">10</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">13</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">10</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">12</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日上午，毛大龙书记来到人文学院调研，人文学院院长王苹、党总支负责人丁丽君等陪同调研。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　调研中，院长王苹向毛书记汇报了人文学院班子近期开展的主要工作、学院示范创建等相关情况，党总支负责人丁丽君汇报了党建工作情况。毛大龙书记对人文学院新学期所取得的各项成绩表示了充分的肯定，同时也希望学院的每一位教职员工在这个科学发展的时代，进一步解放思想，努力树立抢抓机遇的意识，实现与社会全面对接，积极迎头赶上，提升自身内涵，保证学院的可持续发展。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　最后，毛大龙书记还饶有兴致地就人文学院的新招牌谈了自己对</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人文</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">概念的理解，希望人文学院真正实现招牌上所标识的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">铸人文素养之魂，煅明辨慎思之质</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">、崇德厚道、明体达用之高素质强技能应用型人才的培养目标。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '4', '28', '人文乐园', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '毛大龙书记到人文学院调研 ', '1', null, '1');
INSERT INTO `ws_information` VALUES ('69', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">学院领导指导人文学院</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">创先争优</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">”</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">工作</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">10</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">02</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;text-indent:21pt;\" align=\"left\">\r\n						<span style=\"line-height:150%;font-family:宋体;color:black;\">11</span><span style=\"line-height:150%;font-family:宋体;color:black;\">月<span>2</span>日</span><span style=\"line-height:150%;font-family:宋体;color:black;\">上午，学院党委副书记陈光莅临人文学院督察并指导<span>“</span>创先争优<span>”</span>工作。</span><span style=\"line-height:150%;font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:left;text-indent:21pt;\" align=\"left\">\r\n						<span style=\"line-height:150%;font-family:宋体;color:black;\">丁丽</span><span style=\"line-height:150%;font-family:宋体;color:black;\">君代表人文学院党总支作了创先争优工作汇报：一是成立了创先争优领导小组，结合分院实际情况，部署落实各阶段工作；二是按照优秀党员<span>“</span>五带头<span>”</span>标准，认真召开专题组织生活会；三是开展主题党日活动，全面推行党员示范岗；四是树立典型，积极推进党建和学院示范建设；五是完成<span>“</span>八个一<span>”</span>工程，即<span>“</span>联系一个学生支部、结对一个寝室、结对一个班级、上一堂公开课、开展一对一帮困、关爱一个贫困学生、作好一个科研项目、联系一个积极分子。<span>”</span>人文学院将努力以<span>“</span>创科学发展先锋队，做教育服务排头兵<span>”</span>为活动主题，以<span>“</span>建组织，强机制，树形象，作贡献<span>”</span>为载体，找准党建工作与教学、科研、校园文化建设等各项工作的结合点，开展好创先争优活动，力争将工作落实到实处。<span></span></span>\r\n					</p>\r\n					<p style=\"text-align:left;text-indent:21pt;\" align=\"left\">\r\n						<span style=\"line-height:150%;font-family:宋体;color:black;\">&nbsp;</span><span style=\"line-height:150%;font-family:宋体;color:black;\">陈光副书记在听取汇报后，充分肯定了人文学院在开展创先争优活动中已取得的成绩，他说，人文学院本着<span>“</span>思想重视，精心组织<span>”</span>八个字的工作方针，在前期的创先争优活动中做到了六个有：有组织、有机构部署工作；有方案，有主题开展工作；有人才，有载体落实工作。尤其是人文学院的<span>“</span>八个一<span>”</span>工作确实是从细节上保障了创先争优活动开展的实效性。<span></span></span>\r\n					</p>\r\n					<p style=\"text-align:left;text-indent:21pt;\" align=\"left\">\r\n						<span style=\"line-height:150%;font-family:宋体;color:black;\">接着，陈光副书记就人文学院<span>“</span>创先争优<span>”</span>的下一步工作开展提出了几点建议：一是进一步明确工作实施目标，二是重视基层堡垒作用，三是解决突出问题；四是建立长效机制。<span></span></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">王锡金</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '28', '人文乐园', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '学院领导指导人文学院“创先争优”工作', '1', null, '1');
INSERT INTO `ws_information` VALUES ('70', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">新学期</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\"> </span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">新目标</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\"> </span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">新进步</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">——</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">人文学院召开新学期首次教职工大会</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">02</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">28</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　虎岁扬威兴骏业，兔毫着彩绘宏图。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">26</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日上午九点半，人文学院在四楼会议室召开新学期首次教职工大会。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　会议伊始，丁丽君书记向全体教职工致以热情洋溢的新年祝福，接着王苹院长以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“2011</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年是</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">十二五</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">规划的开局之年，也是我们学院面临新机遇新挑战的起始年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">之语切入会议主题。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　会上，王院长这样诠释</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">新机遇</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">新挑战</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的概念，：国家对素质教育提出的新要求即为学院内涵建设的新机遇，而宁波市的强市战略则为学院人才培养的新挑战。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2011</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年新春，宁波市市委书记王辉忠明确提出的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">三思三创</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">（思进思变思发展，创业创新创一流）活动要求对高校教育改革也是一个重要指示，当前我院建设的历史性任务是成为一流的示范性高职院校、培养出更多的社会需求的高素质应用型人才，因此，人文学院作为学院的二级分院，承载着学生人文素养和技能提高的使命，理应清醒地、充分地认识到分院在十二五期间面临的挑战，按照</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">与时俱进、体现特色、服务地方</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的工作要求，分析形势，积极应对，调整思路，破解难题，走内涵发展之路，大力提高我院学生的创新、沟通、合作能力，加强学生的职业核心能力培养，目标是：全面提高学院的人才培养质量，提升学院的综合办学实力，为学校的发展作出更大的贡献。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　之后，王院长强调了新学期的两个工作重点：一是突出重点任务。二是抓好常规工作。落实重点任务主要从三方面着手：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">迎评促建</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">为抓手，推动各项工作上水平、出特色。在做好评建工作同时，要把说课作为一项重要的教研任务、教研活动来落实、安排，每位教师仍需强化项目化教学理念，从而提高教学质量。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．以贯彻落实宁波市</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">三思三创</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">活动为载体，以人文学院的院级示范建设项目和招标课题启动为契机，以毛大龙书记主持的人文素养培养计划为动力，深化教学改革、提高教学质量，推进人文学院在新时期的科学发展。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．以内涵建设为目标，加大师资队伍、科研平台和社会服务建设的力度，切实提高人才培养的质量。以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人才是第一资源</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的理念积极建设一支师德高尚、业务精干、结构合理、充满活力的高素质教师队伍，鼓励教师们申报各类科研项目，撰写高级别论文。启动普通话培训、企业职工培训、专升本培训、短期留学生汉语培训这四个培训项目，不断探索人才培养、社会服务的新模式，为进一步提升人文学院的文化辐射力作努力。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　而学院的常规工作需加强教学日常管理，尤其需要注重师德师风建设。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　王院长的讲话最后强调指出教师们要科学分配精力，处理好重点工作和常规工作之间的关系，做到重点工作见成效，常规工作抓扎实，更新观念、鼓足干劲、充满信心、积极向上，以全新的面貌迎接新学期的工作，抒写学院工作的新篇章。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　丁丽君书记在此次大会上的讲话，重点突出了</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">四个强化</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">（强化大局意识、强化责任意识、强化创新意识、强化服务意识）和两个</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">围绕</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">（围绕</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">三思三创</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">思路，围绕</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">迎评促建</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">工作），明确部署了新学期分院党建、思政及教学的具体工作，也对教师们提出期望：希望全体教职工进一步坚定思想、加强学习、团结协作、踏实工作，围绕自身工作找到</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">标杆</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">，开创新学期工作的新局面。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　总之，新学期工作会议的召开，鼓舞了全体教师的工作热情</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">,</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">我院教师将继续辛勤工作，团结奋进，确保开学工作的稳定、有序，使学院各项工作呈现新气象、新面貌，出现新变化、开创新进步。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '28', '人文乐园', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '新学期 新目标 新进步——人文学院召开新学期首次教职工大会', '1', null, '1');
INSERT INTO `ws_information` VALUES ('71', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">人文学院召开教学管理专题会议</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">03</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">02</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　为保质保量做好学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">迎评促建</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">工作，团结和带动全院师生关心、支持、投身于学院示范建设，</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日下午，人文学院院长王苹和书记丁丽君召集分院思政、英语、语文、数学、体育教研室主任和督导组组长李洪波老师一起进行了一次分院教学管理专题会议。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　会上，王苹院长首先就近期学院图书馆的图书采购任务作了工作部署：不仅要确保任务按时完成，同时，需专人专时专管完成图书采购以确保工作质量。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　在谈及新学期学院教学管理工作重点时，王院长重申工作务实的重要性，且再次强调需细化、量化教师的教学业绩考核工作和综合业绩评定工作，各教研室主任对习焉不察的教学管理老问题要敢于正视，既要做到以人为本，大局为先，也要坚持原则，严格按规章制度办事，规范操作，在职尽责，公正认真，在责任面前不推馁，是非面前不让步，这样，才能带领全体教师瞄准新高度，实现新跨越。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　而会议的另一项议程就是讨论人文学院于四月中下旬开展的说课比赛事宜，在倡议向优秀说课案例学习同时，王苹院长和丁丽君书记要求各教研室主任能及时广泛做好教师们的宣传动员工作，充分发挥教师们学习、研究、创新能力，从而扎实提高教师自身的教学素养及技能，也形成人文学院教师们相互切磋、取长补短，共同提高的良好氛围。并定于各教研室于</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月下旬上报说课的参赛教师名单，学院届时将组织相关专家进行评审，最终确定比赛结果并表彰。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　最后，王院长向辛勤工作在教学管理、督导一线的与会教师表示感谢，新学期里，希望分院工作的开展如同新春伊始宁波市委市政府</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">三思三创</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">活动关键词要求的那样做到</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">思</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">和</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">创</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">，每一位教师都应在工作中树立起</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">标杆</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">意识，从而心往一处想，劲往一处使，凝神聚气，形成合力，为学院的示范建设</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">创一流</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">做出自己的贡献。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '28', '人文乐园', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '人文学院召开教学管理专题会议', '1', null, '1');
INSERT INTO `ws_information` VALUES ('72', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">心系迎评促建，情系学院发展</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">——</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">人文学院召开大会落实阶段</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">迎评</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">”</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">工作</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">03</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">10</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">9</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日下午</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">15</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">点</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">30</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分，人文学院在四楼会议室召开大会。学院王苹院长、丁丽君书记出席会议，分院全体教师参加了会议，会议由丁丽君书记主持。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　会上，就目前正在落实的有关评估</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">数据采集</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">工作事宜，人文学院教科办主任张玉老师再次强调了数据填报内容的准确性要求，又重点罗列了以往数据采集中一些常见的误填内容并做了纠错示范，希望教师们能本着认真、负责的态度做好此项工作，高效有序地保障</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">迎评</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">工作扎实开展。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　接着，王苹院长把上星期二召开的分院教学管理专项会议精神向全体教师作了传达，对细分、量化教师业绩考核及综合教学考核工作的操作指标作了诠释，指出：对以往考核中的已明确化、易操作的指标不做更动，但对考核中的模糊项内容（如分院考核评分分值）将集合分院督导、教研室管理、分院教务办三方面的量化分值实施相关操作方案，教师个人的年度考核则从教师年度业绩、分院行政管理、分院主管领导班子三方面的综合分值给予老师们一个考核依据，做到既以人为本，大局为重，又有章可循，有据可依。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　对下一步院迎评工作领导小组的工作要求</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">——</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">在全院开展</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">说、谈、赛、展</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">活动的具体内容，王院长也在此次会议上作了相关部署，强调全面贯彻</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">以评促建，以评促改，以评促管，评建结合，重在建设</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的评估工作方针，希望全体人文分院教师能按照学院要求，顺利完成建示范、迎评估任务，切实落实好迎评前的学习、培训等准备工作，全面提升学院的人才培养质量和办学水平。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　会议最后，丁丽君书记就目前迎评工作中的校园安全问题作了指示，重申安全防范意识，强调各办公室电器安全使用问题，确保人走电关。在此基础上，学院也将组织人员对分院各个办公室、语音实验室进行安全盘查，把安全工作落实到分院教学科研等各项工作中去。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　总之，在下阶段各项工作中，全体教师只有</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">心系迎评促建，情系学院发展</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">，才能把迎评工作的责任明确、细化及分解到位。只有做到人人有责，事事落实，才能真正确保</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">迎评</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">工作顺利圆满地完成。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '28', '人文乐园', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '心系迎评促建，情系学院发展——人文学院召开大会落实阶段“迎评”工作 ', '1', null, '1');
INSERT INTO `ws_information` VALUES ('73', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">人文学院举行首轮说课选拔赛</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">03</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">23</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　为积极迎接我院示范建设中期检查和人才培养水平评估，深入贯彻学院提出的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">说、谈、赛、展</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">活动，促进教育教学质量的提高，</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">22</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日下午</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">13</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">点</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">30</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分，在学院领导的统一部署下，人文学院语文、数学教研室说课选拔赛首先拉开了分院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">说、谈、赛、展</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">活动的帷幕。教师们观摩了整个赛事，王苹院长、丁丽君书记进行了现场点评。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> <br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　人文学院启动说课比赛，从领导、督导，到各教研室主任及全体教师都做了大量的组织准备工作，为能更好促进教师间互相学习、交流、提高教学理念及技能，首轮说课选拔赛要求人文学院各个教研室每位教师选择自己任教的一门课程进行说课，说课时间严格控制在</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">10</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分钟。每位教师都围绕各自课程特点，按照教学目标确定、教学内容处理、教学环节设计、教学方法运用、教学效果评价等方面，充分发挥所学知识、技能，熟练地运用多媒体教学手段展示说课内容，通过精心设计教学过程的各个环节，注重学生主体作用的发挥，着眼于学生素质、能力的培养和提高，整个说课的设计都较好地体现了人文学院教师的生本教学理念。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　同时，人文学院的首轮说课选拔赛也为语文、数学教研室老师们提供了相互学习、相互交流的平台，促进了教师教学质量与教学水平的提高。而在教师说课比赛结束后，教师互评及分院院长、书记的现场点评都极大增强了说课比赛的实效性，诚如王苹院长最后小结的那样：说课说课，说的是理念，讲的是流程．对参赛教师们的评价重点主要落实在说课教师的教学理论素养，教学设计水平，兼顾说课形式。当然，学院举办此次说课选拔赛，也是为教师们提供展示自己的窗口和相互交流学习的平台，进一步促进教师对所任教课程的思考，达到教育教学改革深化的目的。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> <br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　据悉，人文学院的说课选拔赛在本月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">25</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">29</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日下午还将继续在英语教研室、思政教研室及体育教研室开展，届时也欢迎学院各位领导、专家、教师莅临现场指导。我们同样期待着人文学院的老师们能再接再厉，借这样说课比赛的平台亮出各自的施教风采。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<img alt=\"\" src=\"/attached/image/20120529/20120529155201_499.png\" width=\"500\" height=\"373\" /> \r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '28', '人文乐园', '3', '2012-05-29 15:55:18', null, null, '2', null, null, '人文学院举行首轮说课选拔赛', '2', '/resourcefiles/thumbnail/a222768c3767cf28a3f8fbe9acaa567b.png', '1');
INSERT INTO `ws_information` VALUES ('74', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:420pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"560\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">2012</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">年上半年学生第四期普通话培训测试</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">来源：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">2012-02-29 11:03 </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">各分院：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　为了提高纺织服装学院学生的普通话水平，帮助学生顺利通过国家普通话水平测试等级考试，获得相应的资格证书，浙江纺织服装学院普通话水平测试培训点第四期培训测试开始报名，具体通知如下：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　一、测试</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1.</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">测试对象</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　所有想要报名参加测试、获得证书的学生。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2.</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">测试内容、形式</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　普通话水平测试采用口试的形式进行，内容包括四项：（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">）读</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">100</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">个单音节字；（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">）读</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">50</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">个多音节词；（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">）朗读一篇课文；（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">）说</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分钟话。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．测试地点、时间</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　地点：宁波大学</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　时间：初步定于</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2012</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">21</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">22</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日，具体时间以准考证上公布的为准</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3.</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">合格标准</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　师范类专业的合格标准为二级甲等（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">87</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分）及以上，其他专业的合格标准为二级乙等（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">80</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分）及以上。测试成绩在</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">70</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分以上的考生，可以获得三级甲等普通话水平测试等级证书。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4.</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">收费标准</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　根据省物价局、省财政厅浙价费〔</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2001</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">〕</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">74</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">号文件《关于调整普通话水平测试收费标准的复函》规定，学生测试费</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">30</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">元</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">/</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人次。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　二、培训（凡报名参加测试者均需参加培训）</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．培训满</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">30</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人开班；</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．培训共</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">24</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">个课时，每天</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">8</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">课时，分别为</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">24</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">31</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">7</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日，具体时间为：上午</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">8</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">30——11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">30</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">，下午</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">13</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">00——16</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">00</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">；</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．培训地点在浙江纺织服装学院教学楼，具体教室另行通知；</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．培训费：根据宁波市物价局甬价费〔</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2004</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">〕</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">246</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">号《关于核定普通话培训收费标准的复函》规定，普通话培训收费标准为每人每期</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">100</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">元，培训</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">24</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">课时，教材请自行准备。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　三、报名办法</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　报名须以分院为单位，采用集体报名的方式。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1.</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">测试、培训报名要求</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　分院按以下结构建立一个</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">EXCEL</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">表：序号、学号、姓名、性别、身份证号码、班级、所在学院，以班级为单位按学号顺序输入计算机，然后输出考生名单审核并进行公示（见附件），而后将报名表电子稿和打印稿各一份交到人文学院张玉老师处。（电子稿发送到邮箱：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><a href=\"mailto:yzhang208@zjff,net\"><span style=\"color:#575644;text-decoration:none;\">yzhang208@zjff,net</span></a></span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">）</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">．由于普通话水平测试等级证书不得随意更改数据，请各单位报名时对上报信息务必认真校对，以免发生错报及漏报现象，对于因字库不全而无法打印的姓名请在上报的考生名单上予以注明。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　四、学生报名日期</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2012</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日至</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">22</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　五、参考资料</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　《普通话学习与测试教程》（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2005</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月出版，黑龙江人民出版社）或《普通话培训测试指南》（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2004</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">9</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月第二版，浙江大学出版社）。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　注：本次报名不收照片，参加测试时由考生自己上交，要求：近期免冠一寸证件照一张（大头贴以及盖过钢印的照片均无效），并在照片后面写上学号、学院、姓名。参加测试时须带考试证（校园卡）、准考证和一寸证件照片，无证者不予参加测试。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> <br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">二</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">○</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">一二年二月二十九日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> &nbsp;<br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">附件下载</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><a href=\"http://www.zjff.net/uploads/notice/file/2012_02/29/110345.xls\" target=\"_blank\"><span style=\"font-family:宋体;color:#575644;text-decoration:none;\"><span>浙江纺织服装学院学生普通话培训报名表</span></span><span style=\"color:#575644;text-decoration:none;\">(</span><span style=\"font-family:宋体;color:#575644;text-decoration:none;\"><span>空白）</span></span><span style=\"color:#575644;text-decoration:none;\">.xls</span></a></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">阅读次数：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">55</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '66', '特色培训', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '2012年上半年学生第四期普通话培训测试', '1', null, '1');
INSERT INTO `ws_information` VALUES ('75', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">第四届红帮文化演讲大赛</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\"><br />\r\n——</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">暨学院首届大学生文明礼仪大赛圆满落幕</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">12</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">01</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　为继续深入继承和发扬百年红帮精神，做大做强校园文化品牌，激励我院师生学习发扬红帮精神，鼓励学生秉持红帮精神积极应对挑战，并创造文明校园，营造良好的学习生活环境，提高大学生文明素质，促进大学生养成文明行为、增长礼仪知识、增强礼仪意识、遵守礼仪规范，</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">29</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日晚</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">18</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">点</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">15</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分，在</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">号楼</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">楼</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">T</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">台举行了第四届红帮精神演讲暨第一届大学生文明礼仪大赛。大赛邀请了学院学生处处长胡斌，人文学院院长王苹，党总支书记丁丽君、人文学院公关老师王冬梅、人文学院大学语文老师吴颖担当评委。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　此次比赛由总院团委主办，人文学院及创新教育实验班承办，现场有微博直播。来自纺织、服装、商学院、易斯戴、轨道机电、艺术、信息</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">7</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">个学院的参赛团队参加了本次比赛。大赛共有三大环节：一是演讲环节，即要求参赛队选派一位队员以</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">传承红帮精神，争做红帮新人</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">为主题作限时三分钟的演讲。二是限时问答环节，即要求每个参赛组用</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">90</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">秒时间作文明礼仪知识问答。三是情景模拟表演环节，即每个团队用</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">5</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分钟时间以情景模拟表演诠释不同选题的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">文明礼仪</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">内容。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　比赛中，选手们在展示红帮精神、传承红帮文化、发扬红帮文化的演讲中纷纷展现了对我院校园文化的思考和感悟。在限时问答的环节中，各个参赛团队更是争分夺秒，问答题几乎都以不假思索、对答如流的方式给大家留下了深刻的印象。在高潮迭起的情景模拟环节中，各参赛队又纷纷以编排形式新颖、内容务实、各具创意的表演赢得了现场观众的热烈掌声和感同身受的欢呼声。其中，由金碧聪、方婷婷、陈芸、陈静四位同学组成的易斯戴团队在这一环节的表演中更是得到了评委和现场观众的一致肯定。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　当晚现场还穿插的一段街舞表演和</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">十佳歌手</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">冠军</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">SB</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">组合成员带来的歌曲《男人的好》，既带动了全场的气氛，也为紧张的比赛增加了几份新鲜感。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　随后，人文学院院长王苹上台发言，她首先肯定了全体参赛选手的精彩表现，并高度赞扬了机电轨道学院的城轨通信</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">班</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">26</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">位</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">创新教育班</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">学生在承办本次比赛中的努力和付出。她表示这次大赛能如期成功举办，与创新班全体同学的积极主动的工作态度分不开，与他们全员动员、群策群力、分工明确、各司其职、各尽其责的工作安排分不开，也与他们积极协作、并肩作战的团结作风分不开。而更有意义的是，创新班全体同学在整个比赛的承办中，充分汲取了《与人沟通》《与人合作》等理论知识学习精华，展示和发挥了自身的特长和优势，提高了解决问题的能力，历练了职业核心能力，为未来进入职场储备了能量。最后，王苹院长号召大家积极践行红帮文化精神，做一个文明的人，道德的人，有情操的人。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　而最终的比赛成绩为：服装学院、商学院、艺术学院荣获优秀奖；机电学院和信息学院荣获三等奖；易斯戴学院荣获二等奖；大赛一等奖由纺织学院摘得。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　可以说，每一场比赛其实就是一个学习的过程，作为红帮文化的继承人，我院学子更应该取其精华，去其糟粕，开拓创新，为红帮文化注入新的活力，将红帮精神发扬光大。而对于文明礼仪，当代大学生更应该秉持红帮精神积极应对挑战，为创造文明校园，营造良好的学习生活环境而努力，努力提高自身文明素质，养成文明行为、增长礼仪见识、增强礼仪意识、遵守礼仪规范。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529160011_352.png\" width=\"184\" height=\"246\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">比赛现场</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529160045_155.png\" width=\"500\" height=\"375\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">获奖选手和评委合影</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529160115_664.png\" width=\"500\" height=\"374\" />\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">礼仪知识问答</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529160144_194.png\" width=\"500\" height=\"375\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">情景模拟</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<img alt=\"\" src=\"/attached/image/20120529/20120529160215_164.png\" width=\"500\" height=\"375\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">演讲</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：创新班</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">孙丹燕</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">学生记者团</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">朱蓉翠</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">阅读次数：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">345 </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '66', '特色培训', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '第四届红帮文化演讲大赛暨学院首届大学生文明礼仪大赛圆满落幕', '2', '/resourcefiles/thumbnail/2061b5d92e3b55c58223457b781a29a9.png', '1');
INSERT INTO `ws_information` VALUES ('76', '', '<p style=\"text-align:center;\">\r\n	<a href=\"/attached/file/20120529/20120529155639_94.xls\" target=\"_blank\">数学教研室教学成果.xls</a> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529160058_833.jpg\" width=\"700\" height=\"60\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529160122_619.jpg\" width=\"700\" height=\"112\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529160146_779.jpg\" width=\"700\" height=\"224\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529160211_936.jpg\" width=\"700\" height=\"193\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529160237_342.jpg\" width=\"700\" height=\"212\" /> \r\n</p>', '', '2012-05-29', 'admin', '管理员', '', '13', '52', '教学成果', '3', '2012-05-29 16:03:19', '', '', '2', '', null, '数学教研室教学成果', '2', '/resourcefiles/thumbnail/fcc09aed4672b3dc11a477bde7a23997.jpg', '1');
INSERT INTO `ws_information` VALUES ('77', null, '&nbsp;\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span><span style=\"font-family:宋体;font-size:14pt;\">人文学院为韩国学生量身定制汉语教学课程</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';font-size:14pt;\"></span></span>\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span><span style=\"font-family:宋体;color:windowtext;font-size:10pt;\">上传：人文学院</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';color:windowtext;font-size:10pt;\"> </span></span><span><span style=\"font-family:宋体;color:windowtext;font-size:10pt;\">郑晓</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';color:windowtext;font-size:10pt;\">&nbsp;&nbsp;</span></span><span><span style=\"font-family:宋体;color:windowtext;font-size:10pt;\">时间：</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';color:windowtext;font-size:10pt;\">11</span></span><span><span style=\"font-family:宋体;color:windowtext;font-size:10pt;\">年</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';color:windowtext;font-size:10pt;\">12</span></span><span><span style=\"font-family:宋体;color:windowtext;font-size:10pt;\">月</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';color:windowtext;font-size:10pt;\">30</span></span><span><span style=\"font-family:宋体;color:windowtext;font-size:10pt;\">日</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';color:windowtext;font-size:10pt;\"></span></span>\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span><span style=\"font-family:\'ˎ̥\',\'serif\';color:windowtext;font-size:10pt;\"></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:24pt;\" align=\"left\">\r\n	<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">12</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">29</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">日上午</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">10</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">点，</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">20</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">个从外型看与中国学生区别不大的韩国学生准时出现在学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">号楼</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">楼服饰陈列搭配室，从今天起，这批来自韩国大邱工业大学的学生将在这里开展为期两周的汉语及中国文化体验学习活动。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">　　为能让韩国学生在短期内学习好《实用汉语》并感知中国文化，人文学院自承接了这项</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">负责韩国学生的汉语教学与文化讲座的师资、教材、教学时间安排</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">的工作任务后，首先由院长王苹、书记丁丽君牵头策划了韩国学生学习体验活动的可行性方案，接着，根据既定方案内容逐一落实师资和教学时间、地点的安排；为保证良好的教学质量，提高韩国学生学习汉语、了解中国文化的兴趣，人文学院又多方验证韩国学生求学兴趣点，并及时修补培训方案；在此基础上，又积极与培训教师们沟通交流，充分了解、掌握教师们的教学设想，争取以最完满的教学形式和教学内容给予韩国学生更多的汉语学习乐趣和中国文化感受，以此达到培训目的。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">　　值得一提的是，为保证教学效果，在人文学院确定相关师资后，在与教师们作一一沟通时，教师们都能从学校发展的大局出发，没有一个因为任务的特殊性而畏难或推馁的，即使是在得知需要利用元旦假期来给韩国学生授课或开讲座的，他们也都是一口承应，绝无二话。因此，在教师们大力支持和配合下，人文学院量身定制的韩国留学生短期培训</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">菜单</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">顺利生成了：这里有七个上午的《实用汉语》教学安排，由人文学院选派具有对外汉语教学资格和教学经验的罗佳炜、罗技科、陆晓楠三位老师给学生授课，教师们在通过集体备课、自编讲义的教学准备后，设置了见面介绍、问路、打电话、在机场、在商场、在饭店七个实用场景给学生讲练汉语读音和基本句型；而六个下午的讲座内容则分别是周震豪老师的《中国武术文化》、葛晓鸿老师的《中国国画艺术》、羊元高老师的《中国书法艺术》、王国海老师的《中国风景名胜摄影》、周俊飞老师的《中国服装工艺》及陈锡林老师的《中国民乐表演》。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">　　据随行的韩国大邱工业大学国际交流处金明松教授介绍，这</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">20</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">个学生都是该校的大一学生，因出于对中国文化生活的兴趣，自愿报名参加本次游学活动。他们基本上是第一次踏上中国的国土，且绝大多数学生汉语的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">读写听说</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">状态尚处于初级阶段，因此是无法准确、流畅地与中国人沟通交流的，但这些障碍都无损于他们学习汉语、了解中国文化的热情。金教授希望通过我院精心定制和安排的一系列学习和活动内容，能进一步加深韩国学生对汉语和中国文化的印象，促进</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">他们自觉参与汉语推广工作，从而更大范围、更深层次地推进中韩两校间的合作与交流。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> <span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:20pt;\" align=\"left\">\r\n	<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529160357_643.png\" width=\"500\" height=\"375\" /><span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">罗技科老师在上汉语课</span>\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span><img alt=\"\" src=\"/attached/image/20120529/20120529160431_728.png\" width=\"500\" height=\"375\" />\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	周震豪老师在教授太极拳\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span>&nbsp;\r\n</p>\r\n<p style=\"text-align:left;\" align=\"center\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"center\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\"></span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">阅读次数：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">284</span>\r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '66', '特色培训', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '人文学院为韩国学生量身定制汉语教学课程', '2', '/resourcefiles/thumbnail/1c467731a4747ac476ba326a72bf30de.png', '1');
INSERT INTO `ws_information` VALUES ('78', null, '&nbsp;\r\n<p>\r\n	<span></span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">人文学院召开</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">专升本</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">”</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">管理工作专题会议</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">12</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">02</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">21</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">15</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日上午，在人文分院会议室，丁丽君书记召开了新学期</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">” </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">管理工作专题会议。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">三个班级的班主任、学生导师及负责</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">学生日常管理工作的胡裕波老师参加了会议。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　会上，胡裕波老师首先汇报了学生报到的总体情况，并就未报到学生情况作了说明。据了解，即使在假期里，面对学生</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">QQ</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">上或电话里的培训信息咨询，胡裕波和三位班主任也已经做了大量的沟通工作。开学伊始，他们又在核实学生报到情况的基础上，进一步与规定时间内未报到的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">学生一一进行电话交流，了解落实相关情况。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　同时，胡裕波老师在本次会议上还汇报了易斯戴学生和新参加专升本培训学生的寝室整体安排和入住情况及一村学生寝室搬迁的进度。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　接着，三位班主任、学生导师也逐一交流了班级学生的学习、管理情况。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　最后，丁丽君书记对新学期</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">” </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">管理工作做了进一步部署：一是落实班级常规管理工作，包括继续做好专升本三个</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">QQ</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">群的管理，采集最新考试政策、信息，为考生服务并及时做好学生咨询的回复；二是课程辅导教师继续兼任寝室结对工作，一方面通过课堂内外、网络、下寝室等方式，多方位指导帮助学生，同时，加强与学生的沟通，了解学生学习状态，做好学生思想动态的了解疏导，为学生排忧解难；三是通过建设</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">平安校园</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的主题班会形式，继续强化学生安全意识，尤其是租住在外，边工作边复习的学生；本学期继续做好学生走访工作。四是重视学生因寝室调整、搬迁产生的各类思想问题和人际纠纷问题，落实防范措施；五是重视学风、班风建设。以成绩优秀、积极备考的同学为榜样引领，谈心得体会，交流错题难题，营造优良的学习争优氛围，激励学生积极备考、查漏补缺、实现考试目标。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：胡裕波</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">阅读次数：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">94 </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '66', '特色培训', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '人文学院召开“专升本”管理工作专题会议', '1', null, '1');
INSERT INTO `ws_information` VALUES ('79', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">我院获批成为国家职业核心能力培训认证考评点</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">09</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">09</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　为贯彻落实人力资源和社会保障部职业技能鉴定中心《劳社鉴函（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2009</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">）</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">32</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">号》文件指示精神，满足社会及用人单位对职业核心能力培养和评价的要求，增强学生的就业能力，国家职业核心能力培训认证全国推广中心在全国开展职业核心能力认证测评工作。经过国家人力资源和社会保障部的审核，浙江纺织服装职业技术学院已经符合建立职业核心能力考评点的要求，</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">8</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">25</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日，国家职业核心能力培训认证全国推广中心正式发出《职培（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2011</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">）第</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">13</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">号文件》通知，同意浙江纺织服装职业技术学院正式成为国家职业核心能力考评点，编号为：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">12026002</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　据悉，为提高职业教育服务社会、服务地方经济、服务学生就业的能力，自</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2008</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年始，我院就选派教师参加全国职业核心能力师资培训暨测评师认证培训，经过培训考核，目前已有王苹、王冬梅、吴颖、崔玉环等教师获得了国家职业核心能力培训师和测评师资格证书。为了进一步加强与国家有关部门的合作，深入开展国家职业核心能力的培训、测评和推广工作，把国家职业核心能力培训和认证工作落到实处，自</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2011</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月始，人文学院就与机电轨道学院联手创办</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">创新教育实验班</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">，就学生的沟通能力、合作能力、解决问题能力进行针对性教学与培训，而随着我院获批成为国家职业核心能力培训认证考评点后，首批</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">26</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">位来自城轨通信</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">、</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">班的学生将作为此考点的第一批考生参加国家职业核心能力测评考试，考评合格的学员也将获得了证明自己职业核心能力水平的《职业核心能力水平等级证书》（国家职业资格证书）。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　此次，国家职业核心能力培训认证考评点在我院的设立将大大激发我院开设职业核心能力课程的热情。自新学年始，已着力开展学生职业核心能力培养的人文学院计划将在全院范围内进一步推进落实职业核心能力示范培训认证工作，为提升我院学生核心竞争力创造更好的条件。　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><img alt=\"\" src=\"/attached/image/20120529/20120529160742_167.png\" width=\"500\" height=\"346\" /></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">创新班《与人合作》实践教学课</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n<img alt=\"\" src=\"/attached/image/20120529/20120529160802_792.png\" width=\"500\" height=\"320\" />\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">阅读次数：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">544 </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '66', '特色培训', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '我院获批成为国家职业核心能力培训认证考评点 ', '2', '/resourcefiles/thumbnail/7fbd5b9e654d118fc423e1393c17de4f.png', '1');
INSERT INTO `ws_information` VALUES ('80', null, '&nbsp;\r\n<p>\r\n	<span></span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"width:412.5pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"550\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">院</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">专升本</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">”</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">教学培训进入</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">“</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">攻坚期</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">” </span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">上传：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">12</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">02</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">16</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">日</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　随着</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">20</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日《浙江省</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2012</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年专升本各类别分校分专业招生计划》的公布，</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">距离</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2012</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月中旬的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">考试只剩下仅仅两个月的备考时间了。而为了积极稳妥地推进学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">培训工作，确保已进入培训</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">攻坚时期</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的各项工作能顺利完成，达到培训的预期目标，人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">教学培训工作即使在寒假里都没有真正停歇过，建立起的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">班级</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">QQ</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">群是人文学院领导、培训班班主任、任课教师及</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">学生最便捷的交流及学习平台。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　新学期一开学，为彻底解决住宿在易斯戴的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">学生因交通不便而带来的学习问题，学院安排这些学生到本部的学生宿舍住宿，以便让学生安心备考。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">15</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日，本学期</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> “</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">培训班的教学辅导课正式开课。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　据悉，为了坚定这批学生备考信心、攻坚克难、积极应战，人文学院领导班子在本学期的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">考试课程排课中，充分听取了所在班级的班主任、任课教师及学生的意见，并根据学生上学期期末各科考试成绩，特别加强了文科班语文课和理科班数学课的培训辅导力度，在每周</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">课时的教学课基础上又分别增加了</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">课时的强化训练课，并且要求任课教师在课余时间尽心尽责帮助学生答疑解惑，以此增强学生的备考自信，巩固知识，查漏补缺。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　可以说，为使这些参加</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">培训的学生能顺利通过人生的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">第二次高考</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">，学院方面是做了很多的努力和具体的工作，但随着考试日期的临近，一些学生因找到理想工作而选择中途而退及个别未能取得考试资格认证的学生（英语三级未过），使得</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">攻坚期</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">的</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">学生的备考心理辅导工作也提上了日程。因此，人文学院也相应地对三个班主任及</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">学生导师提出了工作要求，希望能更好地推进</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">现阶段工作，帮助在校参加</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">培训学习的学生坚定信念、排除干扰、集中精力、积极备考。当然，在各方努力下，目前已有十余位因各种原因错过了上学期学院培训班辅导课程学习的毕业班学生，在本学期开学之初，即到人文学院办公室胡裕波老师处咨询并联系入学的相关事宜，为圆自己的一个</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">本科</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">梦而努力。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　常言道：风物长宜放眼量。相信，在学院师生的同心同德、共同拼搏下，这些已参加学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">培训学习的近百名学生，必将安然度过</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">攻坚期</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">，实现自己的人生理想。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><img alt=\"\" src=\"/attached/image/20120529/20120529160923_624.png\" width=\"500\" height=\"375\" /></span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><img alt=\"\" src=\"/attached/image/20120529/20120529160934_317.png\" width=\"500\" height=\"375\" /><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"><br />\r\n<br />\r\n</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '66', '特色培训', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '院“专升本”教学培训进入“攻坚期”  ', '2', '/resourcefiles/thumbnail/9cd54b2e834ea7f4ea2558ba37f78866.png', '1');
INSERT INTO `ws_information` VALUES ('81', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:420pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"560\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">普通话培训班上课通知</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">来源：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">2011-03-24 13:45 </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　已报名参加普通话培训的同学请注意，本周六（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">26</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日）开始上课。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　地点：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">14</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">号楼</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">310</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　时间：早上</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">8:30</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">开始。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '23', '24', '通知公告', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '普通话培训班上课通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('82', null, '&nbsp;\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">关于召开学院进行专升本工作会议的通知</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\"></span></b>\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">来源：招生就业处</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">2011-05-26 13:51</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:20pt;\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">今天（</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">5</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">26</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日）下午</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">15</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">30</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">分在行政楼四楼会议室召开学院专升本工作的会议，望各分院书记与具体负责老师准时参加。具体参会人员名单如下：艺术学院：张苗</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">商学院：傅巧仙</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">&nbsp; </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">国际学院：袁勇</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　服装学院：林坚</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">毛艺瑾</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">&nbsp; </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">信息学院：梅惠平</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">张俐</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">&nbsp; </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人文学院：王苹</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">&nbsp; </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">易斯戴学院：张文杰</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">&nbsp; </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">机电与轨道学院：彭泽民</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">&nbsp; </span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">纺织学院：胡杭杰</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '2', '24', '通知公告', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '关于召开学院进行专升本工作会议的通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('83', null, '&nbsp;\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span><span style=\"font-family:宋体;font-size:12pt;\">关于</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';font-size:12pt;\">09</span></span><span><span style=\"font-family:宋体;font-size:12pt;\">级学生</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';font-size:12pt;\">“</span></span><span><span style=\"font-family:宋体;font-size:12pt;\">专升本</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';font-size:12pt;\">”</span></span><span><span style=\"font-family:宋体;font-size:12pt;\">考试宣传工作通知</span></span>\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span><span style=\"font-family:宋体;font-size:10pt;\">来源：招生就业处</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';font-size:10pt;\">&nbsp;&nbsp;</span></span><span><span style=\"font-family:宋体;font-size:10pt;\">时间：</span></span><span><span style=\"font-family:\'ˎ̥\',\'serif\';font-size:10pt;\">2011-05-31 09:22</span></span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">各二级学院：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　请各二级学院做好</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">09</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">级学生</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">“</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">专升本</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">”</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">考试的报名宣传工作，于</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">6</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">月</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">11</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">日前做好调查统计工作，下载附件</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">和附件</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">填表汇总。有关报名政策及</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2011</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年专升本本科院校招收信息参见附件</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">和附件</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　附件</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：专升本意向调查表</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　附件</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：专升本意向统计表</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　附件</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：报考政策</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　附件</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2011</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年专升本本科院校分校分专业招收计划</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">附件下载</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><a href=\"http://www.zjff.net/uploads/notice/file/2011_05/31/092204.doc\" target=\"_blank\"><span style=\"font-family:宋体;color:#575644;text-decoration:none;\"><span>附件</span></span><span style=\"color:#575644;text-decoration:none;\">1.doc</span></a></span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">附件下载</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><a href=\"http://www.zjff.net/uploads/notice/file/2011_05/31/092207.xls\" target=\"_blank\"><span style=\"font-family:宋体;color:#575644;text-decoration:none;\"><span>附件</span></span><span style=\"color:#575644;text-decoration:none;\">2.xls</span></a></span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">附件下载</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><a href=\"http://www.zjff.net/uploads/notice/file/2011_05/31/092210.doc\" target=\"_blank\"><span style=\"font-family:宋体;color:#575644;text-decoration:none;\"><span>附件</span></span><span style=\"color:#575644;text-decoration:none;\">3.doc</span></a></span>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">附件下载</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><a href=\"http://www.zjff.net/uploads/notice/file/2011_05/31/092213.doc\" target=\"_blank\"><span style=\"font-family:宋体;color:#575644;text-decoration:none;\"><span>附件</span></span><span style=\"color:#575644;text-decoration:none;\">4.doc</span></a></span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '1', '24', '通知公告', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '关于09级学生“专升本”考试宣传工作通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('84', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:420pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"560\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">关于</span></b><b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:12pt;\">2011</span></b><b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">年学院专升本基本情况的通报</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">来源：招生就业处</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">2011-06-30 11:09</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">（提示：根据发布者的要求，本信息为内部信息，请不要随意传播。）</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\"> </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">2011</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">年专升本录取工作已基本告一段落，截止今日我院共有</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">116</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">名学生报考专升本考试，其中</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">28</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">名优秀学生生在专升本考试中取得优异成绩，录取到本科院校。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　报考的学校分别有中国计量学院，宁波大学，宁波工程学院，绍兴文理学院，温州大学，浙江财经学院，浙江传媒学院，浙江大学宁波理工学院，浙江工商大学，浙江工业大学，浙江海洋学院，浙江科技学院，浙江理工大学，浙江农林大学，浙江师范大学，浙江树人大学，浙江万里学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">17</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">所本科院校，共有</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">21</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">个专业。其中报名英语专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">18</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人，报考工商管理专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">7</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人，报考行政管理专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">0</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人，报考电气工程及自动化专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人，报考计算机科学与技术专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人，报考音乐学（师范）专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人，报考美术学（师范）专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">0</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人，报考国际经济与贸易专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">9</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人，报考市场营销专业</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">5</span><span style=\"font-family:宋体;color:#333333;font-size:10pt;\">人录取</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\">1</span><span st