INSERT INTO `ws_information` VALUES ('86', null, '&nbsp;\r\n<div align=\"center\">\r\n	<table style=\"width:420pt;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"560\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<b><span style=\"font-family:宋体;color:#333333;font-size:12pt;\">专升本辅导课开课通知</span></b><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">来源：人文学院</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">&nbsp;&nbsp;</span><span style=\"font-family:宋体;color:#666666;font-size:10pt;\">时间：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">2011-10-10 10:04 </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">　　专升本各门课程的辅导于这周（第七周）开始，请各门课程的相关老师按已确定的时间和地点上课。附专升本各专业课表。</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"> </span>\r\n					</p>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:宋体;color:#333333;font-size:10pt;\">附件下载</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"><a href=\"http://www.zjff.net/uploads/notice/file/2011_10/10/100451.rar\" target=\"_blank\"><span style=\"font-family:宋体;color:#575644;text-decoration:none;\"><span>升本课表安排</span></span><span style=\"color:#575644;text-decoration:none;\">.rar</span></a></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:left;\" align=\"left\">\r\n						<span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-family:宋体;color:#666666;font-size:10pt;\">阅读次数：</span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#666666;font-size:10pt;\">276 </span><span style=\"font-family:\'ˎ̥\',\'serif\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '24', '通知公告', '3', '2012-05-29 16:15:59', null, null, '2', null, null, '专升本辅导课开课通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('87', null, '&nbsp;\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<b>人文分院简介<span> </span></b>\r\n</p>\r\n<p style=\"text-align:justify;text-indent:26.55pt;\">\r\n	<b>人文学院成立于<span> 2005 </span>年<span> 6 </span>月，是浙江纺织服装职业技术学院下属分院之一，负责全院公共课的教学管理工作。下设语文、英语、思政、体育、数学<span> 5 </span>个教研室及校红帮文化研究所。 人文学院师资力量雄厚，拥有<span> 69 </span>名教师，其中高级职称<span> 28 </span>人，中级职称<span> 35 </span>人。近年来，学院共获得省级以上课题<span> 10 </span>余项，市级课题<span> 30 </span>余项，发表论文<span> 400 </span>余篇，多名教师在各类教学技能竞赛中获得多项奖励。<span> </span></b>\r\n</p>\r\n<p style=\"text-align:justify;text-indent:27pt;\">\r\n	<b>人文学院秉承<span> “</span>传道授业、厚积薄发<span>” </span>的传统，坚持以学科建设为龙头，以教学质量为生命，以学术研究为支撑，以培养人才为目标的指导思想，努力实践<span>“ </span>修德长技、求真尚美<span>”</span>之校训，致力于铸人文素养之魂，煅明辨慎思之质，培养崇德厚道、明体达用之高素质强技能应用型人才。 <span></span></b>\r\n</p>\r\n<p style=\"text-align:justify;text-indent:27pt;\">\r\n	<b>人文学院欢迎您的到来！<span> </span></b>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '3', '10', '人文分院简介', '2', '2012-06-01 22:18:02', null, null, '2', null, null, '人文分院简介', '1', null, '1');
INSERT INTO `ws_information` VALUES ('88', null, '&nbsp;\r\n<p>\r\n	<b><span style=\"font-family:宋体;font-size:14pt;\">党政领导</span></b><b><span style=\"font-size:14pt;\"></span></b>\r\n</p>\r\n<p>\r\n	党总支书记、副院长：丁丽君副教授<span> </span>\r\n</p>\r\n<p>\r\n	办公室：<span>10 </span>号楼<span> 402 </span>室<span> </span>\r\n</p>\r\n<p>\r\n	电 话：<span> 86329658</span>\r\n</p>\r\n<p>\r\n	职 责：全面负责学院工作，主持党务工作，分管财务、人事\r\n</p>\r\n<img alt=\"\" src=\"/attached/image/20120529/20120529162139_721.png\" width=\"265\" height=\"174\" /> \r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">副院长（享受正处待遇）：余国良教授</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	办公室：<span>10 </span>号楼<span> 401 </span>室<span> </span>\r\n</p>\r\n<p>\r\n	电 话：<span> 86329277</span>\r\n</p>\r\n<p>\r\n	职 责：协助书记做好分院管理工作，分管教学科研\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '1', '11', '党政领导', '2', '2012-06-01 22:18:02', null, null, '2', null, null, '党政领导', '2', '/resourcefiles/thumbnail/b774a3d23d6cec11b5df10b4a1508f50.png', '1');
INSERT INTO `ws_information` VALUES ('89', null, '&nbsp;\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">机构设置<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;font-size:12pt;\">一、行政机构</span></b><b><span style=\"font-size:12pt;\"></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">书记兼副院长：丁丽君</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">副</span><span><span>&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">院</span><span><span>&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">长：</span> <span style=\"font-family:宋体;\">余国良</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">办公室：</span><span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">王锡金</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">教务办：</span><span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">张玉</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\">实训中心：胡裕波、陈宝珍</span>\r\n</p>\r\n<p>\r\n	<span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;font-size:12pt;\">二、教学机构</span></b><b><span style=\"font-size:12pt;\"></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■</span><span style=\"font-family:宋体;font-size:12pt;\">语文教研室</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主任</span><span style=\"font-size:12pt;\">: </span><span style=\"font-family:宋体;font-size:12pt;\">周震豪</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">成员：张艺、孙奕、林莹、张宏儿、吴颖、许维维</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">■</span><span style=\"font-family:宋体;font-size:12pt;\">思政教研室</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:12pt;\">主任：王苏鹤</span><span style=\"font-size:12pt;\"></span>\r\n</p>\r\n<