INSERT INTO `ws_information` VALUES ('142', '', '<div>\r\n	<p>\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;font-weight:bold;\"> \r\n		<div>\r\n			<p>\r\n				<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;font-weight:bold;\">关于各分院组织<span>2011</span><span>年专升本报名的通知</span></span><span style=\"font-family:\'宋体\';color:#333333;font-size:12pt;font-weight:bold;\"></span>\r\n			</p>\r\n			<p>\r\n				<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">来源：招生就业处<span>&nbsp;&nbsp;</span><span>时间：</span><span>2011-02-28&nbsp;16:30</span></span><span style=\"font-family:\'宋体\';color:#666666;font-size:10pt;\"></span>\r\n			</p>\r\n			<p style=\"text-align:left;\">\r\n				<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">我院报名工作流程：</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n			</p>\r\n			<table style=\"border-collapse:collapse;\">\r\n				<tbody>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"23\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">序&nbsp;号</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"68\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">日&nbsp;期&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"394\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">工&nbsp;作&nbsp;内&nbsp;容&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"69\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">负&nbsp;责&nbsp;单&nbsp;位&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"23\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">1</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" rowspan=\"2\" width=\"68\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">2011<span>年</span><span>2</span><span>月&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"394\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">通知辅导员及各班班主任，组织做好学生报名、填表，考生资格初审（具体工作详见附件二）、收取报名费等工作<span>(</span><span>具体时间各二级学院可自行安排</span><span>)</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"69\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">各二级学院&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"23\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">2</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"394\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">学院网，招生网站发布专升本相关信息&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"69\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">招生就业处&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"23\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">3</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"68\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">2011<span>年</span><span>3</span><span>月</span><span>20</span><span>－</span><span>24</span><span>日&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"394\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">组织考生登录浙江教育考试网（<span>www.zjzs.net</span><span>）进行网上报名&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"69\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">各二级学院&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"23\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">4</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"68\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">2011<span>年</span><span>3</span><span>月</span><span>26</span><span>－</span><span>28</span><span>日&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"394\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">完成报名信息网上输入的学生进行报名信息现场确认&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"69\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">各二级学院&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"23\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">5</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"68\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">2011<span>年</span><span>3</span><span>月</span><span>28</span><span>－</span><span>29</span><span>日&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"394\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">汇总参加专升本考试现场确认后学生材料、考生花名册至招生就业处&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"69\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">各二级学院&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" rowspan=\"2\" width=\"23\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">6</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" rowspan=\"2\" width=\"68\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">2011<span>年</span><span>4</span><span>月</span><span>16</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" rowspan=\"2\" width=\"394\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">通知考生到报考学校领取准考证和熟悉考场&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"69\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">各二级学院&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt outset;border-top:#000000 0.75pt outset;border-right:#000000 0.75pt outset;\" valign=\"middle\" width=\"69\">\r\n							<p style=\"text-align:left;\">\r\n								<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">招生就业处&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td style=\"border-bottom:#000000 0.75pt outset;border-left:#000000 0.75pt out