INSERT INTO `ws_information` VALUES ('189', null, '<div style=\"text-align:center;\">\r\n	<a href=\"/attached/file/20120530/20120530160524_113.doc\" target=\"_blank\">“专升本”学生规范管理办法.doc</a>\r\n</div>', null, '2012-05-30', 'admin', '管理员', null, '2', '46', '专升本培训规定', '3', '2012-05-30 16:07:24', null, null, '2', null, null, '“专升本”学生规范管理办法', '1', null, '1');
INSERT INTO `ws_information` VALUES ('161', null, '<p>\r\n	<img alt=\"\" src=\"/attached/image/20120529/20120529174149_806.jpg\" width=\"339\" height=\"450\" />\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<img alt=\"\" src=\"/attached/image/20120529/20120529174205_821.jpg\" /> \r\n<p>\r\n	&nbsp;\r\n</p>\r\n<img alt=\"\" src=\"/attached/image/20120529/20120529174220_590.jpg\" /> \r\n<p>\r\n	&nbsp;\r\n</p>\r\n<img alt=\"\" src=\"/attached/image/20120529/20120529174232_737.jpg\" /> \r\n<p>\r\n	&nbsp;\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '3', '27', '工会活动照片', '4', '2012-05-29 17:55:21', null, null, '2', '/25b44f3bde0f6dc837e893b3ae6eae56.jpg', null, '摘草莓', '2', '/resourcefiles/thumbnail/596bcd7236e58e93ce681bfbe2db6771.jpg', '1');
INSERT INTO `ws_information` VALUES ('162', null, '&nbsp;\r\n<p style=\"text-indent:103.9pt;\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:16pt;\">人文学院督导组总体情况</span></b><b><span style=\"line-height:150%;font-family:宋体;font-size:16pt;\"></span></b>\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\"></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:35pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">人文学院督导小组成立于<span>2008</span>年。现有督导成员<span>8</span>名，全部是来自教学第一线、具有副高级以上职称的教师。<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:35pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">依据学院教学管理制度，督导小组承担着对英语、体育、语文、数学、思政五个教研室，共 <span>69</span>名教师的教学质量监控，常规教学及教学管理工作的抽查、评价、指导、提供动态分析和参考意见，积极深入地开展督管、督教和督学工作。</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:13.75pt;\">\r\n	<strong><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">一、工作职责</span></strong><span style=\"line-height:150%;font-size:14pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">1</span><span style=\"line-height:150%;font-size:14pt;\">、坚持<span>“</span>督<span>”</span>、<span>“</span>导<span>”</span>结合，以<span>“</span>导<span>”</span>为主的原则，接受学院督导室对分院督导小组工作的检查和指导，根据学校的督导工作安排，完成督导室提出的具体工作任务。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">2</span><span style=\"line-height:150%;font-size:14pt;\">、配合分院进行常规性、基础性的教学督导工作，全面了解和掌握本分院的教学、科研、管理等工作，规范执行学院的教学督导制度和工作流程。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">3</span><span style=\"line-height:150%;font-size:14pt;\">、及时了解一线教学动态，对师生在教学、管理工作中反映出的问题及要求及时反馈到督导室，对分院的教学工作进行检查、督促、指导、评价，为学院改进教学工作提供依据，为分院年终对教师的考核、考评提供依据。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">4</span><span style=\"line-height:150%;font-size:14pt;\">、在督导工作实践中，本着改革和创新的原则，努力发现和捕捉教师教学过程中体现出的亮点，及时总结经验和教训，探索适合教师和教学工作的督导理念、措施等，不断提升督导能力和水平。以期更好的发挥督导的作用。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:27.55pt;\">\r\n	<strong><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">二、工作任务</span></strong><span style=\"line-height:150%;font-size:14pt;\"></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:27.45pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">1</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">、每学期期初制定分院的教学督导组工作计划并报督导室备案。 <span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:28pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">2</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">、参与制定分院教师教学质量督导评估方案等工作。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:27.45pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">3</span><span style=\"line-height:150%;font-size:14pt;\">、每学期按时制定督导教师听课安排表。督导组采取集体听课、集体评议的形式进行：即对每位教师听课及评议至少是三人的形式。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">4</span><span style=\"line-height:150%;font-size:14pt;\">、每学期有计划的听课。督导组每位成员一学期听课一般不少于<span>12</span>学时，每位成员在期末要按时上交听课记录本和一份督导总结。组长负责组织成员开展听课、评议和反馈等二级督导工作。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:27.45pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">5</span><span style=\"line-height:150%;font-size:14pt;\">、每月<span>30</span>日前，整理、汇总本月的教育教学情况，形成督导简报后上报到学院督导室，并将相关资料存档。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:27.45pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">6</span><span style=\"line-height:150%;font-size:14pt;\">、督导组成员不定期召开碰头会，商讨和交流督导情况，明确工作任务与内容，提出改进建议。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:27.45pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">7</span><span style=\"line-height:150%;font-size:14pt;\">、不定期组织召开学生和教师座谈会，了解广大师生对教学工作的意见和要求，及时向学院及相关部门反馈信息和提出建议。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:27.45pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">8</span><span style=\"line-height:150%;font-size:14pt;\">、协助分院组织每学期的期初、期中、期末及其它教学检查工作。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:27.45pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">9</span><span style=\"line-height:150%;font-size:14pt;\">、协助分院完成对每位教师的评教、评优及年度考核工作。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:27.45pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">10</span><b><span style=\"line-height:150%;font-size:14pt;\">、</span></b><span style=\"line-height:150%;font-size:14pt;\">分院督导组长每学期要按时上交督导室听课记录本、督导总结和调研报告。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28.1pt;\">\r\n	<b><span style=\"line-height:150%;font-size:14pt;\">三、工作方法 <span></span></span></b>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">1</span><span style=\"line-height:150%;font-size:14pt;\">、对教师教学效果进行全面评价。二级教学督导组通过开展听课活动，就所听课程的教学内容、方法、手段、效果进行评价，同时指出授课教师在授课过程中的主要优点和存在问题，并提出改进建议。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">2</span><span style=\"line-height:150%;font-size:14pt;\">、对督导工作中发现的问题，进行深入的调查研究，每学期督导组向督导室提交一份调研报告，提出具体改进意见或建议，为学院领导提供第一手情况，为其决策提供依据。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">3</span><span style=\"line-height:150%;font-size:14pt;\">、在听课评课的基础上，我们将把工作范围逐步扩大到教学改革、课程设计、实验实训、出题考试等各个教学环节。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">4</span><span style=\"line-height:150%;font-size:14pt;\">、向分院推荐课堂教学效果突出的教师，对每年教学业绩评为优秀的教师实施免听课制度；每学期组织<span>1-2</span>次观摩教学。督导组还参与分院的教学能手、学科竞赛的推荐选拔等工作。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"line-height:150%;font-size:14pt;\">5</span><span style=\"line-height:150%;font-size:14pt;\">、增强二级教学督导工作<span>“</span>导<span>”</span>的力度，重点放在加强对中青年教师的指导上。督导组在听课后，及时与授课教师交换意见，使其尽快掌握教学技能和教学规律。使青年教师不断的提高自己的教学能力和水平。<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:28.1pt;\" align=\"left\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">四</span></b><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">、</span><b><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">努力方向<span></span></span></b>\r\n</p>\r\n<p style=\"text-align:left;text-indent:28pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">督导工作为稳定教学秩序，顺利开展教学活动，加强教学基本建设，不断提高教学和育人质量，做出了一些成绩，但在工作中还有一定的不足，有待改进和提高。今后我们督导工作的重点是：<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:28pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">1</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">、勇于创新，与时俱进。近年来教育教学形势发展，出现了一些新的思想、新的理念。因此，这就要求我们要加强学习，不断研究新情况、新问题，树立新的教育思想观念，适应不断变化的新形势，进一步把教学督导工作做好。<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:28pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">2</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">、督导结合，强化服务。在教学实践中，进一步处理好督与导的关系。坚持以导为主，以督为辅，为此我们还要不断调查研究，不断改进工作，探索适合分院、不同学科的督导机制，增强为师生服务的意识，强化为教学服务的职能。<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:28pt;\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">3</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">、夯实基础，力求突破。今后一方面要继续加强对教学规范化建设的监督与指导，不断夯实基础，为分院的进一步发展和教学水平的不断提高做好各项基础工作。另方面，在原有的工作基础上，求真务实、加大力度，在学院的课程改革的实践探索中，在完成省示范建设的各种工作中，总结积累经验、努力完善机制，为学院的大发展尽心尽力。</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\"></span>\r\n</p>', null, '2012-05-29', 'admin', '管理员', null, '0', '58', '教学督导', '3', '2012-05-29 17:55:21', null, null, '2', null, null, '人文学院教学督导情况', '1', null, '1');
