INSERT INTO `ws_information` VALUES ('187', null, '<div>\r\n	<p>\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;font-weight:bold;\">关于<span>09</span><span>级学生</span><span>“</span><span>专升本</span><span>”</span><span>考试宣传工作通知</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">来源：招生就业处<span>&nbsp;&nbsp;</span><span>时间：</span><span>2011-05-31&nbsp;09:22</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">各二级学院：</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　请各二级学院做好<span>09</span><span>级学生</span><span>“</span><span>专升本</span><span>”</span><span>考试的报名宣传工作，于</span><span>6</span><span>月</span><span>11</span><span>日前做好调查统计工作，下载附件</span><span>1</span><span>和附件</span><span>2</span><span>填表汇总。有关报名政策及</span><span>2011</span><span>年专升本本科院校招收信息参见附件</span><span>3</span><span>和附件</span><span>4</span><span>。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　附件<span>1</span><span>：专升本意向调查表</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　附件<span>2</span><span>：专升本意向统计表</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　附件<span>3</span><span>：报考政策</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　附件<span>4</span><span>：</span><span>2011</span><span>年专升本本科院校分校分专业招收计划&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">附件下载</span><span><a href=\"http://www.zjff.net/uploads/notice/file/2011_05/31/092204.doc\"><span style=\"font-family:\'ˎ̥\';color:#575644;font-size:10pt;\">附件<span>1.doc</span></span></a></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">附件下载</span><span><a href=\"http://www.zjff.net/uploads/notice/file/2011_05/31/092207.xls\"><span style=\"font-family:\'ˎ̥\';color:#575644;font-size:10pt;\">附件<span>2.xls</span></span></a></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">附件下载</span><span><a href=\"http://www.zjff.net/uploads/notice/file/2011_05/31/092210.doc\"><span style=\"font-family:\'ˎ̥\';color:#575644;font-size:10pt;\">附件<span>3.doc</span></span></a></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">附件下载</span><span><a href=\"http://www.zjff.net/uploads/notice/file/2011_05/31/092213.doc\"><span style=\"font-family:\'ˎ̥\';color:#575644;font-size:10pt;\">附件<span>4.doc</span></span></a></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'Times New Roman\';font-size:10.5pt;\">http://www.zjff.net/notice_show.php?id=35772</span><span style=\"font-family:\'Times New Roman\';font-size:10.5pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-29', 'admin', '管理员', null, '4', '43', '学院通知与报导', '3', '2012-05-29 17:56:52', null, null, '2', null, null, '关于09级学生“专升本”考试宣传工作通知', '1', null, '1');
