INSERT INTO `ws_information` VALUES ('195', '', '<div>\r\n	<p>\r\n		<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"> </span> \r\n	</p>\r\n	<div>\r\n		<p>\r\n			<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p style=\"text-align:center;\">\r\n			<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"> 人文学院与宁波市土地勘测规划所签定合作协议</span><span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p>\r\n			<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p style=\"text-indent:24.0000pt;\">\r\n			<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\">2010年11月，人文学院书记丁丽君代表分院与宁波市土地勘测规划所签定了一份人文学院特色培训（企业文化培训）项目合作协议书，培训课程将根据用人单位需要量身定做，本次合作将开设应用文写作提高、与人交流沟通能力、企业员工心理健康教育指导、企业文化策划、软件应用等课程，将由人文学院选派相关优秀教师执教。</span><span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p style=\"text-indent:24.0000pt;\">\r\n			<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p style=\"text-indent:24.0000pt;\">\r\n			<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p style=\"text-align:right;text-indent:24pt;\">\r\n			<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"> 2010年11月4日</span><span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-weight:bold;font-size:16.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p style=\"text-align:center;\">\r\n			<span style=\"font-weight:bold;font-size:16.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n		<p style=\"text-align:center;\">\r\n			<span style=\"font-weight:bold;font-size:16.0000pt;font-family:\'宋体\';\"></span> \r\n		</p>\r\n	</div>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:right;text-indent:24pt;\">\r\n		<span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span><span style=\"color:#000000;font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-weight:bold;font-size:16.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-weight:bold;font-size:16.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-weight:bold;font-size:16.0000pt;font-family:\'宋体\';\"></span> \r\n	</p>\r\n</div>', '', '2012-05-30', 'admin', '管理员', '', '8', '30', '企业文化培训', '3', '2012-05-30 16:15:23', '', '', '2', '', null, '人文学院与宁波市土地勘测规划所签定合作协议', '1', null, '1');
INSERT INTO `ws_information` VALUES ('196', '', '<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\"></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\">浙江纺织服装职业技术学院人文学院课程培训</span><span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\"><br />\r\n</span>\r\n</p>\r\n<table style=\"width:90%;\" border=\"1\" cellspacing=\"0\" bordercolor=\"#000000\" cellpadding=\"1\" align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">培训单位</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<span style=\"font-family:\'宋体\';font-size:12pt;\">培训内容</span> \r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">授课教师</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">职称</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">课时数（学时）</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"5\">\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">宁波市土地勘测规划所</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">应用文写作提高</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">林莹</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">副教授</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">30</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">与人交流沟通能力</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">田甜</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">研究生</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">30</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">企业员工心理健康教育指导</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">崔玉环</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">副教授</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">30</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">企业文化策划</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">张艺</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">副教授</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">30</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p>\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">软件应用</span> \r\n				</p>\r\n			</td>\r\n			<td>\r\n				<br />\r\n			</td>\r\n			<td>\r\n				<br />\r\n			</td>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					<span style=\"font-family:\'宋体\';font-size:12pt;\">20</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\"> \r\n	<div>\r\n		<p style=\"text-align:right;\">\r\n			<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n		</p>\r\n		<p style=\"text-align:right;\">\r\n			<span style=\"font-family:\'宋体\';font-size:12pt;\">浙江纺织服装职业技术学院人文学院</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n		</p>\r\n		<p style=\"text-align:right;\">\r\n			<span style=\"font-family:\'Times New Roman\';font-size:12pt;\">2010-9-28</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n		</p>\r\n	</div>\r\n<br />\r\n</span> \r\n	<p>\r\n		<br />\r\n	</p>', '', '2012-05-30', 'admin', '管理员', '', '23', '30', '企业文化培训', '3', '2012-05-30 16:17:50', '', '', '2', '', null, '浙江纺织服装职业技术学院人文学院课程培训安排', '1', null, '1');
INSERT INTO `ws_information` VALUES ('239', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'Times New Roman\';font-size:14pt;font-weight:bold;\">中华人民共和国国家通用语言文字法</span><span style=\"font-family:\'宋体\';font-size:14pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\">中华人民共和国主席令第三十七号&nbsp;</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\">2000年10月31日第九届全国人民代表大会常务委员会第十八次会议通过&nbsp;</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\">&nbsp;&nbsp;&nbsp;&nbsp;《中华人民共和国国家通用语言文字法》已由中华人民共和国第九届全国人民代表大会常务委员会第十八次会议于2000年10月31日通过，现予公布，自2001年1月1日起施行。</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\">中华人民共和国主席&nbsp;&nbsp;江泽民</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\">2000年10月31日</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:9pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">目&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;录&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">第一章&nbsp;总则&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">第二章&nbsp;国家通用语言文字的使用&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">第三章&nbsp;管理和监督&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">第四章&nbsp;附则</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">第一章&nbsp;&nbsp;总则&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第一条&nbsp;&nbsp;为推动国家通用语言文字的规范化、标准化及其健康发展，使国家通用语言文字在社会生活中更好地发挥作用，促进各民族、各地区经济文化交流，根据宪法，制定本法。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二条&nbsp;&nbsp;本法所称的国家通用语言文字是普通话和规范汉字。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第三条&nbsp;&nbsp;国家推广普通话，推行规范汉字。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第四条&nbsp;&nbsp;公民有学习和使用国家通用语言文字的权利。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　国家为公民学习和使用国家通用语言文字提供条件。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　地方各级人民政府及其有关部门应当采取措施，推广普通话和推行规范汉字。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第五条&nbsp;&nbsp;国家通用语言文字的使用应当有利于维护国家主权和民族尊严，有利于国家统一和民族团结，有利于社会主义物质文明建设和精神文明建设。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第六条&nbsp;&nbsp;国家颁布国家通用语言文字的规范和标准，管理国家通用语言文字的社会应用，支持国家通用语言文字的教学和科学研究，促进国家通用语言文字的规范、丰富和发展。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第七条&nbsp;&nbsp;国家奖励为国家通用语言文字事业做出突出贡献的组织和个人。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第八条&nbsp;&nbsp;各民族都有使用和发展自己的语言文字的自由。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　少数民族语言文字的使用依据宪法、民族区域自治法及其他法律的有关规定。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">第二章&nbsp;&nbsp;国家通用语言文字的使用&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第九条&nbsp;&nbsp;国家机关以普通话和规范汉字为公务用语用字。法律另有规定的除外。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十条&nbsp;&nbsp;学校及其他教育机构以普通话和规范汉字为基本的教育教学用语用字。法律另有规定的除外。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　学校及其他教育机构通过汉语文课程教授普通话和规范汉字。使用的汉语文教材，应当符合国家通用语言文字的规范和标准。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十一条&nbsp;&nbsp;汉语文出版物应当符合国家通用语言文字的规范和标准。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　汉语文出版物中需要使用外国语言文字的，应当用国家通用语言文字作必要的注释。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十二条&nbsp;&nbsp;广播电台、电视台以普通话为基本的播音用语。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　需要使用外国语言为播音用语的，须经国务院广播电视部门批准。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十三条&nbsp;&nbsp;公共服务行业以规范汉字为基本的服务用字。因公共服务需要，招牌、广告、告示、标志牌等使用外国文字并同时使用中文的，应当使用规范汉字。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　提倡公共服务行业以普通话为服务用语。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十四条&nbsp;&nbsp;下列情形，应当以国家通用语言文字为基本的用语用字：&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（一）广播、电影、电视用语用字；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（二）公共场所的设施用字；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（三）招牌、广告用字；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（四）企业事业组织名称；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（五）在境内销售的商品的包装、说明。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十五条&nbsp;&nbsp;信息处理和信息技术产品中使用的国家通用语言文字应当符合国家的规范和标准。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十六条&nbsp;&nbsp;本章有关规定中，有下列情形的，可以使用方言：&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（一）国家机关的工作人员执行公务时确需使用的；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（二）经国务院广播电视部门或省级广播电视部门批准的播音用语；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（三）戏曲、影视等艺术形式中需要使用的；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（四）出版、教学、研究中确需使用的。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十七条&nbsp;&nbsp;本章有关规定中，有下列情形的，可以保留或使用繁体字、异体字：&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（一）文物古迹；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（二）姓氏中的异体字；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（三）书法、篆刻等艺术作品；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（四）题词和招牌的手书字；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（五）出版、教学、研究中需要使用的；&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　（六）经国务院有关部门批准的特殊情况。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十八条&nbsp;&nbsp;国家通用语言文字以《汉语拼音方案》作为拼写和注音工具。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　《汉语拼音方案》是中国人名、地名和中文文献罗马字母拼写法的统一规范，并用于汉字不便或不能使用的领域。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　初等教育应当进行汉语拼音教学。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第十九条&nbsp;&nbsp;凡以普通话作为工作语言的岗位，其工作人员应当具备说普通话的能力。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　以普通话作为工作语言的播音员、节目主持人和影视话剧演员、教师、国家机关工作人员的普通话水平，应当分别达到国家规定的等级标准；对尚未达到国家规定的普通话等级标准的，分别情况进行培训。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十条&nbsp;&nbsp;对外汉语教学应当教授普通话和规范汉字。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">第三章&nbsp;&nbsp;管理和监督&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十一条&nbsp;&nbsp;国家通用语言文字工作由国务院语言文字工作部门负责规划指导、管理监督。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;国务院有关部门管理本系统的国家通用语言文字的使用。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十二条&nbsp;&nbsp;地方语言文字工作部门和其他有关部门，管理和监督本行政区域内的国家通用语言文字的使用。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十三条&nbsp;&nbsp;县级以上各级人民政府工商行政管理部门依法对企业名称、商品名称以及广告的用语用字进行管理和监督。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十四条&nbsp;&nbsp;国务院语言文字工作部门颁布普通话水平测试等级标准。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十五条&nbsp;&nbsp;外国人名、地名等专有名词和科学技术术语译成国家通用语言文字，由国务院语言文字工作部门或者其他有关部门组织审定。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十六条&nbsp;&nbsp;违反本法第二章有关规定，不按照国家通用语言文字的规范和标准使用语言文字的，公民可以提出批评和建议。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　本法第十九条第二款规定的人员用语违反本法第二章有关规定的，有关单位应当对直接责任人员进行批评教育；拒不改正的，由有关单位作出处理。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　城市公共场所的设施和招牌、广告用字违反本法第二章有关规定的，由有关行政管理部门责令改正；拒不改正的，予以警告，并督促其限期改正。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十七条&nbsp;&nbsp;违反本法规定，干涉他人学习和使用国家通用语言文字的，由有关行政管理部门责令限期改正，并予以警告。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">第四章&nbsp;&nbsp;附则&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">　　第二十八条&nbsp;&nbsp;本法自2001年1月1日起施行。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-06-01', 'admin', '管理员', null, '2', '67', '政策法规', '3', '2012-06-01 15:15:45', null, null, '2', null, null, '中华人民共和国国家通用语言文字法', '1', null, '1');
INSERT INTO `ws_information` VALUES ('197', '', '<p style=\"text-align:center;\">\r\n	<span style=\"font-family:宋体;\" class=\"Apple-style-span\"><span style=\"line-height:46px;font-size:19px;\" class=\"Apple-style-span\"><b></b></span></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<b><span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\">《沟通能力》教学内容安排</span> </b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14pt;\">理&nbsp;&nbsp;&nbsp;&nbsp;论&nbsp;： <span>11 </span><span>学时 </span></span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14pt;\">实&nbsp;&nbsp;&nbsp;&nbsp;践&nbsp;： <span>22 </span><span>学时 </span></span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14pt;\">合&nbsp;&nbsp;&nbsp;&nbsp;计&nbsp;： <span>33 </span><span>学时</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14pt;\"><span><br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14pt;\"><span> \r\n	<table style=\"padding-bottom:0pt;padding-left:5.4pt;width:100%;padding-right:5.4pt;border-collapse:collapse;padding-top:0pt;\" border=\"1\" cellspacing=\"0\" bordercolor=\"#000000\" cellpadding=\"1\" align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">教学内容或实践项目名称</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">作业内容及数量</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"line-height:2.5;\">克服心理障碍，学会当众讲话</span><span style=\"line-height:2.5;\">/</span><span style=\"line-height:2.5;\">自我介绍</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">当众讲话训练</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"line-height:2.5;\">学会团队沟通、当众演讲</span><span style=\"line-height:2.5;\">/</span><span style=\"line-height:2.5;\">组建团队</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">加强团队沟通与协作</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"line-height:2.5;\">展示团队风采</span><span style=\"line-height:2.5;\">/</span><span style=\"line-height:2.5;\">语言与非语言沟通</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">完成问卷设计</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"line-height:2.5;\">问卷优选</span><span style=\"line-height:2.5;\">/</span><span style=\"line-height:2.5;\">学会闲谈和解说</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">问卷调查准备工作</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">实施调查并分析结果、形成报告</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">完成调查报告</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"line-height:2.5;\">学会主持会议、学会倾听和交谈</span><span style=\"line-height:2.5;\">/</span><span style=\"line-height:2.5;\">拟定讨论会方案</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">完成讨论会方案</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">开展讨论会</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">整理会议内容</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"line-height:2.5;\">学会访谈</span><span style=\"line-height:2.5;\">/</span><span style=\"line-height:2.5;\">根据拟定访谈方案</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">联系访谈对象</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">实施访谈</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">整理访谈内容</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">形成总结性文字材料</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">准备汇报</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"line-height:2.5;font-family:宋体;font-size:10.5pt;\">汇报总结、自我评价</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"middle\" width=\"190\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"font-family:\'Times New Roman\';font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"386\">\r\n					<p>\r\n						<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 1px solid;border-left:#000000 1px solid;border-top:#000000 1px solid;border-right:#000000 1px solid;\" valign=\"middle\" width=\"190\">\r\n					<p>\r\n						<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n<br />\r\n</span></span>\r\n</p>', '', '2012-05-30', 'admin', '管理员', '', '19', '39', '课程建设', '3', '2012-05-30 16:51:28', '', '', '2', '', null, '与人沟通教学计划教学计划', '1', null, '1');
INSERT INTO `ws_information` VALUES ('198', null, '<div>\r\n	<table align=\"center\" style=\"border-collapse:collapse;padding:0.0000pt 0.0000pt 0.0000pt 0.0000pt;\">\r\n		<tbody>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'ˎ̥\';\">我院获批成为国家职业核心能力培训认证考评点</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">&nbsp;</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p>\r\n						<span style=\"font-weight:bold;font-size:10.5000pt;font-family:\'Times New Roman\';\">http://www.zjff.net/news_show.php?id=10062</span><span style=\"font-weight:bold;font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n					</p>\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:8.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">上传：人文学院&nbsp;郑晓<span>&nbsp;&nbsp;</span><span>时间：</span><span>11</span><span>年</span><span>09</span><span>月</span><span>09</span><span>日&nbsp;</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 为贯彻落实人力资源和社会保障部职业技能鉴定中心《劳社鉴函（<span>2009</span><span>）</span><span>32</span><span>号》文件指示精神，满足社会及用人单位对职业核心能力培养和评价的要求，增强学生的就业能力，国家职业核心能力培训认证全国推广中心在全国开展职业核心能力认证测评工作。经过国家人力资源和社会保障部的审核，浙江纺织服装职业技术学院已经符合建立职业核心能力考评点的要求，</span><span>8</span><span>月</span><span>25</span><span>日，国家职业核心能力培训认证全国推广中心正式发出《职培（</span><span>2011</span><span>）第</span><span>13</span><span>号文件》通知，同意浙江纺织服装职业技术学院正式成为国家职业核心能力考评点，编号为：</span><span>12026002</span><span>。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 据悉，为提高职业教育服务社会、服务地方经济、服务学生就业的能力，自<span>2008</span><span>年始，我院就选派教师参加全国职业核心能力师资培训暨测评师认证培训，经过培训考核，目前已有王苹、王冬梅、吴颖、崔玉环等教师获得了国家职业核心能力培训师和测评师资格证书。为了进一步加强与国家有关部门的合作，深入开展国家职业核心能力的培训、测评和推广工作，把国家职业核心能力培训和认证工作落到实处，自</span><span>2011</span><span>年</span><span>3</span><span>月始，人文学院就与机电轨道学院联手创办</span><span>“</span><span>创新教育实验班</span><span>”</span><span>，就学生的沟通能力、合作能力、解决问题能力进行针对性教学与培训，而随着我院获批成为国家职业核心能力培训认证考评点后，首批</span><span>26</span><span>位来自城轨通信</span><span>1</span><span>、</span><span>2</span><span>班的学生将作为此考点的第一批考生参加国家职业核心能力测评考试，考评合格的学员也将获得了证明自己职业核心能力水平的《职业核心能力水平等级证书》（国家职业资格证书）。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 此次，国家职业核心能力培训认证考评点在我院的设立将大大激发我院开设职业核心能力课程的热情。自新学年始，已着力开展学生职业核心能力培养的人文学院计划将在全院范围内进一步推进落实职业核心能力示范培训认证工作，为提升我院学生核心竞争力创造更好的条件。　　&nbsp;</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span class=\"Apple-style-span\" style=\"color:#333333;font-family:宋体;font-size:x-small;\"><span class=\"Apple-style-span\" style=\"line-height:19px;\"><img src=\"/attached/image/20120530/20120530170438_989.jpg\" alt=\"\" /><br />\r\n</span></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">创新班《与人合作》实践教学课</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span class=\"Apple-style-span\" style=\"color:#333333;font-family:宋体;font-size:x-small;\"><span class=\"Apple-style-span\" style=\"line-height:19px;\"><img src=\"/attached/image/20120530/20120530170449_872.jpg\" alt=\"\" /><br />\r\n</span></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:8.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:right;\">\r\n						<span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">撰稿：郑晓</span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">阅读次数：<span>544&nbsp;</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n</div>', null, '2012-05-30', 'admin', '管理员', null, '3', '41', '最新资讯', '3', '2012-05-30 17:08:24', null, null, '2', null, null, '我院获批成为国家职业核心能力培训认证考评点', '2', '/resourcefiles/thumbnail/8c5a92f992e7d1e49042315a88d8d915.jpg', '1');
INSERT INTO `ws_information` VALUES ('199', null, '<div>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'Times New Roman\';\">http://www.zjff.net/news_show.php?id=10912</span><span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'Times New Roman\';\"></span>\r\n	</p>\r\n	<table align=\"center\" style=\"border-collapse:collapse;padding:0.0000pt 0.0000pt 0.0000pt 0.0000pt;\">\r\n		<tbody>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'ˎ̥\';\">第四届红帮文化演讲大赛</span><span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'ˎ̥\';\">——<span>暨学院首届大学生文明礼仪大赛圆满落幕</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">&nbsp;</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:8.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">上传：人文学院&nbsp;郑晓<span>&nbsp;&nbsp;</span><span>时间：</span><span>11</span><span>年</span><span>12</span><span>月</span><span>01</span><span>日&nbsp;</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 为继续深入继承和发扬百年红帮精神，做大做强校园文化品牌，激励我院师生学习发扬红帮精神，鼓励学生秉持红帮精神积极应对挑战，并创造文明校园，营造良好的学习生活环境，提高大学生文明素质，促进大学生养成文明行为、增长礼仪知识、增强礼仪意识、遵守礼仪规范，<span>11</span><span>月</span><span>29</span><span>日晚</span><span>18</span><span>点</span><span>15</span><span>分，在</span><span>2</span><span>号楼</span><span>4</span><span>楼</span><span>T</span><span>台举行了第四届红帮精神演讲暨第一届大学生文明礼仪大赛。大赛邀请了学院学生处处长胡斌，人文学院院长王苹，党总支书记丁丽君、人文学院公关老师王冬梅、人文学院大学语文老师吴颖担当评委。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 此次比赛由总院团委主办，人文学院及创新教育实验班承办，现场有微博直播。来自纺织、服装、商学院、易斯戴、轨道机电、艺术、信息<span>7</span><span>个学院的参赛团队参加了本次比赛。大赛共有三大环节：一是演讲环节，即要求参赛队选派一位队员以</span><span>“</span><span>传承红帮精神，争做红帮新人</span><span>”</span><span>为主题作限时三分钟的演讲。二是限时问答环节，即要求每个参赛组用</span><span>90</span><span>秒时间作文明礼仪知识问答。三是情景模拟表演环节，即每个团队用</span><span>5</span><span>分钟时间以情景模拟表演诠释不同选题的</span><span>“</span><span>文明礼仪</span><span>”</span><span>内容。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 比赛中，选手们在展示红帮精神、传承红帮文化、发扬红帮文化的演讲中纷纷展现了对我院校园文化的思考和感悟。在限时问答的环节中，各个参赛团队更是争分夺秒，问答题几乎都以不假思索、对答如流的方式给大家留下了深刻的印象。在高潮迭起的情景模拟环节中，各参赛队又纷纷以编排形式新颖、内容务实、各具创意的表演赢得了现场观众的热烈掌声和感同身受的欢呼声。其中，由金碧聪、方婷婷、陈芸、陈静四位同学组成的易斯戴团队在这一环节的表演中更是得到了评委和现场观众的一致肯定。</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 当晚现场还穿插的一段街舞表演和<span>“</span><span>十佳歌手</span><span>”</span><span>冠军</span><span>SB</span><span>组合成员带来的歌曲《男人的好》，既带动了全场的气氛，也为紧张的比赛增加了几份新鲜感。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 随后，人文学院院长王苹上台发言，她首先肯定了全体参赛选手的精彩表现，并高度赞扬了机电轨道学院的城轨通信<span>1</span><span>、</span><span>2</span><span>班</span><span>26</span><span>位</span><span>“</span><span>创新教育班</span><span>”</span><span>学生在承办本次比赛中的努力和付出。她表示这次大赛能如期成功举办，与创新班全体同学的积极主动的工作态度分不开，与他们全员动员、群策群力、分工明确、各司其职、各尽其责的工作安排分不开，也与他们积极协作、并肩作战的团结作风分不开。而更有意义的是，创新班全体同学在整个比赛的承办中，充分汲取了《与人沟通》《与人合作》等理论知识学习精华，展示和发挥了自身的特长和优势，提高了解决问题的能力，历练了职业核心能力，为未来进入职场储备了能量。最后，王苹院长号召大家积极践行红帮文化精神，做一个文明的人，道德的人，有情操的人。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 而最终的比赛成绩为：服装学院、商学院、艺术学院荣获优秀奖；机电学院和信息学院荣获三等奖；易斯戴学院荣获二等奖；大赛一等奖由纺织学院摘得。</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 可以说，每一场比赛其实就是一个学习的过程，作为红帮文化的继承人，我院学子更应该取其精华，去其糟粕，开拓创新，为红帮文化注入新的活力，将红帮精神发扬光大。而对于文明礼仪，当代大学生更应该秉持红帮精神积极应对挑战，为创造文明校园，营造良好的学习生活环境而努力，努力提高自身文明素质，养成文明行为、增长礼仪见识、增强礼仪意识、遵守礼仪规范。&nbsp;</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span class=\"Apple-style-span\" style=\"color:#333333;font-family:宋体;font-size:x-small;\"><span class=\"Apple-style-span\" style=\"line-height:19px;\"><img src=\"/attached/image/20120530/20120530170610_49.jpg\" alt=\"\" /><br />\r\n</span></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">比赛现场</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span class=\"Apple-style-span\" style=\"color:#333333;font-family:宋体;font-size:x-small;\"><span class=\"Apple-style-span\" style=\"line-height:19px;\"><img src=\"/attached/image/20120530/20120530170619_180.jpg\" alt=\"\" /><br />\r\n</span></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">获奖选手和评委合影</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span class=\"Apple-style-span\" style=\"color:#333333;font-family:宋体;font-size:x-small;\"><span class=\"Apple-style-span\" style=\"line-height:19px;\"><img src=\"/attached/image/20120530/20120530170627_522.jpg\" alt=\"\" /><br />\r\n</span></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">礼仪知识问答</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span class=\"Apple-style-span\" style=\"color:#333333;font-family:宋体;font-size:x-small;\"><span class=\"Apple-style-span\" style=\"line-height:19px;\"><img src=\"/attached/image/20120530/20120530170635_993.jpg\" alt=\"\" /><br />\r\n</span></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">情景模拟</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span class=\"Apple-style-span\" style=\"color:#333333;font-family:宋体;font-size:x-small;\"><span class=\"Apple-style-span\" style=\"line-height:19px;\"><img src=\"/attached/image/20120530/20120530170648_790.jpg\" alt=\"\" /><br />\r\n</span></span>\r\n					</p>\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">演讲</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:8.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:right;\">\r\n						<span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">撰稿：创新班&nbsp;孙丹燕&nbsp;学生记者团&nbsp;朱蓉翠</span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">阅读次数：<span>345&nbsp;</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n</div>', null, '2012-05-30', 'admin', '管理员', null, '2', '41', '最新资讯', '3', '2012-05-30 17:08:24', null, null, '2', null, null, '第四届红帮文化演讲大赛', '2', '/resourcefiles/thumbnail/731f64f7a184b77d797dc26213e15dc1.jpg', '1');
INSERT INTO `ws_information` VALUES ('200', null, '<div>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'Times New Roman\';\">http://www.zjff.net/news_show.php?id=8698</span><span style=\"font-size:10.5000pt;font-family:\'Times New Roman\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'ˎ̥\';\">人文学院教师参加全国职业核心能力培训</span><span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">上传：人文学院&nbsp;王锡金<span>&nbsp;&nbsp;</span><span>时间：</span><span>10</span><span>年</span><span>12</span><span>月</span><span>09</span><span>日</span></span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<table align=\"center\" style=\"border-collapse:collapse;padding:0.0000pt 0.0000pt 0.0000pt 0.0000pt;\">\r\n		<tbody>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">2010<span>年</span><span>12</span><span>月</span><span>4</span><span>日，人文学院王苹院长带领王冬梅及吴颖两位教师，前往杭州参加全国第</span><span>46</span><span>期职业核心能力师资培训暨第</span><span>16</span><span>期测评师认证培训。在为期三天的培训活动中，我们听取了多位专家关于</span><span>“</span><span>职业核心能力</span><span>”</span><span>、</span><span>“</span><span>能力本位教学</span><span>”</span><span>和</span><span>“</span><span>行动导向教学</span><span>”</span><span>等学术报告，在理论上为我们今后的教学改革指明了方向。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 中国就业培训技术指导中心的李怀康研究员在《建构能力本位教学的课程观》中指出，目前国内的职业教育基本上还是实行知识本位、学科本位，但随着社会的发展及人才观的变迁，课程也要随之相应地变革。在<span>“</span><span>职业教育就是就业教育</span><span>”</span><span>思想的指导下，职业教育应该真正步入能力本位的轨道，为社会培养技能型人才。一个人在实际生产岗位上不仅需要岗位胜任力来完成今天的任务，同时还需要职业适应力来面对未来的任务。职业教育就应该训练学生的泛技术</span><span>/</span><span>非技术能力来适应技术应用的需要、跨职业跨岗位的能力来适应岗位流动的需要，甚至还要有跨界的综合能力即可持续的发展能力来适应未来职场的激烈竞争。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 国家职业核心能力专业委员会副主任童山东教授也从当今社会对职业人才的要求出发，通过大量的调查研究指出，包括与人交流、与人合作、信息处理、数字应用、解决问题、自我提高、创新和外语应用等八大能力的职业核心能力，已成为近年来求职竞争的一大重要条件，因此加强学生职业核心能力的培养已经成为当今职业教育发展的必然趋势。在英、美、德、新加坡、香港等发达国家和地区，提高职业核心能力已经成为共识。此外，吕革新、叶昌元、王宝生三位教授则给大家作了标准教材的解读，并就与人合作、与人交流、自我学习和信息处理等模块进行了教学示范。本次培训还组织了浙江机电技工学校、杭州万向职业技术学院及北京时代旭隆企业管理顾问公司等学校、企业来就怎样培养学生和企业员工的职业核心能力介绍经验。</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 通过三天紧张的学习及说课答辩和考试，我们不仅通过了师资培训和测评师认证考试，获得了相应的证书，更重要的是深刻地认识到，学校和教师是无法将所有的知识、技术都教给学生，尤其是在这个知识和技术更新极快的时代，教学生学会如何继续学习要远比让他们在毕业前掌握多少知识来得重要。因此，我们必须颠覆传统的教学观念和模式，以就业为导向、能力为本位，运用行为引导的教学方法和多元化的评价方式来组织教学，变教师单纯的<span>“</span><span>教</span><span>”</span><span>为学生主动地</span><span>“</span><span>学</span><span>”</span><span>，让课堂延伸出教室，让学生在活动中学习，并逐渐成长为具有综合职业能力的新时代人才。&nbsp;</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:8.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:right;\">\r\n						<span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">撰稿：吴颖</span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">阅读次数：<span>560&nbsp;</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'Times New Roman\';\"></span>\r\n	</p>\r\n</div>', null, '2012-05-30', 'admin', '管理员', null, '2', '41', '最新资讯', '3', '2012-05-30 17:08:24', null, null, '2', null, null, '人文学院教师参加全国职业核心能力培训', '1', null, '1');
INSERT INTO `ws_information` VALUES ('201', null, '<div>\r\n	<p>\r\n		<span><a href=\"http://www.zjff.net/news_show.php?id=9655\"><span style=\"color:#0000FF;text-decoration:underline;font-size:10.0000pt;font-family:\'Times New Roman\';\">http://www.zjff.net/news_show.php?id=9655</span></a></span><span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#333333;font-weight:bold;font-size:12.0000pt;font-family:\'ˎ̥\';\">职业核心能力<span>:</span><span>学生就业的</span><span>“</span><span>独门兵器</span><span>”</span></span><span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">推荐：宣传部&nbsp;郑斌<span>&nbsp;&nbsp;</span><span>时间：</span><span>11</span><span>年</span><span>05</span><span>月</span><span>26</span><span>日</span></span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n	<table align=\"center\" style=\"border-collapse:collapse;padding:0.0000pt 0.0000pt 0.0000pt 0.0000pt;\">\r\n		<tbody>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\">一年又到五月时<span>,</span><span>大学毕业生就业高峰期如期而至</span><span>,</span><span>即将走进职场跃跃欲试。有些人获得了心仪的职业</span><span>,</span><span>他们要么极具亲和力和团队合作精神</span><span>,</span><span>要么就是沟通高手，或者能恰当地驾驭自己的情绪和智力。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 然而，还有一部分学生却将面临就业难题。数据显示<span>,2011</span><span>年全国等待就业与分配的应往届大学生总计约</span><span>760</span><span>万人。就当前大学生就业存在的压力</span><span>,</span><span>日前由教育部教育管理信息中心和本报联合主办的</span><span>“</span><span>第一届全国职业核心能力研讨会</span><span>”</span><span>引起了各方的密切关注。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 学生求职的秘密武器</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 全国职业核心能力认证办公室主任许湘岳介绍<span>,</span><span>对于一名大学生来说</span><span>,</span><span>专业能力被称为硬能力</span><span>,</span><span>而包括职业沟通、团队合作、自我管理、解决问题、信息处理和创新创业等在内的能力被称为职业核心能力或软能力</span><span>,</span><span>它是除专业岗位能力之外取得成功所必需的基本能力</span><span>,</span><span>它可让人自信和成功地展示自己，并根据具体情况选择和应用。以前</span><span>,</span><span>用人单位选人用人的标准是资历和学历</span><span>,</span><span>而现在</span><span>,</span><span>雇主则越来越倾向于注重包括职业核心能力在内的素质全面的学生。</span><span>“</span><span>资格证书只能使人获得面试的机会</span><span>,</span><span>核心能力则能使人找到工作。</span><span>”</span><span>许湘岳引用一位雇主的话说。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 在当今专业能力越来越趋同的情况下<span>,</span><span>职业核心能力越来越成为处于求职学生的</span><span>“</span><span>独门兵器</span><span>”,</span><span>谁拥有了它谁无疑就率先走近了就业、走向了成功。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 潜移默化提升学生的素质</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> <span>“</span><span>大学毕业等于失业</span><span>”,</span><span>话虽夸张</span><span>,</span><span>却也在一定程度上反映了大学生就业的严峻形势。而河南一所学校引入教育部职业核心能力课程却让学生就业发生了一些不可忽视的改变。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 位于平顶山市的河南质量工程职业学院一直注重提高学生综合素质<span>,</span><span>注重学生特长的培养</span><span>,</span><span>尤其是学院引进了教育部职业核心能力自我管理课程后</span><span>,</span><span>对于学生就业能力的培养更科学、更有针对性。如曾在第五届世界杯武术散打比赛</span><span>90</span><span>公斤以上级冠军获得者陈彦召</span><span>;</span><span>捧得上海国际模特大赛冠军的计算机专业二年级学生王丹</span><span>,</span><span>都是学校针对性科学培养人才的结果。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 个体成长还不能说明问题<span>,</span><span>整体成长和获得职业生涯的成功才是真正的成功。该院自从引入教育部职业核心能力课程之后</span><span>,</span><span>学校学习风气和学生就业明显好转。院长白君堂说</span><span>:“</span><span>素质教育在潜移默化的过程中提高了学生的一些素质</span><span>,</span><span>尤其是专业技能之外的一些软技能</span><span>,</span><span>使得学生在走入社会后受益匪浅。</span><span>”</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> <span>“</span><span>原来说话也是一种能力</span><span>”</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 汤冬<span>,</span><span>山东化工技师学院一名学生。宿舍关系紧张</span><span>,</span><span>曾五次转学</span><span>,</span><span>上课不发言</span><span>,</span><span>被列为问题学生。</span><span>2009</span><span>年伊始</span><span>,</span><span>随着教育部核心职业能力课程的培训的深入</span><span>,</span><span>汤冬的思维方式开始转变</span><span>,</span><span>生活也开始慢慢地发生了微妙的变化</span><span>,</span><span>一年后他成长为学生会副主席</span><span>,</span><span>并获得第十一届全运会</span><span>“</span><span>优秀志愿者</span><span>”</span><span>荣誉称号。</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 汤冬说<span>,</span><span>我从来没有意识到原来说话也是一种能力。该校团委副书记许芝平老师说</span><span>:“</span><span>一把尺子不能衡量所有的学生</span><span>,</span><span>学生需要重新构建自己的人生价值</span><span>,</span><span>找到自信</span><span>,</span><span>职业核心能力就是及时给我们学生的一把成功的钥匙。</span><span>”</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 国家教育咨询委员会成员、中国就业促进会副会长陈宇教授认为<span>:“</span><span>现代教育是以孩子为中心的教育</span><span>,</span><span>其重点就是要培养孩子的软实力</span><span>,</span><span>培养双核人才</span><span>,</span><span>即拥有</span><span>‘</span><span>核心从业素质</span><span>’</span><span>和</span><span>‘</span><span>核心职业技能</span><span>’</span><span>的人才。</span><span>”</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 教育部前副部长周远清说<span>:“</span><span>众多院校的实践证明</span><span>,</span><span>科学合理的职业核心能力训练对于即将走入职场的毕业生来说</span><span>,</span><span>可以全面提高学生的就业竞争力</span><span>,</span><span>并使他们可以更快地适应职场不断变化的需求。</span><span>”</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"> 本报通讯员&nbsp;魏晓明&nbsp;本报记者&nbsp;姚晓丹&nbsp;</span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"color:#333333;font-size:8.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"550\" valign=\"center\">\r\n					<p style=\"text-align:right;\">\r\n						<span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">来源：光明日报</span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\"><br />\r\n</span><span style=\"color:#666666;font-size:10.0000pt;font-family:\'ˎ̥\';\">阅读次数：<span>1230&nbsp;</span></span><span style=\"color:#333333;font-size:10.0000pt;font-family:\'ˎ̥\';\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<span style=\"font-size:10.5000pt;font-family:\'宋体\';\"></span>\r\n	</p>\r\n</div>', null, '2012-05-30', 'admin', '管理员', null, '2', '41', '最新资讯', '3', '2012-05-30 17:08:24', null, null, '2', null, null, '职业核心能力学生就业的独门兵器', '1', null, '1');
INSERT INTO `ws_information` VALUES ('202', '', '<p style=\"text-indent:21.0000pt;\">\r\n	<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">王冬梅，女，山西太原人。毕业于山西大学大学历史系。原为山西省教育学院教师，&nbsp;2003&nbsp;年&nbsp;12&nbsp;月调入浙江纺织学院人文分院，为</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">国家职业核心能力测评师，职业培训师、</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">国家高级礼仪培训师、高级礼仪、全国专业人才教育专家委员会礼仪委员&nbsp;、中国高等教育学会公关教育专业委员会浙江理事、浙江省公共关系协会理事、宁波市公共关系协会理事、省级&nbsp;普通话水平测试员&nbsp;。&nbsp;主要从事公共关系和礼仪教学与研究，&nbsp;开设过“公共关系学”、“秘书实务”、“社交礼仪”、“商务礼仪”、“涉外礼仪”、“大学生口才与交际”、&nbsp;“应用文写作”、“普通话学习与考级”等专业课与选修课，教学效果良好，深受学生欢迎，在学院开展的我心目中的好老师评选活动中曾两次获提名。&nbsp;学术研究方向公共关系学、礼仪。参编世纪高等院校创新课程规划教材《公共关系实务教程》，任副主编 </span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n</p>\r\n<p style=\"text-indent:21.0000pt;\">\r\n	<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;text-indent:21pt;\">\r\n	<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><img src=\"/attached/image/20120530/20120530171335_729.jpg\" width=\"700\" height=\"525\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;text-indent:21pt;\">\r\n	<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><img src=\"/attached/image/20120530/20120530171403_275.jpg\" width=\"700\" height=\"525\" alt=\"\" /><br />\r\n</span> \r\n</p>', '', '2012-05-30', 'admin', '管理员', '', '8', '40', '师资介绍', '3', '2012-05-30 17:12:01', '', '', '2', '', null, '王冬梅', '2', '/resourcefiles/thumbnail/6c9d0f366e32b3df1f7ef167eb6c39e4.jpg', '1');
INSERT INTO `ws_information` VALUES ('203', '', '<p>\r\n	<span style=\"font-weight:bold;font-size:13.5000pt;font-family:\'宋体\';\">&nbsp;&nbsp;&nbsp;&nbsp;王苹，女，&nbsp;1963&nbsp;年出生，浙江绍兴人。毕业于北京师范大学中文系。原为宁波大学人文与传媒学院教授，&nbsp;硕士生导师，2010年&nbsp;4&nbsp;月调入浙江纺织学院人文分院，任院长。为国家级普通话水平测试员，</span><span style=\"font-weight:bold;font-size:13.5000pt;font-family:\'宋体\';\">国家职业核心能力测评师，职业培训师。</span><span style=\"font-weight:bold;font-size:13.5000pt;font-family:\'宋体\';\"> 华东修辞学会、浙江省语言学会会员。&nbsp;主要从事现代汉语教学与研究，&nbsp;开设过“现代汉语”、“形式逻辑”、“修辞研究”、“汉语通论”、“大学生口才与交际”、“教师教学语言”、“普通话学习与考级”等专业课与选修课，教学效果优秀，深受学生欢迎。&nbsp;学术研究方向为修辞学、文化语言学。在《语言研究》、《修辞学习》、《汉字文化》、《辞书研究》、《现代语文》、《贵州社会科学》、《中文》（台湾）等国内一、二级中文核心刊物及学报上发表论文&nbsp;60&nbsp;篇；出版专著《汉语修辞与文化》一部，合著《廉政文化丛书》之一，二十余万字，&nbsp;担任&nbsp;《现代汉语》、《普通话学习与测试教程》&nbsp;两部教材副主编&nbsp;、参编《新编大学生口语交际教程》教材一部；主持承担了浙江省教育厅、浙江省社科联、浙江省哲社规划、&nbsp;浙江省新世纪高等教育教学改革研究项目&nbsp;、宁波市及宁波大学的科研课题&nbsp;15&nbsp;项；专著&nbsp;获浙江省社科联省级学术著作出版基金项目资助&nbsp;，并获得宁波市第十一届哲学社会科学优秀成果奖二等奖；教研成果“‘教&nbsp;\'&nbsp;‘测&nbsp;\'&nbsp;结合，建立高校普通话教学体系”获得宁波市第四届高校优秀教学成果二等奖&nbsp;、&nbsp;宁波大学优秀教学成果二等奖；宁波市教育规划课题成果获宁波市教育规划二等奖；所写论文获得过&nbsp;浙江省语言文字论文三等奖；宁波市首届普通话科研论文二等奖。除此以外，还&nbsp;获得过宁波大学教学优秀奖，&nbsp;宁波大学&nbsp;文学院首届&nbsp;教学评比一等奖，宁波大学优秀毕业论文指导教师奖，宁波大学优秀青年骨教师奖，宁波大学“事业家庭兼顾型”优秀女教职工奖，宁波大学优秀工会干部，宁波大学优秀班主任，浙江省省级优秀测试员，宁波大学优秀测试员，宁波大学推普先进个人等多项荣誉称号。</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-weight:bold;font-size:13.5000pt;font-family:\'宋体\';\"><img src=\"/attached/image/20120530/20120530171826_154.jpg\" width=\"700\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-weight:bold;font-size:13.5000pt;font-family:\'宋体\';\"><img src=\"/attached/image/20120530/20120530171850_338.jpg\" width=\"700\" alt=\"\" /><br />\r\n</span> \r\n</p>', '', '2012-05-30', 'admin', '管理员', '', '4', '40', '师资介绍', '3', '2012-05-30 17:19:01', '', '', '2', '', null, '王苹', '2', '/resourcefiles/thumbnail/e67d02221f55eecc7b1c149cce412df9.jpg', '1');
INSERT INTO `ws_information` VALUES ('204', '', '<p style=\"text-indent:21.0000pt;\">\r\n	<span style=\"font-size:12.0000pt;font-family:\'宋体\';\">吴颖，女，&nbsp;1975&nbsp;年出生，浙江象山人，讲师</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">，国家职业核心能力测评师，职业培训师</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\">。毕业于宁波大学中文系。为&nbsp;浙江大学中国现当代文学硕士，浙江省当代文学研究会会员。主要从事大学语文和中国现当代文学的教学与研究，开设过“大学语文”、“美学概论”、“现代文学名家名作赏析”、“中国传统文化”、“秘书实务”等专业课与选修课，教学效果优良，广受学生欢迎与好评。学术研究方向为：中国现当代文学、影视文学。发表过学术论文《无端狂笑无端哭——苏曼殊与魏晋风度》、《郁达夫与吴越文化》、《论张爱玲&nbsp;都市小说中的欲望书写&nbsp;》等，参编过《实用大学语文》教材一部，主持了校级科研课题一项，参与研究校级科研课题两项。曾获得学院第二届优质课评比优秀奖、第一届优秀教案评比三等奖、第二届说课比赛三等奖、校级优秀班主任、宁波市优秀班主任等荣誉称号。</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span> \r\n</p>\r\n<p style=\"text-indent:21.0000pt;\">\r\n	<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><br />\r\n</span>\r\n</p>\r\n<p style=\"text-align:center;text-indent:21pt;\">\r\n	<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><img src=\"/attached/image/20120530/20120530172040_742.jpg\" width=\"700\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;text-indent:21pt;\">\r\n	<span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><img src=\"/attached/image/20120530/20120530172052_70.jpg\" width=\"700\" alt=\"\" /><br />\r\n</span> \r\n</p>', '', '2012-05-30', 'admin', '管理员', '', '2', '40', '师资介绍', '3', '2012-05-30 17:21:01', '', '', '2', '', null, '吴颖', '2', '/resourcefiles/thumbnail/9d4f81d5fa575b0eab99c948b919eaa6.jpg', '1');
INSERT INTO `ws_information` VALUES ('205', null, '<div>\r\n	<p style=\"text-indent:81.9pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:15pt;font-weight:bold;\">2011<span>年韩国大邱留学生短期进修课程介绍</span></span><span style=\"font-family:\'宋体\';font-size:15pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-36pt;margin-left:36pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\">一、&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\">汉语学习</span><span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:11.25pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">&nbsp;1、实用会话一</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">2、实用会话二</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">3、实用会话三</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">4、实用会话四</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">5、实用会话五</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">6、实用会话六</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">7、实用会话七</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\">二、讲座</span><span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:11.25pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">1、讲座一：中国武术文化。</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:11.25pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">2、讲座二：中国国画艺术。</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:11.25pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">3、讲座三：中国书法艺术。</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:11.25pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">4、讲座四：中国风景名胜欣赏。</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:11.25pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">5、讲座五：中国服装工艺。</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:11.25pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">6、讲座六：中国民乐。</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:84.98pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '32', '留学生培训', '3', '2012-05-31 15:41:57', null, null, '2', null, null, '2011年韩国大邱留学生短期进修课程介绍', '1', null, '1');
INSERT INTO `ws_information` VALUES ('206', null, '<div>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:14pt;font-weight:bold;\">关于召开韩国交流生接待协调会的通知</span><span style=\"font-family:\'宋体\';color:#333333;font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';font-size:10pt;\">来源：党院办公室<span>&nbsp;&nbsp;</span><span>时间：</span><span>2011-12-20&nbsp;10:31</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">为保证韩国大邱工业大学交流生活动规范、有序、有质地进行，定于<span>12</span><span>月</span><span>20</span><span>号（周二）下午三点于行政楼四楼会议室召开协调会，请韩国交流生接待工作领导小组成员准时参加：</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　常务副组长：杨&nbsp;威</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　成员：（以姓氏笔画排序）</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　王&nbsp;苹、史&nbsp;行、张章忠、郑禄红、罗润来、黄&nbsp;炜</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　下设办公室：</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　办公室主任：史&nbsp;行</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　工作秘书：陈&nbsp;琰</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　成员：张丽丽、王锡金、赵红红、王国海、张苗、夏红飞</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span><a href=\"http://www.zjff.net/notice_show.php?id=36781\"><span style=\"font-family:\'Times New Roman\';color:#0000ff;font-size:10pt;text-decoration:underline;\">http://www.zjff.net/notice_show.php?id=36781</span></a></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '32', '留学生培训', '3', '2012-05-31 15:46:01', null, null, '2', null, null, '关于召开韩国交流生接待协调会的通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('207', '', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;font-weight:bold;\">韩国大邱<span>20</span><span>名大学生抵我院将展开短期游学培训</span></span><span style=\"font-family:\'宋体\';color:#333333;font-size:12pt;font-weight:bold;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">上传：宣传部&nbsp;郑斌<span>&nbsp;&nbsp;</span><span>时间：</span><span>11</span><span>年</span><span>12</span><span>月</span><span>28</span><span>日</span></span><span style=\"font-family:\'宋体\';color:#333333;font-size:12pt;font-weight:bold;\"></span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span><img alt=\"\" src=\"/attached/image/20120531/20120531154411_166.jpg\" width=\"501\" height=\"333\" /></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">图为韩国大学生对着纸条在学中文</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">当中国学生争先追逐<span>“</span><span>韩流</span><span>”</span><span>时，韩国学生也对中国的语言和传统文化产生了浓厚的兴趣。</span><span>12</span><span>月</span><span>28</span><span>日，韩国大邱工业大学的</span><span>22</span><span>名师生来到我校，将在这里进行为期两周的中国语及中国文化体验学习。今天上午交流会上，两国的两所学校的四十多名学生代表进行了轻松愉快的交流，分享两国文化、生活体会和经验。&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　我校的学生，尽显东道主的热情，主动跟友好学校学生结成一对一辅导小组，帮助他们提高汉语言能力和了解中国文化的方方面面。同时，我校学生也<span>“</span><span>零距离</span><span>”</span><span>地感受到了韩国学生的勤奋、谦逊。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　在交流会上，中文稍好点的韩国高年级学生时不时<span>“</span><span>秀</span><span>”</span><span>出一些幽默、搞笑的用语，惹得大家哈哈大笑；中文稍逊的学生也努力争取更多的交流机会，认真的样子让人忍俊不禁。这些韩国学生都非常珍惜这次交流机会，希望得到更多国内书本以外的收获。短短一小时交流以后，中韩学生彼此依依不舍，互留联系方式，并合影留恋。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　据悉，我校与韩国大邱工业大学在<span>2011</span><span>年</span><span>1</span><span>月就建立了友好关系。今年</span><span>1</span><span>月</span><span>21</span><span>日，双方就两校开展师生交流、短期培训、科研合作等签署了两校合作交流协议书。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　韩国大邱工业大学国际交流处金明松教授介绍说，此次<span>20</span><span>名同学组成的游学团队</span><span>,</span><span>分别来自眼镜光学系、人物形象设计学部、建筑设计消防安全系、汽车系等专业，将在我校进行半个月的短期游学，包括汉语培训及游览历史名胜古迹。他希望，这一系列的学习和活动，能加深韩国学生对汉语和中国文化的了解，促进中韩学校的友好往来和交流。</span></span><span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\">\r\n		<span style=\"font-family:\'楷体_GB2312\';color:#333333;font-size:10pt;\">相关链接</span><span style=\"font-family:\'楷体_GB2312\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'楷体_GB2312\';color:#333333;font-size:10pt;\">　　<span>“</span><span>游学</span><span>”</span><span>，也叫</span><span>“</span><span>修学旅游</span><span>”</span><span>，最早起源于英国，距今已有一百多年历史。游学本意指在校学生学习期间，为了配合课堂及书本所学，弥补其不足而开展的以丰富知识、增长见闻、扩大视野、培养素质、增进交流以及学科实践等为目的的旅行活动。主要对象为大中专学生和中学生，重点突出</span><span>“</span><span>学</span><span>”</span><span>，一般以某一专题为目标，或考察风俗文化，或了解一门学科，或学习一种语言，或参观高等院校科研院所。它要求参加者对所学内容有一定了解，活动完毕要写出心得体会。在旅游内容、线路设计上围绕学习内容来组织，突出专业性、知识性和趣味性，使学生在旅游中学到的知识更系统，收获也比传统的夏令营更大。在发达国家，学生有组织、有计划地出游的修学旅游十分盛行，并被认为是素质教育的一个重要组成部分。</span></span><span style=\"font-family:\'楷体_GB2312\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\" align=\"center\">\r\n		<span><img alt=\"\" src=\"/attached/image/20120531/20120531154420_723.jpg\" width=\"501\" height=\"322\" /></span>\r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\">\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">韩国学生<span>“</span><span>秀</span><span>”</span><span>出一些幽默、搞笑的用语，惹得大家哈哈大笑</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\" align=\"center\">\r\n		<span><img alt=\"\" src=\"/attached/image/20120531/20120531154427_375.jpg\" width=\"501\" height=\"333\" /></span>\r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\">\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">图为韩国大学生蔡姬<span>(</span><span>中</span><span>)</span><span>在向我院学生请教汉语</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\" align=\"center\">\r\n		<span><img alt=\"\" src=\"/attached/image/20120531/20120531154433_543.jpg\" width=\"501\" height=\"320\" /></span>\r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\">\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">图为韩国大学生刘斌娜<span>(</span><span>左</span><span>)</span><span>与浙纺服院章思思同学在合影留念</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:19.5pt;\">\r\n		<span style=\"font-family:\'Times New Roman\';font-size:10.5pt;\">http://www.zjff.net/news_show.php?id=11181</span><span style=\"font-family:\'Times New Roman\';font-size:10.5pt;\"></span> \r\n	</p>\r\n</div>', '', '2012-05-31', 'admin', '管理员', '', '3', '32', '留学生培训', '3', '2012-05-31 15:46:01', '', '', '2', '', null, '韩国大邱20名大学生抵我院将展开短期游学培训', '2', '/resourcefiles/thumbnail/7f1f652f6788c25cd60ed8d32c871890.jpg', '1');
INSERT INTO `ws_information` VALUES ('208', '', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:14pt;font-weight:bold;\">人文学院为韩国学生量身定制汉语教学课程</span><span style=\"font-family:\'宋体\';color:#333333;font-size:14pt;font-weight:bold;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';font-size:10pt;\">上传：人文学院&nbsp;郑晓<span>&nbsp;&nbsp;</span><span>时间：</span><span>11</span><span>年</span><span>12</span><span>月</span><span>30</span><span>日</span></span><span style=\"font-family:\'宋体\';font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">12<span>月</span><span>29</span><span>日上午</span><span>10</span><span>点，</span><span>20</span><span>个从外型看与中国学生区别不大的韩国学生准时出现在学院</span><span>4</span><span>号楼</span><span>1</span><span>楼服饰陈列搭配室，从今天起，这批来自韩国大邱工业大学的学生将在这里开展为期两周的汉语及中国文化体验学习活动。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　为能让韩国学生在短期内学习好《实用汉语》并感知中国文化，人文学院自承接了这项<span>“</span><span>负责韩国学生的汉语教学与文化讲座的师资、教材、教学时间安排</span><span>”</span><span>的工作任务后，首先由院长王苹、书记丁丽君牵头策划了韩国学生学习体验活动的可行性方案，接着，根据既定方案内容逐一落实师资和教学时间、地点的安排；为保证良好的教学质量，提高韩国学生学习汉语、了解中国文化的兴趣，人文学院又多方验证韩国学生求学兴趣点，并及时修补培训方案；在此基础上，又积极与培训教师们沟通交流，充分了解、掌握教师们的教学设想，争取以最完满的教学形式和教学内容给予韩国学生更多的汉语学习乐趣和中国文化感受，以此达到培训目的。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　值得一提的是，为保证教学效果，在人文学院确定相关师资后，在与教师们作一一沟通时，教师们都能从学校发展的大局出发，没有一个因为任务的特殊性而畏难或推馁的，即使是在得知需要利用元旦假期来给韩国学生授课或开讲座的，他们也都是一口承应，绝无二话。因此，在教师们大力支持和配合下，人文学院量身定制的韩国留学生短期培训<span>“</span><span>菜单</span><span>”</span><span>顺利生成了：这里有七个上午的《实用汉语》教学安排，由人文学院选派具有对外汉语教学资格和教学经验的罗佳炜、罗技科、陆晓楠三位老师给学生授课，教师们在通过集体备课、自编讲义的教学准备后，设置了见面介绍、问路、打电话、在机场、在商场、在饭店七个实用场景给学生讲练汉语读音和基本句型；而六个下午的讲座内容则分别是周震豪老师的《中国武术文化》、葛晓鸿老师的《中国国画艺术》、羊元高老师的《中国书法艺术》、王国海老师的《中国风景名胜摄影》、周俊飞老师的《中国服装工艺》及陈锡林老师的《中国民乐表演》。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;\">　　据随行的韩国大邱工业大学国际交流处金明松教授介绍，这<span>20</span><span>个学生都是该校的大一学生，因出于对中国文化生活的兴趣，自愿报名参加本次游学活动。他们基本上是第一次踏上中国的国土，且绝大多数学生汉语的</span><span>“</span><span>读写听说</span><span>”</span><span>状态尚处于初级阶段，因此是无法准确、流畅地与中国人沟通交流的，但这些障碍都无损于他们学习汉语、了解中国文化的热情。金教授希望通过我院精心定制和安排的一系列学习和活动内容，能进一步加深韩国学生对汉语和中国文化的印象，促进</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">他们自觉参与汉语推广工作，从而更大范围、更深层次地推进中韩两校间的合作与交流。&nbsp;</span><span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;text-indent:20pt;\">\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\" align=\"center\">\r\n		<span><img alt=\"\" src=\"/attached/image/20120531/20120531154519_451.jpg\" width=\"501\" height=\"376\" /></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">罗技科老师在上汉语课</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\" align=\"center\">\r\n		<span><img alt=\"\" src=\"/attached/image/20120531/20120531154527_601.jpg\" width=\"501\" height=\"376\" /></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">周震豪老师在示范太极拳动作</span><span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:right;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">撰稿：郑晓</span><span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">阅读次数：<span>284</span></span><span style=\"font-family:\'宋体\';color:#666666;font-size:10pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'ˎ̥\';font-size:12pt;\">http://www.zjff.net/news_show.php?id=11200</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n	</p>\r\n	<p style=\"text-align:right;\">\r\n		<span style=\"font-family:\'宋体\';color:#666666;font-size:10pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';color:#666666;font-size:10pt;\"></span>\r\n	</p>\r\n</div>', '', '2012-05-31', 'admin', '管理员', '', '5', '32', '留学生培训', '3', '2012-05-31 15:46:01', '', '', '2', '', null, '人文学院为韩国学生量身定制汉语教学课程', '2', '/resourcefiles/thumbnail/725f00f175d95677143cfb1b9523bb04.jpg', '1');
INSERT INTO `ws_information` VALUES ('209', '', '<div>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">浙纺服院政办[2011]25号</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:18pt;font-weight:bold;\">浙江纺织服装职业技术学院办公室关于</span><span style=\"font-family:\'宋体\';font-size:18pt;font-weight:bold;\"></span> \r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:18pt;font-weight:bold;\">成立2011年韩国交流生接待领导工作小组的通知</span><span style=\"font-family:\'宋体\';font-size:18pt;font-weight:bold;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">为促进中韩交流，增进两校之间的交流与合作；同时，更好地保证交流活动规范、有序、有质地进行，特此成立韩国交流生接待工作领导小组。</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">组长：王梅珍&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">常务副组长：杨&nbsp;威</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">成员：（以姓氏笔画排序）</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">王&nbsp;苹、史&nbsp;行、张章忠、郑禄红、罗润来、黄&nbsp;炜、梁&nbsp;惠</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">下设办公室：</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">办公室主任：史&nbsp;行</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">工作秘书：陈&nbsp;琰</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">成员：张丽丽、王锡金、赵红红、王国海、刘敏</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">各部门职责：</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">外事办：负责接待方案制定，与韩方以及人文学院、艺术与设计学院、学院办公室、后勤公司、旅游公司的总体协调联络。</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">财务处：负责接受韩方付款，以及各项费用的支出。</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">人文学院：负责韩方学生的汉语教学与文化讲座的师资、教材、教学时间安排。</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">艺术与设计学院：负责韩方学生日常管理，教学场地安排，中韩师生互动活动。</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">后勤公司：负责韩方师生的住宿、卫生打扫、餐饮。</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">保卫处：负责韩方师生在校期间的安全。</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:248pt;\" align=\"right\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">二O一一年十二月一日</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'黑体\';font-size:16pt;\">主题词：外事&nbsp;&nbsp;临时机构&nbsp;&nbsp;成立&nbsp;&nbsp;通知</span><span style=\"font-family:\'黑体\';font-size:16pt;\"></span> \r\n	</p>\r\n	<table style=\"border-collapse:collapse;\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" width=\"576\">\r\n					<p>\r\n						<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">浙江纺织服装职业技术学院办公室&nbsp;&nbsp;&nbsp;2011年12月5日印发</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>\r\n	</p>\r\n</div>', '', '2012-05-31', 'admin', '管理员', '', '5', '32', '留学生培训', '3', '2012-05-31 15:46:01', '', '', '2', '', null, '学院成立2011年韩国交流生接待领导工作小组的通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('210', null, '<div>\r\n	<p>\r\n		<span><a href=\"http://www.zjff.net/notice_show.php?id=34523\"><span style=\"font-family:\'Times New Roman\';color:#575644;font-size:10pt;text-decoration:none;\">http://www.zjff.net/notice_show.php?id=34523</span></a></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<table style=\"border-collapse:collapse;\" align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;font-weight:bold;\">2010<span>年下半年学生第一期普通话培训测试报名的通知</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">来源：人文学院<span>&nbsp;&nbsp;</span><span>时间：</span><span>2010-09-26&nbsp;10:48&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">各分院：</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　为了提高纺织服装学院学生的普通话水平，帮助学生顺利通过国家普通话水平测试等级考试，获得相应的资格证书，经与宁波大学高校测试点协商，特在浙江纺织服装学院成立普通话水平测试培训点，培训纺织服装学院和工程学院的学生。<span>2010</span><span>年下半年测试在即，培训测试通知具体如下：</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　一、测试</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>1.</span><span>测试对象</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　所有想要报名参加测试、获得证书的学生。</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>2.</span><span>测试内容、形式</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　普通话水平测试采用口试的形式进行，内容包括四项：（<span>1</span><span>）读</span><span>100</span><span>个单音节字；（</span><span>2</span><span>）读</span><span>50</span><span>个多音节词；（</span><span>3</span><span>）朗读一篇课文；（</span><span>4</span><span>）说</span><span>3</span><span>分钟话。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>3</span><span>．测试地点、时间</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　地点：宁波大学</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　时间：初步定于<span>2010</span><span>年</span><span>10</span><span>月和</span><span>11</span><span>月的周末（暂定为</span><span>10</span><span>月</span><span>30</span><span>、</span><span>31</span><span>，</span><span>11</span><span>月</span><span>6</span><span>、</span><span>7</span><span>、</span><span>13</span><span>、</span><span>14</span><span>、</span><span>21</span><span>日，具体时间以准考证上公布的为准）</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>3.</span><span>合格标准</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　师范类专业的合格标准为二级甲等（<span>87</span><span>分）及以上，其他专业的合格标准为二级乙等（</span><span>80</span><span>分）及以上。测试成绩在</span><span>70</span><span>分以上的考生，可以获得三级甲等普通话水平测试等级证书。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>4.</span><span>收费标准</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　根据省物价局、省财政厅浙价费〔<span>2001</span><span>〕</span><span>74</span><span>号文件《关于调整普通话水平测试收费标准的复函》规定，学生测试费</span><span>30</span><span>元</span><span>/</span><span>人次。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　二、培训（凡报名参加测试者均需参加培训）</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>1</span><span>．培训满</span><span>35</span><span>人开班；</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>2</span><span>．培训共</span><span>24</span><span>个课时，每天</span><span>8</span><span>课时，分别为</span><span>10</span><span>月</span><span>23</span><span>日、</span><span>30</span><span>日、</span><span>11</span><span>月</span><span>6</span><span>日，具体时间为：上午</span><span>8</span><span>：</span><span>30——11</span><span>：</span><span>30</span><span>，下午</span><span>13</span><span>：</span><span>00——16</span><span>：</span><span>00</span><span>；</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>3</span><span>．培训地点在浙江纺织服装学院教学楼，具体教室另行通知；</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>4</span><span>．培训费：根据宁波市物价局甬价费〔</span><span>2004</span><span>〕</span><span>246</span><span>号《关于核定普通话培训收费标准的复函》规定，普通话培训收费标准为每人每期</span><span>100</span><span>元，培训</span><span>24</span><span>课时，教材请自行准备。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　三、报名办法</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　报名须以分院为单位，采用集体报名的方式。</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>1.</span><span>测试、培训报名要求</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　分院按以下结构建立一个<span>EXCEL</span><span>表：序号、学号、姓名、性别、身份证号码、班级、所在学院，以班级为单位按学号顺序输入计算机，然后输出考生名单审核并进行公示（见附件），而后将报名表电子稿和打印稿各一份交到人文学院张玉老师处。（电子稿发送到邮箱：</span></span><span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"><a href=\"mailto:yzhang208@zjff,net\">yzhang208@zjff,net</a></span>）<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>2</span><span>．由于普通话水平测试等级证书不得随意更改数据，请各单位报名时对上报信息务必认真校对，以免发生错报及漏报现象，对于因字库不全而无法打印的姓名请在上报的考生名单上予以注明。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　四、学生报名日期</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　即日起至<span>10</span><span>月</span><span>19</span><span>日下午</span><span>4</span><span>：</span><span>30</span><span>。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　五、参考资料</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　《普通话学习与测试教程》（<span>2005</span><span>年</span><span>1</span><span>月出版，黑龙江人民出版社）或《普通话培训测试指南》（</span><span>2004</span><span>年</span><span>9</span><span>月第二版，浙江大学出版社）。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　注：本次报名不收照片，参加测试时由考生自己上交，要求：近期免冠一寸证件照一张（大头贴以及盖过钢印的照片均无效），并在照片后面写上学号、学院、姓名。参加测试时须带考试证（校园卡）、准考证和一寸证件照片，无证者不予参加测试。&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">附件下载</span><span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"><a href=\"http://www.zjff.net/uploads/notice/file/2010_09/26/104836.xls\">浙江纺织服装学院学生普通话培训报名表<span>.xls</span></a></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:right;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">阅读次数：<span>561&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '34', '最新资讯', '3', '2012-05-31 15:55:54', null, null, '2', null, null, '第一期普通话培训测试报名通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('211', null, '<div>\r\n	<p>\r\n		<span><a href=\"http://www.zjff.net/notice_show.php?id=35149\"><span style=\"font-family:\'Times New Roman\';color:#575644;font-size:10pt;text-decoration:none;\">http://www.zjff.net/notice_show.php?id=35149</span></a></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;font-weight:bold;\">2011<span>年上半年学生第二期普通话培训测试报名通知</span></span><span style=\"font-family:\'宋体\';color:#333333;font-size:12pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">来源：人文学院<span>&nbsp;&nbsp;</span><span>时间：</span><span>2011-02-27&nbsp;10:16</span></span><span style=\"font-family:\'宋体\';color:#666666;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';color:#666666;font-size:10pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:\'ˎ̥\';font-size:10pt;\">为了提高纺织服装学院学生的普通话水平，帮助学生顺利通过国家普通话水平测试等级考试，获得相应的资格证书，浙江纺织服装学院普通话水平测试培训点第二期培训测试开始报名，具体通知如下：</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　一、测试</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>1.</span><span>测试对象</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　所有想要报名参加测试、获得证书的学生。</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>2.</span><span>测试内容、形式</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　普通话水平测试采用口试的形式进行，内容包括四项：（<span>1</span><span>）读</span><span>100</span><span>个单音节字；（</span><span>2</span><span>）读</span><span>50</span><span>个多音节词；（</span><span>3</span><span>）朗读一篇课文；（</span><span>4</span><span>）说</span><span>3</span><span>分钟话。</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>3</span><span>．测试地点、时间</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　地点：宁波大学</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　时间：初步定于<span>2011</span><span>年</span><span>4</span><span>月</span><span>17</span><span>日左右，具体时间以准考证上公布的为准</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>3.</span><span>合格标准</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　师范类专业的合格标准为二级甲等（<span>87</span><span>分）及以上，其他专业的合格标准为二级乙等（</span><span>80</span><span>分）及以上。测试成绩在</span><span>70</span><span>分以上的考生，可以获得三级甲等普通话水平测试等级证书。</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>4.</span><span>收费标准</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　根据省物价局、省财政厅浙价费〔<span>2001</span><span>〕</span><span>74</span><span>号文件《关于调整普通话水平测试收费标准的复函》规定，学生测试费</span><span>30</span><span>元</span><span>/</span><span>人次。</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　二、培训（凡报名参加测试者均需参加培训）</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>1</span><span>．培训满</span><span>35</span><span>人开班；</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>2</span><span>．培训共</span><span>24</span><span>个课时，每天</span><span>8</span><span>课时，分别为</span><span>3</span><span>月</span><span>19</span><span>日、</span><span>3</span><span>月</span><span>26</span><span>日、</span><span>4</span><span>月</span><span>9</span><span>日，具体时间为：上午</span><span>8</span><span>：</span><span>30——11</span><span>：</span><span>30</span><span>，下午</span><span>13</span><span>：</span><span>00——16</span><span>：</span><span>00</span><span>；</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>3</span><span>．培训地点在浙江纺织服装学院教学楼，具体教室另行通知；</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>4</span><span>．培训费：根据宁波市物价局甬价费〔</span><span>2004</span><span>〕</span><span>246</span><span>号《关于核定普通话培训收费标准的复函》规定，普通话培训收费标准为每人每期</span><span>100</span><span>元，培训</span><span>24</span><span>课时，教材请自行准备。</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　三、报名办法</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　报名须以分院为单位，采用集体报名的方式。</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>1.</span><span>测试、培训报名要求</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　分院按以下结构建立一个<span>EXCEL</span><span>表：序号、学号、姓名、性别、身份证号码、班级、所在学院，以班级为单位按学号顺序输入计算机，然后输出考生名单审核并进行公示（见附件），而后将报名表电子稿和打印稿各一份交到人文学院张玉老师处。（电子稿发送到邮箱：</span></span><span><a href=\"mailto:yzhang208@zjff,net\"><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">yzhang208@zjff,net</span></a></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">）</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　<span>2</span><span>．由于普通话水平测试等级证书不得随意更改数据，请各单位报名时对上报信息务必认真校对，以免发生错报及漏报现象，对于因字库不全而无法打印的姓名请在上报的考生名单上予以注明。</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　四、学生报名日期</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　<span>2011</span><span>年</span><span>2</span><span>月</span><span>26</span><span>日至</span><span>3</span><span>月</span><span>12</span><span>日。</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　五、参考资料</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　《普通话学习与测试教程》（<span>2005</span><span>年</span><span>1</span><span>月出版，黑龙江人民出版社）或《普通话培训测试指南》（</span><span>2004</span><span>年</span><span>9</span><span>月第二版，浙江大学出版社）。</span></span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">　　注：本次报名不收照片，参加测试时由考生自己上交，要求：近期免冠一寸证件照一张（大头贴以及盖过钢印的照片均无效），并在照片后面写上学号、学院、姓名。参加测试时须带考试证（校园卡）、准考证和一寸证件照片，无证者不予参加测试。&nbsp;</span><span style=\"font-family:\'ˎ̥\';font-size:10pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'ˎ̥\';font-size:10pt;\">附件下载</span><span><a href=\"http://www.zjff.net/uploads/notice/file/2011_02/27/101622.xls\"><span style=\"font-family:\'ˎ̥\';font-size:10pt;\">浙江纺织服装学院学生普通话培训报名表<span>(</span><span>空白）</span><span>.xls</span></span></a></span><span style=\"font-family:\'宋体\';font-size:10pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '34', '最新资讯', '3', '2012-05-31 15:55:54', null, null, '2', null, null, '第二期普通话培训测试报名通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('212', null, '<div>\r\n	<p>\r\n		<span><a href=\"http://www.zjff.net/notice_show.php?id=35322\"><span style=\"font-family:\'Times New Roman\';color:#575644;font-size:10pt;text-decoration:none;\">http://www.zjff.net/notice_show.php?id=35322</span></a></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;font-weight:bold;\">普通话培训班上课通知</span><span style=\"font-family:\'宋体\';color:#333333;font-size:12pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">来源：人文学院<span>&nbsp;&nbsp;</span><span>时间：</span><span>2011-03-24&nbsp;13:45</span></span><span style=\"font-family:\'宋体\';color:#666666;font-size:10pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">已报名参加普通话培训的同学请注意，本周六（<span>3</span><span>月</span><span>26</span><span>日）开始上课。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　地点：<span>14</span><span>号楼</span><span>310</span><span>。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　时间：早上<span>8:30</span><span>开始。</span></span><span style=\"font-family:\'宋体\';color:#666666;font-size:10pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '2', '34', '最新资讯', '3', '2012-05-31 15:55:54', null, null, '2', null, null, '第二期普通话培训上课通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('213', null, '<div>\r\n	<p>\r\n		<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">http://www.zjff.net/notice_show.php?id=36151</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<table style=\"border-collapse:collapse;\" align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:12pt;font-weight:bold;\">2011<span>年下半年学生第三期普通话培训测试报名的通知</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">来源：人文学院<span>&nbsp;&nbsp;</span><span>时间：</span><span>2011-09-27&nbsp;09:12&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">各分院：</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　为了提高纺织服装学院学生的普通话水平，帮助学生顺利通过国家普通话水平测试等级考试，获得相应的资格证书，浙江纺织服装学院普通话水平测试培训点第三期培训测试开始报名，具体通知如下：</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　一、测试</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>1.</span><span>测试对象</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　所有想要报名参加测试、获得证书的学生。</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>2.</span><span>测试内容、形式</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　普通话水平测试采用口试的形式进行，内容包括四项：（<span>1</span><span>）读</span><span>100</span><span>个单音节字；（</span><span>2</span><span>）读</span><span>50</span><span>个多音节词；（</span><span>3</span><span>）朗读一篇课文；（</span><span>4</span><span>）说</span><span>3</span><span>分钟话。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>3</span><span>．测试地点、时间</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　地点：宁波大学</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　时间：初步定于<span>2011</span><span>年</span><span>10</span><span>月底或</span><span>11</span><span>月初，具体时间以准考证上公布的为准</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>3.</span><span>合格标准</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　师范类专业的合格标准为二级甲等（<span>87</span><span>分）及以上，其他专业的合格标准为二级乙等（</span><span>80</span><span>分）及以上。测试成绩在</span><span>70</span><span>分以上的考生，可以获得三级甲等普通话水平测试等级证书。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>4.</span><span>收费标准</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　根据省物价局、省财政厅浙价费〔<span>2001</span><span>〕</span><span>74</span><span>号文件《关于调整普通话水平测试收费标准的复函》规定，学生测试费</span><span>30</span><span>元</span><span>/</span><span>人次。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　二、培训（凡报名参加测试者均需参加培训）</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>1</span><span>．培训满</span><span>35</span><span>人开班；</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>2</span><span>．培训共</span><span>24</span><span>个课时，每天</span><span>8</span><span>课时，分别为</span><span>10</span><span>月</span><span>16</span><span>日、</span><span>10</span><span>月</span><span>22</span><span>日、</span><span>10</span><span>月</span><span>29</span><span>日，具体时间为：上午</span><span>8</span><span>：</span><span>30——11</span><span>：</span><span>30</span><span>，下午</span><span>13</span><span>：</span><span>00——16</span><span>：</span><span>00</span><span>；</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>3</span><span>．培训地点在浙江纺织服装学院教学楼，具体教室另行通知；</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>4</span><span>．培训费：根据宁波市物价局甬价费〔</span><span>2004</span><span>〕</span><span>246</span><span>号《关于核定普通话培训收费标准的复函》规定，普通话培训收费标准为每人每期</span><span>100</span><span>元，培训</span><span>24</span><span>课时，教材请自行准备。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　三、报名办法</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　报名须以分院为单位，采用集体报名的方式。</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>1.</span><span>测试、培训报名要求</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　分院按以下结构建立一个<span>EXCEL</span><span>表：序号、学号、姓名、性别、身份证号码、班级、所在学院，以班级为单位按学号顺序输入计算机，然后输出考生名单审核并进行公示（见附件），而后将报名表电子稿和打印稿各一份交到人文学院张玉老师处。（电子稿发送到邮箱：</span></span><span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"><a href=\"mailto:yzhang208@zjff,net\">yzhang208@zjff,net</a></span>）<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>2</span><span>．由于普通话水平测试等级证书不得随意更改数据，请各单位报名时对上报信息务必认真校对，以免发生错报及漏报现象，对于因字库不全而无法打印的姓名请在上报的考生名单上予以注明。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　四、学生报名日期</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　<span>2011</span><span>年</span><span>9</span><span>月</span><span>5</span><span>日至</span><span>10</span><span>月</span><span>11</span><span>日。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　五、参考资料</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　《普通话学习与测试教程》（<span>2005</span><span>年</span><span>1</span><span>月出版，黑龙江人民出版社）或《普通话培训测试指南》（</span><span>2004</span><span>年</span><span>9</span><span>月第二版，浙江大学出版社）。</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　注：本次报名不收照片，参加测试时由考生自己上交，要求：近期免冠一寸证件照一张（大头贴以及盖过钢印的照片均无效），并在照片后面写上学号、学院、姓名。参加测试时须带考试证（校园卡）、准考证和一寸证件照片，无证者不予参加测试。　　</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">　　&nbsp;</span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\">附件下载</span><span style=\"font-family:\'宋体\';color:#333333;font-size:10pt;\"><a href=\"http://www.zjff.net/uploads/notice/file/2011_09/05/091213.xls\">浙江纺织服装学院学生普通话培训报名表<span>(</span><span>空白）</span><span>.xls</span></a></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:left;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#333333;font-size:8pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign=\"middle\" width=\"560\">\r\n					<p style=\"text-align:right;\">\r\n						<span style=\"font-family:\'ˎ̥\';color:#666666;font-size:10pt;\">阅读次数：<span>451&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';color:#333333;font-size:10pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '34', '最新资讯', '3', '2012-05-31 15:55:54', null, null, '2', null, null, '第三期普通话培训测试报名通知', '1', null, '1');
INSERT INTO `ws_information` VALUES ('214', null, '<p align=\"center\">\r\n	<a href=\"/attached/file/20120531/20120531155707_853.xls\" target=\"_blank\">学生普通话培训报名表(2011.10）.xls</a>\r\n</p>', null, '2012-05-31', 'admin', '管理员', null, '5', '34', '最新资讯', '3', '2012-05-31 15:59:55', null, null, '2', null, null, '学生普通话培训报名表(2011.10）', '1', null, '1');
INSERT INTO `ws_information` VALUES ('215', 'annex/07d5e012830176ccb4baa007d119b0db', '', 'application/x-zip-compressed', '2012-05-31', 'admin', '管理员', '普通话成绩 10.11.zip', '2', '34', '最新资讯', '3', '2012-05-31 15:59:55', null, null, '2', null, null, '普通话成绩 10.11', '1', null, '1');
INSERT INTO `ws_information` VALUES ('216', null, '<div>\r\n	<p style=\"text-indent:20.58pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:13.5pt;font-weight:bold;\">王苹，女，&nbsp;1963&nbsp;年出生，浙江绍兴人。毕业于北京师范大学中文系。原为宁波大学人文与传媒学院教授，&nbsp;硕士生导师，2010年&nbsp;4&nbsp;月调入浙江纺织学院人文分院，任院长。为国家级普通话水平测试员，&nbsp;华东修辞学会、浙江省语言学会会员。&nbsp;主要从事现代汉语教学与研究，&nbsp;开设过“现代汉语”、“形式逻辑”、“修辞研究”、“汉语通论”、“大学生口才与交际”、“教师教学语言”、“普通话学习与考级”等专业课与选修课，教学效果优秀，深受学生欢迎。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:13.5pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span><img alt=\"\" src=\"/attached/image/20120531/20120531160307_423.jpg\" width=\"652\" height=\"492\" /></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'Verdana\';color:#565656;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span><img alt=\"\" src=\"/attached/image/20120531/20120531160314_528.jpg\" width=\"423\" height=\"568\" /></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '35', '教师介绍', '3', '2012-05-31 16:03:27', null, null, '2', null, null, '王苹', '2', '/resourcefiles/thumbnail/d85df0b5c1e924770b98d0e0837fddac.jpg', '1');
INSERT INTO `ws_information` VALUES ('217', 'annex/5d931b8c3b91d70e829b61067918d5a5', '', 'application/x-zip-compressed', '2012-05-31', 'admin', '管理员', '汉语拼音发音示范全集.zip', '2', '37', '课程资料', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '汉语拼音发音示范全集', '1', null, '1');
INSERT INTO `ws_information` VALUES ('218', '', '<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;font-weight:bold;\">普通话的由来</span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;font-weight:bold;\"><br />\r\n</span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;font-weight:bold;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#000000;font-size:10.5pt;\">尹鸿祝&nbsp;吕诺</span><span style=\"font-family:\'宋体\';color:#000000;font-size:9pt;\"></span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:24pt;\">\r\n	<span style=\"font-family:\'ˎ̥\';color:#000000;font-size:12pt;\">&nbsp;\"<span>普通话</span><span>\"</span><span>一词，是朱文熊于１９０６年首次提出的，后来瞿秋白等也曾提出</span><span>\"</span><span>普通话</span><span>\"</span><span>的说法，并与茅盾就普通话的实际所指展开争论。经</span><span>\"</span><span>五四</span><span>\"</span><span>以来的白话文运动、大众语运动和国语运动，北京语音的地位得到确立并巩固下来。　</span></span><span style=\"font-family:\'ˎ̥\';color:#000000;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#000000;font-size:12pt;\">　　新中国成立后，１９５５年举行的<span>\"</span><span>全国文字改革会议</span><span>\"</span><span>上，张奚若在大会主题报告中说明：汉民族共同语早已存在，现在定名为普通话，需进一步规范，确定标准。</span><span>\"</span><span>这种事实上已经逐渐形成的汉民族共同语是什么呢？这就是以北方话为基础方言，以北京语音为标准音的普通话。</span><span>\"\"</span><span>为简便起见，这种民族共同语也可以就叫普通话。</span><span>\"</span></span><span style=\"font-family:\'ˎ̥\';color:#000000;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';color:#000000;font-size:12pt;\">　　１９５６年２月６日，国务院发布的《关于推广普通话的指示》中，对普通话的含义作了增补和完善，正式确定普通话<span>\"</span><span>以北京语音为标准音，以北方话为基础方言，以典范的现代白话文著作为语法规范</span><span>\"</span><span>。</span><span>\"</span><span>普通话</span><span>\"</span><span>一词开始以明确的内涵被广泛应用。　</span></span><span style=\"font-family:\'ˎ̥\';color:#000000;font-size:12pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:325.5pt;\" align=\"right\">\r\n	<span style=\"font-family:\'ˎ̥\';color:#000000;font-size:10.5pt;\">（摘自新华网）</span><span style=\"font-family:\'ˎ̥\';color:#000000;font-size:10.5pt;\">\r\n</p>\r\n</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>', '', '2012-05-31', 'admin', '管理员', '', '4', '38', '语文知识', '3', '2012-05-31 16:09:36', '', '', '2', '', null, '普通话的由来', '1', null, '1');
INSERT INTO `ws_information` VALUES ('219', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\">普通话相关知识问答&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1．&nbsp;国家语委规定，什么时间实现普通话在全国初步普及、汉字社会应用基本规范的目标？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：2010年</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">2．《教师资格条例实施办法》中对教师普通话等级标准的规定是什么？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：各级各类学校和幼儿园及其他教育机构的教师应不低于二级乙等，其中语文教师和对外汉语教师不低于二级甲等，语音教师不低于一级乙等。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">3．&nbsp;学校语言文字工作的“三纳入一渗透”的内容是什么？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：把提高学生语言文字规范意识和语言文字应用能力的要求纳入各级各类学校的培养目标和有关课程标准，纳入教育教学和学生技能训练的基本内容，纳入学校工作日程和常规管理，渗透到德育、智育、体育、美育和社会实践等教育活动中。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">4．&nbsp;推广普通话的十二字方针是什么？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：“大力推行、积极普及、逐步提高”。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">5．&nbsp;什么是普通话水平测试？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答:&nbsp;普通话水平测试是应试人运用普通话所达到的标准程度的检测和评定，是应试人的汉语标准语测试。应试人在运用普通话口语进行表达过程中所表现的语音、词汇、语法规范程度，是评定其所达到的水平等级的重要依据。普通话水平测试一律采用口语方式进行。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">6．&nbsp;现阶段，普通话水平测试的主要测试对象包括那些人？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：（1）教师和申请教师资格的人员；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（2）广播电台、电视台的播音员、节目主持人；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（3）影视话剧演员；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（4）国家机关工作人员；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（5）师范类专业、播音与主持艺术专业、影视话剧表演专业以及其他与口语表达密切相关专业的学生；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（6）行业主管部门规定的其他应该接受测试的人员。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">7．&nbsp;普通话等级证书由什么机构颁发？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：由省（自治区、直辖市）语言文字工作机构颁发。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">8．&nbsp;普通话水平的等级由什么机构认定？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：测试成绩的认定由省级语言文字工作机构认定，但其中一级甲等成绩必须在认定前由国家语委普通话培训测试中心复审，一级乙等成绩在认定前由省级普通话培训测试中心复审。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">9．&nbsp;普通话水平测试内容由几个测试项目构成？各占多少分?其目的是什么？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：四个测试项。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（1）、读单音节词100个，时限3分钟，占10分。目的考察应试人普通话声母、韵母和声调的发音。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（2）、读双音节词语50个，时限3分钟，占20分。目的是考察应试人声、韵、调的发音外，还要考察上声便调、儿化韵和轻声的读音。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（3）、400字短文朗读，限时4分钟，占30分。目的是考察应试人使用普通话朗读书面材料的能力，重点考察语音、语流音变、语调等。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（4）、说话，时间不少于3分钟，占40分。目的是考察应试人在无文字凭借的情况下说普通话所达到的规范程度。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">10．&nbsp;国务院规定什么时间为全国推广普通话宣传周？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：国务院决定自1998年起每年9月份第三周为全国推广普通话宣传周。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">11．&nbsp;新世纪推广普通话的工作目标是什么？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：2010年以前，普通话在全国范围内初步普及，交际中的方言隔阂基本消除；本世界中叶以前，普通话在全国范围内普及，交际中没有方言隔阂。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">12．&nbsp;什么叫普通话？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：“普通”是指普遍、共通的意思，普通话是全国通用的语言。普通话以北京语音为标准音，以北方方言为基础方言，以典范的现代白话文著作为语法规范。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">13．&nbsp;普通话有什么特点？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：普通话是优美、丰富的语言；语言方面，音节结构中元音占优势，每个音节都有声调，音节在汉语中占有重要地位，这些特点是普通话富有抑扬、和谐、悦耳的音乐美；词汇方面双音节占优势，构词法灵活多样，词汇丰富，能够反映纷繁的社会现象和表达细腻的思想感情；语法方面，各级语言单位的组合具有一致性，语序和虚词是最重要的两种组合手段，量词丰富，这些语法特点使现代汉语的表达容易做到生动丰富、简明准确。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">14．&nbsp;实现语言文字的规范化、标准化的意义？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：语言文字的运用是否合乎规范、标准，往往反映一个国家、一个民族的文明程度。语言文字是人们的交际工具，是信息的载体。加强语言文字的规范化、标准化，对社会的发展，科学的进步与交流和文化教育水平的提高，都有重要意义。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">15．&nbsp;北京话是普通话吗?普通话的标准是什么？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：北京话属于北方方言，但不是普通话。普通话是“以北方语言为标准音，以北方话为基础，以典范的现代白话文著作为语法规范”</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">16．&nbsp;为什么要推广普通话？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：国际推广全国通用的普通话，并不是要人为地消灭方言，主要是为了消除方言隔阂，减少不同方言区的人交流时的困难，有利于社会交往，有利于科学技术和管理经验的交流，有利于扩大商品流通和建立统一的国内市场，有利于普及教育和提高教育质量，还有利于国家的统一和民族的团结，有利于国际交往，有利于社会主义两个文明建设。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">17．&nbsp;制定《汉语拼音方案》的原则是什么？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：①国际化原则：字母采用国际通用的拉丁字母。②音素化原则：用音素来描写音节。③标准音原则：以北京音为标准音。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">18．《汉语拼音方案》是什么时间公布的？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：1958年2月11日第一届全国人民代表大会第五次会议通过并发布了《汉语拼音方案》。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">19．《汉语拼音方案》与汉字的关系？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：在今后相当长的时期，汉语作为国家的法定文字还要继续发挥它的作用。现行的《汉语拼音方案》不是代替汉字的拼音文字，它是帮助学习汉语、汉字和推广普通话的注音工具，并用语汉字不便使用或不能使用的方面。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">20．什么是《汉语拼音正词法》？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：汉语拼音正词法就是用《汉语拼音方案》拼写现代汉语的规则。它包括分词连写法、外来词语拼写法、人名地名拼写法、标调法、移行规则法等。为了适应特殊的要求，同时提出一些可供技术处理的变通方式。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">21．什么是四呼？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：按照韵母的韵头的情况，我国传统语言学将韵母分开口呼、齐齿呼、合口呼、撮口呼四类，简称四呼。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">开口呼——没有韵头，韵腹是a、o、e的韵母。发音时，嘴张得比较大，所以叫做开口呼；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">齐齿呼——韵头或韵腹是i（舌面元音）的韵母。发音时，嘴向两边开，露出牙齿，所以叫做齐齿呼；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">合口呼——韵头或韵腹是u的韵母。发音时，嘴唇向中间收缩，所以叫做合口呼。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">撮口呼——韵头或韵腹是ü的韵母。发音时，嘴唇是圆的，发音部位i与相同,它是i的圆唇化，所以叫做撮口呼。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">22.普通话语音的四声有什么特点？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：普通话语音的四声是指阴平、阳平、上声、去声四个声调。上声读sh?ng，是特定的读法。“阴”yīn、“阳”yáng、“上”sh?ng、“去”qù四个字，恰好代表了四个声调。普通话语音的声调有一个极大的特点，就是四个词类的调值有明显的区分：一平、二升、三曲、四降，不容易相混。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">23．怎样读好普通话的上声字？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：普通话四声的调值分别是55，35，214，51。这只是粗略的标记，实际读音要比这复杂，特别是上声字。上声开头有降，中间低平部分是主要的，末尾有短促的上升。不要把上声字简单看作降升调，说它基本上是个低平调更为准确。上声调值记为214已沿用多年，实际上应记为2114。读读下面这些上声字，体会一下上声字的特点：买、小、有、组。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">24．声调符号应该标记在哪儿？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：声调符号应该标在主要元音上。有一个口诀：‘a’出现别放过，没有a找o、e，i、u并立标在后，单字韵母不用说。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">25.什么是轻声？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：汉语有些词里的音节或有些句子里的词，念成又轻又短的调子，叫轻声，轻声的特点是轻短、模糊。如普通话里“桌子”的“子”，“家里”的“里”，“葡萄”的“萄”。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">26．什么叫儿化韵？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：普通话里有一些词带着辅助成分：“儿尾”，如：“花儿”、“鸟儿”等等。“儿”本来的独立的音节，但经过长期的极流利的连读，就产生了一种连音变化，两个音节融合为一个音节，“er”失去了独立性，保持了因卷舌动作而发出的十分短弱的卷舌音。这个卷舌音附加在它前面一个音节的韵母上，使原来的那个韵母有或多或少的变化。这种变化的韵母叫做“儿化韵”。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">27．怎样读准儿化韵？</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">答：韵母儿化，大致有两种情况。一种是虽然儿化了，但原韵母不变，只是在发该韵母的同时加上了卷舌动作就可以了。另一种是儿化后，原韵母发生变化，或者丢掉或者增加某些音素，同时加卷舌动作。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '38', '语文知识', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '普通话相关知识问答', '1', null, '1');
INSERT INTO `ws_information` VALUES ('220', '', '<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'宋体\';font-size:14pt;\">推广普通话就是不让说方言吗？</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:12pt;\">《中华人民共和国宪法》规定“国家推广全国通用的普通话”。推广普通话并不是不让说方言，这是一个很重要的语言政策。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;普通话是现代汉民族共同语，也是汉民族的标准语，是我国占主导地位的语言。推广普通话有利于社会主义现代化建设，有利于全国文化素质的提高。普通话是以北京话为基础发展起来的，由于政治、经济、文化等种种原因，经过几百年的历史，它的流通范围越来越大，影响也越来越大，逐步上升到民族共同语的地位，而方言只是处于从属地位的一方之言。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;推广普通话并不是要消灭方言，而是要在会说方言的基础上，还要会说民族共同语。推广普通话总的要求是在正式的场合和公众交际的场合讲普通话，但并不是排除在非正式场合讲方言。国家推广全国通用的普通话是有重点、有步骤地进行的，并不是一刀切，也不是所有场合一律不让说方言。以学校为例，首先要求语文课用普通话，然后提出各科教学都用普通话，使普通话成为教学语言，继而再要求普通话成为校园语言。即使是这样，也允许师生在校外的非正式场合讲方言。有人提出“在家也要说普通话，不能讲方言”。当然，在家里愿意讲普通话更好，可以更快地提高普通话水平，但是，我们并不强迫人们这样做。必须指出，这种不切实际的口号容易引起方言区人们的反感，并不利于推广普通话工作的顺利进行。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;另外，我们对方言也要有正确的认识。虽然方言是处于从属地位的一方之言，但它并不是低级的语言、落后的语言。语言没有优劣之分，有些人把方言看成是“县以下的人才说的”低级的、落后的语言，这是错误的。方言为一个地区的人们服务，能够很好地发挥日常交际的功能。人们对自己的家乡话有着深厚的感情，不加分析地贬低方言、排斥方言，会使方言区的人产生抵触情绪，对推广普通话工作极为不利。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;40年来的推广普通话工作，使各个方言已经发生变化，在语音、词汇方面都有向普通话靠拢的趋势，原先那种很纯正的方言几乎不存在了。同时普通话也不断地从方言中吸取营养，借以丰富自己。原来根本没有的一些其他方言中的词也进入了普通话，例如“垃圾”“过硬”“敲竹杠”等。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:220.5pt;\" align=\"right\">\r\n	<span style=\"font-family:\'宋体\';font-size:12pt;\">摘自《语言文字工作百问》</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>', '', '2012-05-31', 'admin', '管理员', '', '4', '38', '语文知识', '3', '2012-05-31 16:09:36', '', '', '2', '', null, '推广普通话就是不让说方言吗？', '1', null, '1');
INSERT INTO `ws_information` VALUES ('221', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'Times New Roman\';font-size:14pt;\">连央视都常用错的成语！你用对了吗？</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;1.七月流火：指天气逐渐凉爽起来。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;2.不足为训：不值得作为典范或法则。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;3.久假不归：长期借用不归还。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;4.侧目而视：意为敢怒不敢言，形容距今畏惧的样子。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;5.马革裹尸：用马皮将尸体包裹起来，形容英勇杀敌，战死疆场的&nbsp;无畏气概。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;6.危言危行：讲正直的话，做正直的事。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;7.万人空巷：并不是指街上空无一人，而是说人们都从巷子里出来来到大街上，街上人山人海，兄容欢迎、庆祝等盛况。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;8.目无全牛：技艺达到了极度纯熟的地步。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;9.望其项背：能够望见脖子和背，表示赶得上或比得上。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;10.火中取栗：受人利用冒了险，结果白吃苦头自己还没得到好处。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;11.炙手可热：比喻气焰很盛，权势很大。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;12.美轮美奂：形容房屋多而又高大华丽。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;13.如坐春风：在不知不觉中受到长辈或良师的恳切教诲。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;14.举案齐眉：形容夫妻之间想敬相爱，关系融洽。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;15.首当其冲：最先受到攻击和遭受灾难。&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;16.改头换面：比喻只改变形式，不改变内容。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '38', '语文知识', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '连央视都常用错的成语！你用对了吗？', '1', null, '1');
INSERT INTO `ws_information` VALUES ('222', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14pt;\">汉字有多少？什么是常用字、通用字？</span><span style=\"font-family:\'宋体\';font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">汉字的数量很多,但是到底有多少,恐怕没有人能说出它的准确数字。我们只能从古今字典中,知道它的大概数目。清朝的《康熙字典》收字47035个；当代的《汉语大字典》收字54678个,这是目前收字最多的字典。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;在社会生活中,不同的行业、部门,不同职业的人所需要的用字量是不同的；在学校教育的不同阶段,也需要规定不同量的识字标准。因此,汉字的字量标准就不能只有一个,而应该有不同的层次和类别。就用字来讲,首先应该区分出常用字和通用字。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;常用字就是经常使用的,阅读一般报刊书籍必须掌握的字。但“经常使用”只是一个模糊概念,因此在不同的历史时期,根据不同的需要,“常用字”概念的内涵也时有变化,即使是同样的内涵,其所收字与收字数量也会不同。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;通用字的范围比常用字大,它是指一个时期的出版印刷、辞书编纂和汉字信息处理所需要使用的汉字。现代汉语通用字则是指现代所需要使用的汉字,它不包括很生僻的、专门用于去汉语的用字,也不包括专业用字。根据对文字资料的统计,从商代到现代,一般使用的汉字数量没有显著的变化,可能一直在五六千左右。近年来制订的具有通用字表性质的汉字标准,基本上维持了这一数目。如《印刷通用汉字字形表》收字6196个,国家标准《信息交换用汉字编码字符表》收字6763个。根据近年来实际使用的情况,国家语委在1988年研制成的《现代汉语通用字表》,共收字7000个。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（语言文字周报2003年2月12日）</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '0', '38', '语文知识', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '汉字有多少？什么是常用字、通用字？', '1', null, '1');
INSERT INTO `ws_information` VALUES ('223', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:14pt;font-weight:bold;\">招牌上的字究竟属于书法艺术作品，还是一般的社会用字</span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:27.75pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">人们常常问道：招牌上的字属于书法作品，还是一般社会用字？我们的回答是：从总体上看应属后者。&nbsp;</span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:27.75pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">“<span>书法作品</span><span>”</span><span>与</span><span>“</span><span>社会用字</span><span>”</span><span>是两个不同的概念，不能混为一谈。从功能上盾，</span><span>“</span><span>书法</span><span>”</span><span>当属艺术创作，其主要目的和用途是供人鉴赏，具有装饰、美化作用，如挂在室内供人欣赏的条幅和国画上的字，参展的书法作品和作为艺术品收藏的篆刻作品，以及正式出版的书法作品专辑等。书法作为艺术，当然可以书写包括繁体字在内的各种字体，这是江总书记指示过而我们多年来在实际管理工作中也一贯坚持的政策和做法。但是，街头招牌则不能简单化地一概归入书法艺术范畴。因为招牌用字主要具有社会交际功能，都是面向广大社会公众、供人识记，用以说明是什么单位、卖什么东西的。它同主要用于鉴赏和装饰的书法艺术作品明显不同。比如一家书店招牌上是</span><span>“</span><span>中华儿童书店</span><span>”</span><span>六个字，其中</span><span>“</span><span>崋、兒、書</span><span>”</span><span>是繁体，这样写有人可能认为丰满，可是进入这家书店的主要是小学生，他们往往不认识那三个繁体字，只能猜。可见，滥用繁体字有时候就达不到交际的目的，效果自然不会好。当然，不可否认，某些写得既正确规范又美观漂亮的招牌，也具有一定的装饰、美化作用，也带有书法艺术的一些特色，但这绝不是其主要的功能。所以，我们主张招牌必须使用简化字，这不仅是规范化工作的需要，更是从便于群众认读来考虑，是符合社会经济文化生活要求的举措。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:27.75pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">&nbsp;<span>书法界有的先生不太同意按功能分类，提出城市里的招牌大多数是书法家所写，这些招牌上的汉字有一个规范化的问题；而对书汉家所写的招牌，则可以网开一面，不必去管。我们认为无论从政策要求还是具体管理看，这样做均不可行。汉字规范化是国家对全社会每一个成员的要求，招牌只准书法家写，不准别人写，实际做不到；对书法家网开一面，那么，不是书法家所写的招牌实际上也就难以管理了。另外，以是否书法家来划界，那谁是谁不是，有时也不大能说清楚。所以，这种以书法家来划界的提法，在管理上实际是无法操作的。我们认为，书法艺术主要不是体现在招牌的书写上，书法家要弘扬传统艺术，完全可以通过举办书法展览、出版书法专辑或在报刊上发表作品等来展现自己的艺术才干和风格。当今时代，书法家在艺术创作上有着广阔的天地，不一定只限于在招牌上来体现。当然，这个问题还可以进一步研究讨论。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:27.75pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">在招牌用字的管理上，国家主管部门认为应根据不同的字体提出不同的要求，对按印刷体书写的，应严格要求规范化，字形以《现代汉语通用字表》为准；对各种美术字体，要求在必须使用简化字的前提下，允许有笔形上的某些变化，但必须做到易于辨识；对和写体书写的招牌，其字形要求可以适当放宽，即在使用规范简体字的前提下，允许笔形有些变化，允许彩用碑刻、字贴上名家墨迹的写法。</span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '38', '语文知识', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '招牌上的字究竟属于书法艺术作品，还是一般的社会用字', '1', null, '1');
INSERT INTO `ws_information` VALUES ('224', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:14pt;\">&nbsp;&nbsp;</span><span style=\"font-family:\'Arial\';color:#000000;font-size:14pt;font-weight:bold;\">什么是规范汉字、现行规范汉字以什么作为依据&nbsp;</span><span style=\"font-family:\'Arial\';color:#000000;font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">所谓规范汉字，是指经过整理简化并由国家以字表形式正式公布的正体字、简化字和未经整理简化的传承字。&nbsp;</span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">简化字以<span>1986</span><span>年</span><span>10</span><span>月国务院批准重新发表的《简化字总表》中收录的简化字为准。《简化字总表》所收对照的繁体字法即为不规范字，一般在面向社会公众的场合中停止使用。例如</span><span>“</span><span>書</span><span>”</span><span>是</span><span>“</span><span>书</span><span>”</span><span>的繁体，</span><span>“</span><span>筆</span><span>”</span><span>是</span><span>“</span><span>笔</span><span>”</span><span>的繁体，</span><span>“</span><span>輕</span><span>”</span><span>是</span><span>“</span><span>轻</span><span>”</span><span>的繁体，</span><span>“</span><span>萬</span><span>”</span><span>是</span><span>“</span><span>万</span><span>”</span><span>的繁体，等等，都不再使用（翻印出版古籍、文物古迹、书法艺术作品除外）。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">&nbsp;<span>正体字以</span><span>1955</span><span>年文化部和中国文字改革委员会发布的《第一批异体字整理表》中选用的字为准（该表公布后又作了几次调整，一共恢复使用了</span><span>28</span><span>个被淘汰的异体字，即：阪、挫、晔、诓、雠、、邱、於、澹、骼、彷、菰、溷、徼、薰、黏、桉、愣、晖、凋等。所以，实际上被淘汰的异体字是</span><span>1027</span><span>个），与此相对的异体字不再使用。如：</span><span>“</span><span>牀</span><span>”</span><span>是</span><span>“</span><span>床</span><span>”</span><span>的异体，</span><span>“</span><span>慾</span><span>”</span><span>是</span><span>“</span><span>欲</span><span>”</span><span>的异体，等等，都不再使用。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">传承字是指历史上流传下来沿用至今，未经整理简化或不需要整理简化的字。如：人、刀、土、大、小、山、川、王、田、日、月等。</span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">规范汉字的字形以<span>1988</span><span>年国家语委和新闻出版署联合发布的《现代汉语通用字表》为准。与此相对的旧字形即为不规范的，不再使用。如</span><span>“</span><span>黙</span><span>”</span><span>是</span><span>“</span><span>默</span><span>”</span><span>的旧字形，等等，都不能再使用。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">&nbsp;<span>除上面所提的《简化字总表》、《第一批异体字整理表》和《现代汉语通用字表》外，作为现行规范汉字的依据的还有：《部分计量单位名称统一用字表》（中国文字改革委员会和国家标准计量局</span><span>1977</span><span>年发布）、《普通话异读词审音表》（以国家语委、国家教委、广播电视部</span><span>1985</span><span>年</span><span>12</span><span>月公布的修订本为准）、《现代汉语常用字表》（国家语委、国家教委</span><span>1988</span><span>年发布）、《信息交换用汉字编码字符集</span><span>·</span><span>基本集》（中华人民共和国国家标准，国家标准总局发布，</span><span>1981</span><span>年</span><span>5</span><span>月</span><span>1</span><span>日起实施）。此外，从</span><span>1955</span><span>年</span><span>3</span><span>月</span><span>30</span><span>日到</span><span>1964</span><span>年</span><span>8</span><span>月</span><span>29</span><span>日，经国务院批准，分九次更改了</span><span>35</span><span>个县级以上地名中的生僻字，更改的生僻地名字总数</span><span>37</span><span>个，单字</span><span>34</span><span>个。这也是现行规范汉字的一个依据。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">&nbsp;<span>学习规范汉字，必须掌握上述字表。读者要全面了解上述字表的情况，可参阅语文出版社出版的《语言文字规范手册》（增订本，</span><span>1993</span><span>版）一书。</span></span><span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '38', '语文知识', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '什么是规范汉字、现行规范汉字以什么作为依据', '1', null, '1');
INSERT INTO `ws_information` VALUES ('225', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:14pt;font-weight:bold;\">汉语拼音方案是怎样产生的</span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">《汉语拼音方案》是怎样产生的？&nbsp;</span><span style=\"font-family:\'宋体\';color:#000000;font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">《汉语拼音方案》是在过去的直音、反切以及各种拼音方案的基础上发展出来的。从采用拉丁字母来说，它的历史渊源远则可以一直推溯到<span>400</span><span>年以前，近则可以说是总结了近百年来我国人民创制汉语拼音方案的经验。为什么这样说呢？我们知道人们按一定的体系编制拉丁字母拼写汉语的方案，已经有</span><span>300</span><span>多年的历史了。例如大家所熟悉的意大利传教士利玛窦（</span><span>Matteo&nbsp;Ricci</span><span>）的《西字奇迹》就是</span><span>1605</span><span>年（明万历三十三年）在北京出版的。假如以没有严格的体系用拉丁字母拼写中国的专名，例如从《马可波罗游记》等文献算起，那就更久了。这些都是外国人为了便于传教和学习汉语而创制的。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">我国的有识之士采用拉丁字母拼写汉语的尝试，从现在算起有百年的历史了。最初的出版物是<span>1906</span><span>年朱文熊的《江苏新字母》和</span><span>1908</span><span>年刘孟扬的《中国音标字书》。当然影响较大被人们所熟悉的是</span><span>1926</span><span>年的</span><span>“</span><span>国语罗马字</span><span>”</span><span>和</span><span>1931</span><span>年的</span><span>“</span><span>拉丁化中国字</span><span>”</span><span>。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">以上就是人们多年来进行的拉丁化汉字拼音方案创制，并进行了不少实践活动的概况。所有这些都为后来我国制定《汉语拼音方案》提供了丰富的有益的经验。也就是说现在的《汉语拼音方案》是在历史上各种方案和前人实践的基础上发展出来的。</span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">人民迫切要求学习文化，要求有方便的文字工具，便于学习。<span>1949</span><span>年中国人民在中国共产党的领导下，政治上翻了身，也迫切要求文化上得到翻身，并且要求以原来推行过的</span><span>“</span><span>新文字</span><span>”</span><span>（北拉）为基础，迅速进行文字改革，以利于扫除文盲。吴玉章同志在</span><span>1949</span><span>年建国前夕写信给党中央和毛泽东主席，反映群众的这一要求。毛主席将信批转给郭沫若、茅盾和马叙伦诸同志进行研究。经过多方商讨，于</span><span>1949</span><span>年</span><span>10</span><span>月就成立了中国文字改革协会，开展文字改革研究工作，其中一项就是研究拼音方案。此后，中国文字改革协会、中国文字改革研究委员会和</span><span>1954</span><span>年</span><span>10</span><span>月改组为国务院直属机构的中国文字改革委员会都不断在研究拼音方案。这段时间可以说是全国群众性的设计汉语拼音方案的高潮时期。其间共收到群众寄来自己创造的拼音方案</span><span>1600</span><span>多个。从它们的多样化的形式和内容来看，可以说是我国拼音方案的百花园。从字母的形式看，有（</span><span>A</span><span>）汉字笔画式的，在这种类型里包括偏旁部首式和汉字笔形式的；（</span><span>B</span><span>）拉丁字母式的；（</span><span>C</span><span>）斯拉夫字母式的；在</span><span>B</span><span>和</span><span>C</span><span>两种类型里还包括有两种字母或几种字母混合型的。（</span><span>D</span><span>）速记式的；（</span><span>E</span><span>）图案式的；此外还有数字等等其他形式的。从音节结构上看，有音素化的，有双拼的，也有三拼的。我们现在回忆起当时的情况时，总是为那百家争鸣、百花齐放的盛况所激动。</span></span><span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">从以上各种各样方案中我们可以发现，要制定一个较好的方案必须首先确定几个原则，那就是：</span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">（一）语音标准：制定汉语拼音方案，首先遇到的一个问题就是以什么语音作为标准音的问题，这个问题由于历史上已经基本上形成了以北京语音为标准音，以北方话为基础方言和以典范的现代白话文著作为语法规范的现代汉民族共同语<span>——</span><span>普通话，所以就比较顺利的解决了。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">（二）音节结构：采用两拼、三拼，还是音素化的音节结构呢？这是讨论较多的一个问题。汉字一个字代表一个音节，而拼音则是音节的分析和拼合，因而只有采用音素制才是符合<span>“</span><span>分析和拼合</span><span>”</span><span>的科学原则，所以经过认真地讨论，大家确认汉语拼音方案采用音素制的音节结构是科学的、合理的、明智的。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">（三）字母形式：这个问题也是一个争论较多的问题，它涉及民族性与国际性的统一问题，因此，关于采用什么字母形式这个问题，到第一次全国文字改革会议时（<span>1955</span><span>年</span><span>10</span><span>月）还没有定下来。这时拼音方案委员会向出席会议的代表们提出了六种方案草稿，其中有汉字笔画式的、斯拉夫字母和拉丁字母式的。全国文字改革会议以后，根据各方面的意见，最后决定采用国际通用的罗马字母（拉丁字母）作为汉语拼音方案的字母。这就为我国以后的国际交流和科学技术工作带来了方便。</span></span><span style=\"font-family:\'宋体\';color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Arial\';color:#000000;font-size:12pt;\">当然除上述问题以外，还有一些问题是值得讨论的，例如：<span>26</span><span>个字母不够用时，是创造新字母还是采取加符号字母或是双字母？字母要不要名称？如果要名称，给它们起什么名字？怎样称呼它们？调号怎样处理？隔音符号怎样解决？等等。所有这些问题经过认真激烈地讨论，都得到了妥善的解决。总之，《汉语拼音方案》是在全国各省、市、自治区，各部门，各行业千百万人士以不同形式进行广泛讨论的基础上（同时还收到全国各方人士及海外侨胞的</span><span>4300</span><span>多件意见书），经由国务院组织各方面专家组成专门机构</span><span>——</span><span>汉语拼音方案审订委员会，进行认真讨论，经国务院全体会议第</span><span>60</span><span>次会议通过后，提请</span><span>1958</span><span>年</span><span>2</span><span>月</span><span>11</span><span>日第一届全国人民代表大会第五次会议通过和批准的。</span></span><span style=\"font-family:\'Arial\';color:#000000;font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '1', '38', '语文知识', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '汉语拼音方案是怎样产生的', '1', null, '1');
INSERT INTO `ws_information` VALUES ('226', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'ˎ̥\';font-size:14pt;font-weight:bold;\">推普周语言文字小常识</span><span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'ˎ̥\';font-size:12pt;\">1<span>、什么是普通话？</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">普通话就是现代汉民族共同语，是全国各民族通用的语言。普通话以北京语音为标准音，以北方话为基础方言，以典范的现代白话文著作为语法规范。&nbsp;</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">2<span>、为什么要推广普通话？</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">语言是最重要的交际工具和信息载体。民族共同语的普及是国家统一、民族团结，社会进步的重要基础。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">在建设中国特色社会主义现代化进程中，大力推广普通话，有利于克服语言隔阂，促进社会交往，维系民族团结，对社会主义经济、政治、文化建设都具有重要意义。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">3<span>、国家推广普通话的方针是什么？</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">方针是：<span>“</span><span>大力推行，积极普及，逐步提高。</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">4<span>、普通话与各民族语言的关系怎样？</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">各民族语言平等共存，各民族都有使用、发展自己语言的自由。国家鼓励各民族互相学习语言。使用少数民族语言进行教学的学校，开设汉语课程，应当教学普通话。民族自治地方和少数民族聚居地方需要使用汉语的场合，要推广和使用普通话。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">5<span>、推广普通话是不是要消灭方言？</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">在处理普通话与汉语方言的关系上，坚持社会语言生活主体化和多样化相结合的原则，一方面使公民普遍具备普通话应用能力，并在必要场合自觉使用普通话；另一方面承认方言在一定场合具有其自身的使用价值，推广普通话不是消灭方言。&nbsp;</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">6<span>、推广普通话工作的目标是什么？&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">2010<span>年以前，普通话在全国范围内初步普及，交际中的方言隔阂基本消除，受过中等以上教育的公民具各普通话的应用能力，并在必要的场合自觉地使用普通话；本世纪中叶以前，普通话在全国范围内普及，交际中没有方言隔阂。经过未来四五十年的不懈努力，我国国民语文素质大幅度提高，普通话的社会应用更加适应社会的经济、政治、文化发展需要，形成与中等发达国家水平相适应的良好语言环境。</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">7<span>、普及普通话主要是普及哪四种用语？&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">第一成为教学用语，指各级各类学校采用普通话教学。第二成为工作用语，指各级各类机关工作时一般使用普通话。第三成为宣传用语，指广播、电视、电影、话剧使用普通话。第四成为交际用语，指不同方言区的人在公共场合交往基本上使用普通话。&nbsp;</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">8<span>、推广普通话的重点在哪些领域</span><span>?</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">学校、党政机关、广播电视和公共服务行业是推普的四个重要领域，学校是普及普通话的基础阵地。党政机关在全社会普及普通话方面充分发挥龙头作用。广电等有声传媒对全社会有巨大而广泛的影响力，应当成为普及普通话的榜样。公共服务行业应当成为全社会推普的窗口。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">9<span>、全国推广普通话的形象大使是谁</span><span>?</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">全国推广普通话的形象大使是中央电视台主持人王小丫。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">10<span>、为什么要开展全国推广普通话宣传周活动</span><span>?</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">因为我国是多民族、多方言的国家，共同语的普及程度是国家和民族文明程度的一个重要标准。至今全社会普及普通话目标尚未实现，还不能适应改革开放和社会主义现代化建设对于普及各民族共同语的客观需要。开展全国推普周活动就是要大力推广、积极普及普通话，以适应经济、文化建设和社会发展的迫切需求。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">11<span>、全国推广普通话宣传周活动开始于哪一年</span><span>?</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">经国务院第<span>134</span><span>次总理办公会批准，自</span><span>1998</span><span>年起，每年</span><span>9</span><span>月份第三周为全国推广普通话宣传周。</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">12<span>、今年全国推广普通话宣传周的宣传主题是什么</span><span>?</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">本届<span>(</span><span>第八届</span><span>)</span><span>推普周活动的宣传主题是：</span><span>“</span><span>实现顺畅交流，构建和谐社会。</span><span>”</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">13<span>、普通话水平测试是怎么一回事？</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">普通话水平测试是推广普通话工作的重要组成部分，是使推广普通话工作逐步走向科学化、规范化、制度化的重要举措。目的是评定应试人普通话所达到的水平等级；通过测试，促进普通话的普及，并在普及的基础上逐步提高全社会的普通话水平，提高现代汉语规范化的程度。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">14<span>、什么是规范汉字？</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">规范汉字，是指经过整理简化并由国家以字表形式正式公布的正体字、简化字和未经整理简化的传承字。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">简化字以<span>1986</span><span>年</span><span>lO</span><span>月国务院批准重新发表的《简化字总表》中收录的简化字为准。</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">正体字以<span>1955</span><span>年文化部和中国文字改革委员会发布的《第一批异体字整理表》中选用的字为准，与此相对的异体字</span><span>(</span><span>共淘汰</span><span>1027</span><span>个</span><span>)</span><span>不再使用。</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">传承字是指历史上流传下来沿用至今，未经整理简化或不需要整理简化的字。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">15<span>、为什么要推行简化汉字</span><span>?</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">推行简化汉字，可经从几个方面来认识：</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">第一，简化是汉字发展的总趋势。古文字从甲骨文到小篆，再向隶书、楷书的发展，无论从字形、笔画都在简化；&nbsp;</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">第二，简化汉字是广大群众的自发要求，也是知识分子的共同愿望。今天推行的简化字，绝大多数是历代群众创造的；&nbsp;</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">第三，简化字简便易学，有很大的优越性；</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">第四，简化字已被各个使用汉字的国家、地区所接受。&nbsp;</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">16<span>、哪几种情形可以保留，使用繁体字</span><span>?&nbsp;</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">《国家通用语言文字法》第十七条规定，下列情形可保留或使用繁体字、异体字：<span>(</span><span>一</span><span>)</span><span>文物古迹；</span><span>(</span><span>二</span><span>)</span><span>姓氏中的异体字；</span><span>(</span><span>三</span><span>)</span><span>书法、篆刻等艺术作品；</span><span>(</span><span>四</span><span>)</span><span>题词和招牌的手书字；</span><span>(</span><span>五</span><span>)</span><span>出版、教学、研究中需要使用的；</span><span>(</span><span>六</span><span>)</span><span>经国务院有关部门批准的特殊情况。</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">17<span>、滥用繁体字有什么危害</span><span>?</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">繁体字泛滥所造成的严重危害，不容低估。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">其一，滥用繁体字使我国汉字规范化工作受到干扰，文字改革成果面临严峻挑战。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">其二，滥用繁体字给广大群众的学习、工作和生活带来了极大的不便，其中，首当其冲受害都便是中小学生。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">其三，滥用繁体字加剧了社会用字的严重混乱，有损国家形象。</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">18<span>、《中华人民共和国国家通用语言文字法》是什么时候通过，又是什么时候开始施行的</span><span>?</span></span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'ˎ̥\';font-size:12pt;\">《中华人民共和国国家通用语言文字法》是<span>2000</span><span>年</span><span>10</span><span>月</span><span>31</span><span>日第九届全国人民代表大会常务委员会第十八次会议通过的。从</span><span>2001</span><span>年</span><span>1</span><span>月</span><span>1</span><span>日起施行</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\">。</span><span style=\"font-family:\'宋体\';font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '2', '38', '语文知识', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '推普周语言文字小常识', '1', null, '1');
INSERT INTO `ws_information` VALUES ('227', null, '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:14pt;font-weight:bold;\">普通话相关知识</span><span style=\"font-family:\'黑体\';font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">普通话——是以北京语音为标准音，以北方话为基础方言，以典范的现代白话文著作为语法规范的汉民族共同语。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">2．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">自<span>1998</span><span>年始，每年九月的第三周为“全国推广普通话宣传周”。</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">3．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">《中华人民共和国国家通用语言文字法》由中华人民共和国第九届全国人民代表大会常务委员会第十八次会议于<span>2000</span><span>年</span><span>10</span><span>月</span><span>31</span><span>日通过，自</span><span>2001</span><span>年</span><span>1</span><span>月</span><span>1</span><span>日起施行。</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">4．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">国家语委颁布的《普通话水平测试等级标准》是划分普通话等级的全国统一标准。按照该标准规定，普通话水平测试等级自上而下分为一、二、三级，每个级别内分为甲、乙两个等次。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">5．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">根据国家语委《关于普通话水平测试管理工作的若干规定（试行）》（<span>1997</span><span>年</span><span>6</span><span>月</span><span>26</span><span>日）的要求，</span><span>1946</span><span>年</span><span>1</span><span>月</span><span>1</span><span>日以后出生的下列人员应接受普通话水平测试并达到规定的等级。</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">①&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">师范系统的教师和毕业生，普通话水平不得低于二级，其中普通话语音课教师和口语课教师必须达到一级；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">②&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">普教系统的教师以及职业中学与口语表达密切相关专业的毕业生，普通话水平不得低于二级（其中）；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">③&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">非师范类高等院校的教师以及与口语表达密切相关专业的毕业生，普通话水平不得低于二级；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">④&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">广播电视教学的教师，普通话水平不得低于二级；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">⑤&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">报考教师资格的人员，普通话水平不得低于二级；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">⑥&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">国家级和省级广播电台、电视台的播音员和节目主持人，普通话水平必须达到一级甲等，其余广播电台、电视台的播音员和节目主持人的达标要求由广播电影电视部另行规定；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">⑦&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">电影、话剧、广播剧、电视剧等表演、配音人员、播音、主持人专业和电影、话剧表演专业的教师和毕业生，普通话水平必须达到一级；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">⑧&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">其他应当接受普通话水平测试人员（如公务员、律师、医护人员、导游员、讲解员、公共服务行业的营业员等），其达标等级可根据不同地区、不同行业特点由省级语委确定。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"margin-left:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">注：师范中文专业的毕业生和语文类教师应该达到二级甲等。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:14pt;font-weight:bold;\">声母的相关知识</span><span style=\"font-family:\'黑体\';font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"margin-left:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">普通话声母共有<span>21</span><span>个（零声母除外）</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Times New Roman\';font-size:12pt;\">按发音部位声母分类可分为七类：双唇音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（<span>b&nbsp;p&nbsp;m</span><span>）、</span></span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">唇齿音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">（<span>f</span><span>）</span></span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、舌尖前音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(z&nbsp;&nbsp;c&nbsp;&nbsp;s)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、舌尖中音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(d&nbsp;&nbsp;t&nbsp;&nbsp;n)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、舌尖后音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(zh&nbsp;&nbsp;ch&nbsp;&nbsp;sh&nbsp;&nbsp;r)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、舌面音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(j&nbsp;&nbsp;q&nbsp;&nbsp;x&nbsp;&nbsp;)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、舌根音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(</span><span style=\"font-family:\'宋体\';font-size:12pt;\">ɡ</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;&nbsp;k&nbsp;&nbsp;h)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'Times New Roman\';font-size:12pt;\">按发音方法分类，声母可分为五类：塞音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(b&nbsp;&nbsp;p&nbsp;&nbsp;d&nbsp;&nbsp;t&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">ɡ&nbsp;&nbsp;k</span><span style=\"font-family:\'宋体\';font-size:12pt;\">)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、擦音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(f&nbsp;&nbsp;s&nbsp;&nbsp;sh&nbsp;&nbsp;x&nbsp;&nbsp;h&nbsp;&nbsp;m&nbsp;&nbsp;r)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、塞擦音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(&nbsp;z&nbsp;&nbsp;c&nbsp;&nbsp;zh&nbsp;&nbsp;ch&nbsp;&nbsp;j&nbsp;&nbsp;q)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、鼻音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(m&nbsp;&nbsp;n&nbsp;)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">、边音</span><span style=\"font-family:\'宋体\';font-size:12pt;\">(&nbsp;&nbsp;l&nbsp;&nbsp;)</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:21pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:14pt;font-weight:bold;\">韵母的相关知识</span><span style=\"font-family:\'黑体\';font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1<span>．普通话韵母共有三十九个，按结构可以分为单韵母、复韵母、鼻韵母；&nbsp;</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">⑨&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">由一个元音构成的韵母叫单韵母，又叫单元音韵母。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">②</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;由两个或三个元音结合而成的韵母叫复韵母。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">③</span><span style=\"font-family:\'宋体\';font-size:12pt;\">&nbsp;由一个或两个元音后面带上鼻辅音构成的韵母叫鼻韵母。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">2<span>．按开头元音发音口形可分为开口呼、齐齿呼、合口呼、撮口呼</span></span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">&nbsp;,&nbsp;<span>简称</span><span>“</span><span>四呼</span><span>”</span><span>。</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">①&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">开口呼韵母，没有韵头，而韵腹又不是<span>i</span><span>、</span><span>u</span><span>、</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\">ü</span><span style=\"font-family:\'宋体\';font-size:12pt;\">的韵母；</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">②&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">齐齿呼韵母，韵头或韵腹是<span>i</span><span>的韵母；</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">③&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">合口呼韵母，韵头或韵腹是<span>u</span><span>的韵母；</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:39pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">④&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">撮口呼韵母，云头或韵腹是ü的韵母。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\">声调的相关知识</span><span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1.&nbsp;通话有四个声调，分别是第一声、第二声、第三声和第四声。又叫阴平、阳平、上声、去声。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">2.&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">阴平的调值是55，阳平的调值是35，上声的调值是214，去声的调值是51。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\">音变的相关知识</span><span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">普通话的音变包括变调（包括上声变调、“一”、“不”变调、去声变调、重叠形容词变调等）、轻声、儿化和</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\">语气词<span>“</span><span>啊</span><span>”</span><span>的音变</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\">语调的相关知识</span><span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">句子里用来表达意思和感情的抑扬顿挫的调子，叫做语调。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">2．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">语调和声调不同。声调指单个字的调子，功用在于却别词义或语素义，也叫做“字调”；语调指贯穿整个句子的调子，功用在于表达整句的意思和感情，也叫做“句调”。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">3．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">语调的内容比较复杂，一般说来，它主要包括停顿、重音、升降三个方面。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\">朗读的相关知识</span><span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">朗读是把书面语言转化为有声语言的一种再创作活动。朗读不仅可以提高阅读能力，而且可以强化理解能力。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">2．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">在普通话水平测试中，朗读的目的是测查应试人使用普通话朗读书面作品的水平，在测查声母、韵母、声调读音标准程度的同时，重点测查连续音变、停连、语调以及流畅程度，是对应试人普通话运用能力的一种综合检测形式。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">3．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">朗读的要求：一、普通话语音标准。二、发音吐字清晰，朗读自然流畅。三、把握好朗读基调，感情贴切到位。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\">说话的相关知识</span><span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">说话测试是测查应试人在无文字凭借的情况下说普通话的水平，重点测查语音标准程度、词汇语法规范程度和自然流畅程度。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">2．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">“命题说话”不是即兴演讲，不是考查应试人的口才，它在主题的鲜明性、材料的新颖性和语言的感召力等方面没有严格的要求，只要求应试人围绕一个中心，流畅地说上一段用时<span>3</span><span>分钟的话，内容集中，表达有条有理即可。</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">3．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">说话的要求：一、语音清楚自然。二、词汇、语法规范（包括：不使用方言词语、语法；多用口语词；不用粗俗语、时髦语；准确选择动词、形容词、注意词语色彩的选择、注意声音的选择）。三、语句自然流畅（包括：多用短语，少用长句；冗余适当，避免口头禅；注意克服几种常见的语法错误）。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\">模拟试卷的相关知识</span><span style=\"font-family:\'黑体\';font-size:16pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">1．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">单音节不允许读轻声，如遇多音字可以任读一个读音（轻声除外）。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">2．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">应试人在读字词时，如重复某一字词，可以以最后一遍读音为记评音。</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:-18pt;margin-left:18pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:12pt;\">3．&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\">朗读评分以短文的<span>400</span><span>个音节（不含标点和括注的音节）为限，但应试人应将第</span><span>400</span><span>个音节所在的句子读完整。</span></span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span>\r\n	</p>\r\n</div>', null, '2012-05-31', 'admin', '管理员', null, '3', '38', '语文知识', '3', '2012-05-31 16:09:36', null, null, '2', null, null, '普通话相关知识', '1', null, '1');
